function D1205010040F_OnUnloadCompleted(obj) {

	if (ds_DrainageDayDetail.GetUpdate()) {
		if (!confirm(gfn_GetMessage("M000016","%AAA%","변경된 내용이 있습니다.종료"))) {
			return false;
		}
	}
}

function cob_Team_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	ds_Search.ClearData();
	ds_Search.AddRow();
	
	ds_Search.SetColumn(0, "BRANCH_CD" , cob_Branc.Value);
	ds_Search.SetColumn(0, "TEAM_CD", cob_Team.Value);
	
	var strSvcID = "find_block";
	var strPart  = "D10";
	var strURL   = "handle.do?ServiceName=D1_FacilityInfoMgr-service";
	var strInDatasets  = "ds_Search=ds_Search";
	var strOutDatasets  = "ds_Block=ds_Block";
	var strArgument  = "find_block=1";
				
	gfn_syncCall( 
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
			   );					 	    
    
    if(ErrorCode < 0)
	{    
		alert(ErrorMsg);
		return;
	}
	
	cob_PBlockCd.Index = 0;
	
	if(ds_Block.GetRowCount() == 0){
		cob_PBlockCd.Enable = false;
	}else{
		cob_PBlockCd.Enable = true;
		// 전체추가
		var str_nRow = ds_Block.AppendRow();
		ds_Block.SetColumn(str_nRow, "BLOCK_CD", ""    );
		ds_Block.SetColumn(str_nRow, "BLOCK_NM", "전체");
	}
}

//------------------------------------------------------------------------------- 
// 함수명: grd_Rec_OnCellClick()
// 설명: 정류기등록 상세내용를 조회 하는 fn_D1_DetailSearch()을 부르는 이벤트용 함수
// 그리드 클릭시 이벤트 처리  
//------------------------------------------------------------------------------- 
function grd_Dra_OnCellClick( obj,nRow,nCell,nX,nY,nPivotIndex)
{


    if (nCell == 17) {
        var args = "BUNGI='11'";
	    args += " FACI_NUM=" + ds_Drainage.getcolumn(nRow, "FACI_NUM");
	
	    Dialog("D10::D1402060011U.xml", args, 1208, 840, "Resize=true Scroll=true", -1, -1);
    } else {
		ds_DrainageDayDetail.ClearData(); 
		ds_DrainageDayPoint.ClearData();
		ds_DrainageDaySector.ClearData();
		
		fv_faciNum 	= ds_Drainage.getcolumn(grd_Dra.CurrentRow, "FACI_NUM");
		fv_faciNm 	= ds_Drainage.getcolumn(grd_Dra.CurrentRow, "FACI_NM");
		
		fn_D1_DrainageDay(fv_faciNum);
		//일일 정보 이력조회
		//fn_D1_DayDetailSearch( nRow );		
		//fn_D1_FileListNm(fv_faciNum);
		//fn_D1_FileListNmImg(fv_faciNum);
		//fn_D1_DayDetailSearch( nRow );		 
		fv_saveMode = "updateRecDay";
		
		//셀선택시 자동 조회..
		//fn_D1_DayPointSearch( ds_RectifierDayDetail.currow );
		//fn_D1_DaySectorSearch( ds_RectifierDayDetail.currow );	
		grd_DraDay_OnCellClick( ds_DrainageDayDetail.currow  );
	}
	
	if (nCell == 16) {
		fn_Card(nRow);
	}
}

 
