//------------------------------------------------------------------------------- 
// 함수명: fn_D1_DayPointSearch(nRow)
// 설명: 배류기점  상세정보 가져옴
// arguments   
// FACI_NUM  : 시설물 번호
// return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_D1_DayPointSearch(nRow)
{ 
	if(nRow < 0) return;    
	
	//영역조사 Code:3 일경우만 정류점과 영역조사 측정데이터가 등록되게 된다.
	if( ds_DrainageDayDetail.GetColumn( nRow, "INPUT_FLAG") == '3' )  
	{
		ds_Search.DeleteAll();
		var strRow  = ds_Search.AddRow();
		
		ds_Search.SetColumn(strRow, "FACI_NUM", ds_DrainageDayDetail.GetColumn( nRow, "FACI_NUM"));
		ds_Search.SetColumn(strRow, "INPUT_YMD", ds_DrainageDayDetail.GetColumn( nRow, "INPUT_YMD"));
		ds_Search.SetColumn(strRow, "INPUT_FLAG", ds_DrainageDayDetail.GetColumn( nRow, "INPUT_FLAG"));
		
		var ServiceId     	= "selectDrainageDayPoint";
		var ServicePart     = "D10";
		var Url             = "handle.do?ServiceName=D1_FacilityInfoDraMgr-service";
		var InputDsList     = "ds_Search=ds_Search";
		var OutputDsList 	= "ds_DrainageDayPoint=ds_DrainageDayPoint";
		var Argument      	= "selectDrainageDayPoint";
	
		gfn_SyncCall(ServiceId, ServicePart, Url, InputDsList, OutputDsList, Argument);

		//등록된 자료가 없다면 새로운 로우를 추가한다. 
		if( ds_DrainageDayPoint.rowcount < 1 ) { 
			fn_AddRowDrainageDayPoint();
		}
	} else {
		ds_DrainageDayPoint.ClearData();
	} 
}

//------------------------------------------------------------------------------- 
// 함수명: fn_D1_DaySectorSearch( nRow ) 
// 설명: 일일 시설물 정류기 리스트를 가져옴 
//------------------------------------------------------------------------------- 
function fn_D1_DaySectorSearch( nRow )
{ 
	if(nRow < 0) return; 
	
	//영역조사 일경우만 정류점과 영역조사 측정데이터가 보여지고 등록할수 있다.
	if( ds_DrainageDayDetail.GetColumn( nRow, "INPUT_FLAG") == '3' )  
	{
		ds_Search.DeleteAll();
		var strRow  = ds_Search.AddRow();
		
		ds_Search.SetColumn(strRow, "FACI_NUM", ds_DrainageDayDetail.GetColumn( nRow, "FACI_NUM"));
		ds_Search.SetColumn(strRow, "INPUT_YMD", ds_DrainageDayDetail.GetColumn( nRow, "INPUT_YMD"));
		ds_Search.SetColumn(strRow, "INPUT_FLAG", ds_DrainageDayDetail.GetColumn( nRow, "INPUT_FLAG"));
	
		var ServiceId     	= "selectDrainageDaySector";
		var ServicePart     = "D10";
		var Url             = "handle.do?ServiceName=D1_FacilityInfoDraMgr-service";
		var InputDsList     = "ds_Search=ds_Search";
		var OutputDsList 	= "ds_DrainageDaySector=ds_DrainageDaySector";
		var Argument      	= "selectDrainageDaySector";
	
		gfn_SyncCall(ServiceId, ServicePart, Url, InputDsList, OutputDsList, Argument); 
	} else {
		ds_DrainageDaySector.ClearData();
	} 
}


