function cal_InputYmd_CanChange(obj,strOldText,strText)
{
	if( !gfn_isNull( ds_DrainageDayDetail.GetColumn( ds_DrainageDayDetail.row, "CRT_DTM")) ) 
	{
		alert("신규등록건에 한하여 변경가능합니다.\n이전데이터는 변경할수 없습니다.");
		obj.value = strOldText;
		return false;
	}
}

