//-------------------------------------------------------------------------------
// 함수명: cob_InputFlag_OnChanged()
// 설명: 입력구분값이 변경시 중복되어 설정되지 못하도록 제어한다.
//	1. 오늘 이전에 등록하였던 데이터는 입력구분값을 변경하지 못하도록 한다.
//	2. 금일자료중 중복된 구분값이 나오면 back처리한다.
//	3. 영역조사일경우 정류점데이터도 행이 추가된다.
//------------------------------------------------------------------------------- 
function cob_InputFlag_OnChanged( obj , strCode , strText , nOldIndex , nNewIndex)
{   	
	//생성일자가 있고 구분값이 있을경우 변경못하도록 막는다.
	if( !gfn_isNull( ds_DrainageDayDetail.GetColumn(ds_DrainageDayDetail.row, "CRT_DTM"))
		&& !gfn_isNull( ds_DrainageDayDetail.GetColumn(ds_DrainageDayDetail.row, "CRT_DTM")) ) 
	{
			alert("이전데이터에 저장되었던 입력구분값은 변경할수 없습니다.");
			obj.index = nOldIndex;
			return;
	}

	for(var i = 0; i < ds_DrainageDayDetail.rowcount ; i++)
	{
		if(    ds_DrainageDayDetail.GetColumn(i, "INPUT_YMD" )  == cal_InputYmd.Value 
		    && ds_DrainageDayDetail.GetColumn(i, "INPUT_FLAG" ) == strCode 
		    && i != ds_DrainageDayDetail.currow )
		{
			alert("이미 등록된 이력입니다.");
			obj.index = nOldIndex;
			return;
		}
	} 
	 
	//영역조사일 경우 조회 정류점이 나오도록 한다.
	fn_D1_DayPointSearch( ds_DrainageDayDetail.currow ) ;
	//영역조사일 경우 조회 정류점이 나오도록 한다.
	fn_D1_DaySectorSearch( ds_DrainageDayDetail.currow ) ;
}

function cal_MakeYmd_OnChanged(obj,strOldText,strNewText)
{
	fn_DateCheckToday( cal_MakeYmd );
}

function cal_ReplYmd_OnChanged(obj,strOldText,strNewText)
{
	fn_DateCheckToday( cal_ReplYmd );
}

