//-------------------------------------------------------------------------------
// 함수명: fn_ZipSearch()
// 설명: 우편번호 조회클릭시 우편번호 조회 popup화면 로딩
// arguments   
// obj: 사용된 객체를 전달 한다.
// return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_ZipSearchPoint( obj )
{ 	
	var Arguments = ""; 
	var Result = gfn_Dialog("A10", "A1101020010U", Arguments,400,450);

	if(Result.Length>0){
		ds_DrainageDayPoint.SetColumn(ds_DrainageDayPoint.curRow, "ZIP_SEQ", Result[0]);
		ds_DrainageDayPoint.SetColumn(ds_DrainageDayPoint.curRow, "LOT_NUM_UNION", Result[17]);
		ds_DrainageDayPoint.SetColumn(ds_DrainageDayPoint.curRow, "ZIP_NO1", Result[2]);
		ds_DrainageDayPoint.SetColumn(ds_DrainageDayPoint.curRow, "ZIP_NO2", Result[3]);
		ds_DrainageDayPoint.SetColumn(ds_DrainageDayPoint.curRow, "ZIP_NO3", Result[4]);
		ds_DrainageDayPoint.SetColumn(ds_DrainageDayPoint.curRow, "HILL", Result[10]);
		ds_DrainageDayPoint.SetColumn(ds_DrainageDayPoint.curRow, "ADDR_M", Result[11]);
		ds_DrainageDayPoint.SetColumn(ds_DrainageDayPoint.curRow, "ADDR_S", Result[12]);
		ds_DrainageDayPoint.SetColumn(ds_DrainageDayPoint.curRow, "SECOND_ADDR", Result[13]);
		ds_DrainageDayPoint.SetColumn(ds_DrainageDayPoint.curRow, "BLD_DONG", Result[14]);
		ds_DrainageDayPoint.SetColumn(ds_DrainageDayPoint.curRow, "HOSU", Result[15]);
	}
	break;
}  

//-------------------------------------------------------------------------------
// 함수명: fn_RecSectorAddRow()
// 설명:  플래그가 3일경우 ..  영역조사 측정 데이터에 대하여 로우를 추가시킨다.
// arguments    
//------------------------------------------------------------------------------- 
function fn_RecSectorAddRow( obj )
{  
	if( cob_InputFlag.Value == '3' ) { 
		ds_DrainageDaySector.AddRow();
		ds_DrainageDaySector.SetColumn( ds_DrainageDaySector.currow , "INPUT_FLAG" , ds_DrainageDayDetail.GetColumn( ds_DrainageDayDetail.currow , "INPUT_FLAG" )  );
		ds_DrainageDaySector.SetColumn( ds_DrainageDaySector.currow , "INPUT_YMD"  , ds_DrainageDayDetail.GetColumn( ds_DrainageDayDetail.currow , "INPUT_YMD" )  );
		ds_DrainageDaySector.SetColumn( ds_DrainageDaySector.currow , "FACI_NUM"   , ds_DrainageDayDetail.GetColumn( ds_DrainageDayDetail.currow , "FACI_NUM" )  );
	    if( ds_DrainageDaySector.GetRowCount() > 0 ){ 
			//기존로우에 추가시키는 경우라면 섹터번호 + 1을 세팅한다.
			ds_DrainageDaySector.SetColumn( ds_DrainageDaySector.currow , "SECTOR_NUM" , ds_DrainageDaySector.Max("SECTOR_NUM") + 1 );
	    } else { 
			//처음삽입하는 경우라면 섹터번호를 1로 세팅한다.
			ds_DrainageDaySector.SetColumn( ds_DrainageDaySector.currow , "SECTOR_NUM" , 1 ); 
		} 
		ds_DrainageDaySector.SetColumn( ds_DrainageDaySector.currow , "SECTOR_SUB_NUM" , 1 );
	} else {
		alert( '배류기 입력구분이 [영역조사] 항목일 경우만\n영역조사 측정 데이터를 등록하실수 있습니다.' );
		ds_DrainageDaySector.ClearData();
	} 
}

function fn_DraSectorDeleteRow( obj )
{
    var currSectorNum = ds_DrainageDaySector.GetColumn( ds_DrainageDaySector.currow , "SECTOR_NUM" ) ;
    var currSectorSubNum = ds_DrainageDaySector.GetColumn( ds_DrainageDaySector.currow , "SECTOR_SUB_NUM" ) ;
    var maxSubNum = ds_DrainageDaySector.CaseCount( "SECTOR_NUM='"+currSectorNum+"'" , "SECTOR_SUB_NUM");

    if(currSectorSubNum == maxSubNum)
    {
		ds_DrainageDaySector.DeleteRow( ds_DrainageDaySector.row ); 
    }
    else if( ds_DrainageDaySector.GetRowCount()  < 1 )
    {
		alert( "삭제하실 데이터가 없습니다." );
		return false;
    }
    else
    {
		alert('sector subnum의 마지막 순번부터 삭제가 가능합니다!!!\n(현재 로우번호:'+currSectorSubNum+', 현섹터마지막로우번호:'+maxSubNum+')');
		return false;
    }	
}

function grdDraSector_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{	 
	switch( nCell ) {
		case 1 : 
				if( ds_DrainageDaySector.GetColumn( nRow , "SECTOR_SUB_NUM" )  == 1 )
				{
					var varSectorSubNum = 1;
					var currSectorNum = ds_DrainageDaySector.GetColumn( nRow , "SECTOR_NUM" ) ;
					var nextSectorRow = ds_DrainageDaySector.FindRow( "SECTOR_NUM", ds_DrainageDaySector.GetColumn( nRow , "SECTOR_NUM" ) + 1  ) ;
					// 다음번호가 없을경우 섹터서브번호는 맨마지막 번호 + 1를 세팅해준다.
					if(nextSectorRow == -1) 
					{
						nextSectorRow = ds_DrainageDaySector.GetLastRow() + 1; // 가장마지막 로우를 리턴한다. (다음번호가 없다라는건 자신이 마지막 번호라는 의미)
					}
					// else {
						//기존 보조번호가 있다면
						//최종 보조섹터번호 + 1로 넣어준다.
						//varSectorSubNum = toNumber(  ds_RectifierDaySector.GetColumn( nextSectorRow - 1 , "SECTOR_SUB_NUM" ) ) + 1;
					//}
				
					ds_DrainageDaySector.InsertRow( nextSectorRow ); // 섹터서브번호의 마지막에 row를 추가한다.
					
					//추가후 기본값들을 세팅한다.  
					ds_DrainageDaySector.SetColumn( ds_DrainageDaySector.currow , "INPUT_FLAG" , ds_DrainageDaySector.GetColumn( nRow , "INPUT_FLAG" )  );
					ds_DrainageDaySector.SetColumn( ds_DrainageDaySector.currow , "INPUT_YMD"  , ds_DrainageDaySector.GetColumn( nRow , "INPUT_YMD" )  );
					ds_DrainageDaySector.SetColumn( ds_DrainageDaySector.currow , "FACI_NUM"   , ds_DrainageDaySector.GetColumn( nRow , "FACI_NUM" )  ); 
					//ds_RectifierDaySector.SetColumn( ds_RectifierDaySector.currow , "SECTOR_NUM"       , ds_RectifierDaySector.GetColumn( nRow , "SECTOR_NUM" ) ); 
					//ds_RectifierDaySector.SetColumn( ds_RectifierDaySector.currow , "SECTOR_SUB_NUM"   , varSectorSubNum  );
					 
					ds_DrainageDaySector.SetColumn( ds_DrainageDaySector.currow, "SECTOR_NUM"     , ds_DrainageDaySector.GetColumn( nRow , "SECTOR_NUM" ) );     // 현재섹터번호를 넣어준다.
					ds_DrainageDaySector.SetColumn( ds_DrainageDaySector.currow, "SECTOR_SUB_NUM" , ds_DrainageDaySector.CaseCount( "SECTOR_NUM='"+currSectorNum+"'" , "SECTOR_SUB_NUM") ); 
				}
			break; 
		case 3 :
			var sector_flag = ds_DrainageDaySector.GetColumn( nRow , "SECTOR_FACI_FLAG" ); 
				if( sector_flag == '30' || sector_flag == '40') {
						var arrList = gfn_Dialog("A10", "A1101020010U", "", 1100, 600, true, -1, -1); 
						if(arrList == 0){
							return;
						}							
						if ( !gfn_isNull( arrList ) ) {    
							ds_DrainageDaySector.setColumn( nRow , "ZIP_NO1" , arrList[2] );
							ds_DrainageDaySector.setColumn( nRow , "ZIP_NO2" , arrList[3] );
							ds_DrainageDaySector.setColumn( nRow , "ZIP_SEQ" , arrList[0] );
							ds_DrainageDaySector.setColumn( nRow , "HILL" , arrList[10] );
							ds_DrainageDaySector.setColumn( nRow , "ADDR_M" , arrList[11] );
							ds_DrainageDaySector.setColumn( nRow , "ADDR_S" , arrList[12] );
							ds_DrainageDaySector.setColumn( nRow , "SECOND_ADDR" , arrList[13] );
							ds_DrainageDaySector.setColumn( nRow , "BLD_DONG" , arrList[14] );
							ds_DrainageDaySector.setColumn( nRow , "HOSU" , arrList[15] );
							ds_DrainageDaySector.setColumn( nRow , "SECTOR_FACI_ADDR" , arrList[17] );
						} 			 
				}  
		    break;
			
		case 5 :  
				var sector_flag = ds_DrainageDaySector.GetColumn( nRow , "SECTOR_FACI_FLAG" ); 
				if( gfn_isNull( sector_flag ) ) {
					alert( "유형을 먼저 선택하세요." );  
				} else {
					if( sector_flag == '10' || sector_flag == '20') {
								var arg = "fa_code=" + quote( sector_flag ) ;	 
								var arrList = gfn_Dialog("d10", "D1205010030U", arg , 1100, 600, true, -1, -1);
								if( Length( arrList ) < 1){
									return;
								} 
								/*
								서울도시가스 (9568): arrList[0] = GR000140_GV01
								서울도시가스 (9568): arrList[1] = 100584
								서울도시가스 (9568): arrList[2] = 서부지사
								서울도시가스 (9568): arrList[3] = 150
								서울도시가스 (9568): arrList[4] = 영등포구
								서울도시가스 (9568): arrList[5] = 여의공작
								서울도시가스 (9568): arrList[6] = GV
								서울도시가스 (9568): [GR000140_GV01,100584,서부지사,150,영등포구,여의공작,GV,서울 영등포구 여의도동21번지 공작아파트내]
								*/ 
								//trace( arrList[7] );
								ds_DrainageDaySector.setColumn( nRow , "SECTOR_FACI_NUM"  , arrList[0] );
								ds_DrainageDaySector.setColumn( nRow , "ZIP_NO1" , arrList[3] );
								ds_DrainageDaySector.setColumn( nRow , "SECTOR_FACI_ADDR" , arrList[7] );
								 
					} else {
						alert("주소를 직접기입해 주세요.");
					}
				}
			
			break;
		default : 
	}  
}
 
 
// 2013-07-10 김형수 정배류기 공통 체크 함수 
function fn_saveChk()
{
    var detailCrtDtm = ds_DrainageDayDetail.GetColumn(ds_DrainageDayDetail.row, "CRT_DTM");
    
    if(trim(detailCrtDtm) == '' || trim(detailCrtDtm) == null)
    {
        alert("배류기 정보를 먼저 등록해주세요!!!");
        return -1;
    }
    else
    {
        return 0;
    }
} 
 
 
function btn_PointSave_OnClick(obj)
{ 
  // 2013-07-10 김형수 정/배류기 정보가 등록되지 않은 상태에서 정류점이 등록되지 않도록 한다.
  if(fn_saveChk() == -1)
  {
      return false;
  }  

	if(cal_InputYmd.Value == "" || cal_InputYmd.Value == null)
    {
        alert("입력일자를 선택해주세요.");
        return;    
    }
    
	if( fn_mergeDrainageDayPoint() )
	{
		alert( "배류점 정보가 저장되었습니다." );
	}
}

function btn_DraSetorSave_OnClick(obj)
{
  // 2013-07-10 김형수 정/배류기 정보가 등록되지 않은 상태에서 정류점이 등록되지 않도록 한다.
  if(fn_saveChk() == -1)
  {
      return false;
  }  

	if( fn_DelInsertDrainageDaySector() )
	{
		alert( "영역조사 측정 데이터가 저장되었습니다." );
	}
}   

function ds_DrainageDaySector_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	switch( strColumnID  ) {
		case "SECTOR_FACI_FLAG" : //영역조사 측정데이터 유형이 변경되었을때 초기화
				 ds_DrainageDaySector.SetColumn( nRow , "SECTOR_FACI_ADDR" , "" );
				 ds_DrainageDaySector.SetColumn( nRow , "SECTOR_FACI_NUM" , "" );
				 ds_DrainageDaySector.setColumn( nRow , "ZIP_NO1" , "" );
				 ds_DrainageDaySector.setColumn( nRow , "ZIP_NO2" , "" );
				 ds_DrainageDaySector.setColumn( nRow , "ZIP_SEQ" , "" );
				 ds_DrainageDaySector.setColumn( nRow , "HILL" , "" );
				 ds_DrainageDaySector.setColumn( nRow , "ADDR_M" , "" );
				 ds_DrainageDaySector.setColumn( nRow , "ADDR_S" , "" );
				 ds_DrainageDaySector.setColumn( nRow , "SECOND_ADDR" , "" );
				 ds_DrainageDaySector.setColumn( nRow , "BLD_DONG" , "" );
				 ds_DrainageDaySector.setColumn( nRow , "HOSU" , "" ); 
				 break;
					
		default :
		
	}
}

//등록/수정대장첨부
function btn_FileList3_OnClick(obj)
{  	
	if(fv_faciNum != "")
	{
		var arg = "fa_Url=handle.do?ServiceName=D1_FacilityInfoDraMgr-service&FACI_NUM=" + ds_DrainageDayDetail.GetColumn( ds_DrainageDayDetail.currow , "FACI_NUM" ) 
		          + "&INPUT_YMD=" + ds_DrainageDayDetail.GetColumn( ds_DrainageDayDetail.currow , "INPUT_YMD" )
		          + "&INPUT_FLAG=" + ds_DrainageDayDetail.GetColumn( ds_DrainageDayDetail.currow , "INPUT_FLAG" )
		          + "&ATTACH_FILE_NUM=3"
		          
		          + " fa_faciNum=" + ds_DrainageDayDetail.GetColumn( ds_DrainageDayDetail.currow , "FACI_NUM" ) 
				  + " fa_inputYmd=" + ds_DrainageDayDetail.GetColumn( ds_DrainageDayDetail.currow , "INPUT_YMD" )
				  + " fa_inputFlag=" + ds_DrainageDayDetail.GetColumn( ds_DrainageDayDetail.currow , "INPUT_FLAG" )
				  + " fa_AtNum=3"  
		          + " fa_Part=D10 ";
		var arrList = gfn_Dialog("D10","D1101010023U",arg,450,280,false,-1,-1);
		//trace( arrList );
		if( !gfn_isNull( arrList ) )
		{
			edt_FileList3.text = "";
			for(var i=0; i < arrList.length; i++)
			{ 
				edt_FileList3.Text += arrList[i]; 
				if( arrList.length-1 != i) edt_FileList3.text += "/";
			}
		}
	}		
}  
 
