function grd_DraDay_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	switch( nCell ) {
		case 5 : 
			btn_FileList3_OnClick();
			break;
		case 7 :
			btn_FileList4_OnClick();
			break;
		default :	
	}
} 

function fn_CommonExcelExport(obj)
{
    var SearchCondition= ""; 
     
	SearchCondition = SearchCondition + "지사 : " + RPad( cob_Branc.Text ," ",20) ; 
	SearchCondition = SearchCondition + "시/구 : " + RPad( cob_ZipNo1.Text ," ",20) ; 
	SearchCondition = SearchCondition + "팀 : " + RPad( cob_Team.Text ," ",20) ; 
	SearchCondition = SearchCondition + "구간 : " + RPad( cob_PBlockCd.Text ," ",20) ;
	SearchCondition = SearchCondition + "배류기명 : " + RPad( edt_DraNm.Text ," ",20) ;
	gfn_ExportExcel("배류기 리스트 ", 5, SearchCondition, "grd_excel"); 

}
