//------------------------------------------------------------------------------- 
function fn_init()
{
    //-----------------------------------------
	//  콤보박스의 내용을 셋팅한다.
	//-----------------------------------------	
	gfn_D1_All_SetComboBox_Branchcd(gfn_D1_setDsObject_Branchcd("cob_Branc"));
	gfn_D1_SetComboBox_Comcode("D10311", "ds_InputFlagCombo" ); 
	gfn_D1_NULL_SetComboBox_Comcode("D10059",gfn_setDsObject_Comcode("cob_ChthodicFlag"));
	
	gfn_D1_All_SetComboBox_Comcode("D10059",gfn_setDsObject_Comcode("cob_ChthodicFlag_search"));
	cob_ChthodicFlag_search.Value = "";
	
	if (gfn_GetGlobalValue("G_BRANCH_CD") == "999999") {
	    cob_Branc.value = '';
	} else {
	    cob_Branc.Value = gfn_GetGlobalValue("G_BRANCH_CD");
	    cob_Branc.Enable = false;
	}
	     
	cob_Branc_OnChanged();
}

//------------------------------------------------------------------------------- 
// 함수명: fn_D1_Search()
//  설명: 정압실/정압기등록 화면 리스트를 조회 하는 함수
// arguments   
//  return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_D1_Search()
{	
    // if (cob_Branc.value == "" || cob_Branc.value == null) {
         // alert("지사를 선택하십시오.");
         // cob_Branc.SetFocus();
         // return;
    // }
	ds_GovRoom.ClearData();
	ds_GovRoomDetail.ClearData();
	ds_GovDetail.ClearData();
	ds_GovList.ClearData();
	
	//Searh Parameter Setting DataSet:ds_Searhc
	ds_Search.ClearData();
	var strRow = ds_Search.AddRow();

	ds_Search.SetColumn(strRow, "BRANCH_CD", cob_Branc.Value);
	ds_Search.SetColumn(strRow, "ZIP_NO1", cob_ZipNo1.Value);
	ds_Search.SetColumn(strRow, "TEAM_CD", cob_Team.Value);
	ds_Search.SetColumn(strRow, "FACI_NUM", edt_FaciNumSearch.Text);
	ds_Search.SetColumn(strRow, "FACI_NM", edt_FaciNumSearch.Text);
	//ds_Search.SetColumn(strRow,"FACI_NUM",edt_scada_num.Text);
	ds_Search.SetColumn(strRow, "BLOCK_CD", cob_PBlockCd.Value);
	ds_Search.SetColumn(strRow, "CATHODIC_FLAG", cob_ChthodicFlag_search.Value);
	ds_Search.SetColumn(strRow, "REC_DRA_NUM_NM", edt_RecDraNm_search.Value);
	
	
	//-------------------------------------------------------------------------------
	//  Transition   : Service id
	//  ServicePart   : Service part
	//  Url   : Server url
	//  InputDsList   : Input Dataset List
	//  OutputDsList   : Output Dataset List
	//  Argument   :  Argument
	//-------------------------------------------------------------------------------	
    var ServiceId     = "selectGovRoomList";
    var ServicePart   = "D10";
    var Url           = "handle.do?ServiceName=D1_ElectScadaInfoMgr-service";
    var InputDsList   = "ds_Search=ds_Search";
    var OutputDsList  = "ds_GovRoom=ds_GovRoom";
    var Argument      = "selectGovRoomList";	

    gfn_SyncCall(ServiceId, ServicePart, Url, InputDsList, OutputDsList, Argument);
	
    st_Reg.Text = ds_GovRoom.GetRowCount();

    fv_faciNum = "";
    fv_govNum = "";
    
    if(ds_GovRoom.GetRowCount()==0){
		grd_GovRoom.NoDataText = "검색된 자료가 없습니다.";
	}else{
		grd_GovRoom.NoDataText = "";
	}

}

