//------------------------------------------------------------------------------- 
// 함수명: fn_D1_DetailSearch(FACI_NUM)
//   설명: 정압실/정압기등록  상세내용를 조회 하는 함수
// arguments   
//          FACI_NUM  : 시설물 번호
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_D1_ElectScadaSearch(nRow){

	ds_ElectScada.ClearData();
	
	ds_Search.ClearData();
	
	var nAddRow = ds_Search.AddRow();
	ds_Search.SetColumn(nAddRow, "FACI_NUM", ds_GovRoom.GetColumn(nRow, "FACI_NUM"));
	
	//-------------------------------------------------------------------------------
	//  Transition   : Service id
	//  ServicePart   : Service part
	//  Url   : Server url
	//  InputDsList   : Input Dataset List
	//  OutputDsList   : Output Dataset List
	//  Argument   :  Argument
	//-------------------------------------------------------------------------------	
    var ServiceId     = "selectScadaList";
    var ServicePart   = "D10";
    var Url           = "handle.do?ServiceName=D1_ElectScadaInfoMgr-service";
    var InputDsList   = "ds_Search=ds_Search";
    var OutputDsList  = "ds_ElectScada=ds_ElectScada";
    var Argument      = "selectScadaList";
   
    gfn_SyncCall(ServiceId, ServicePart, Url, InputDsList, OutputDsList, Argument);
       
    if(ds_ElectScada.GetRowCount()==0){
		grd_ElectScada.NoDataText = "검색된 자료가 없습니다.";
	}else{
		grd_ElectScada.NoDataText = "";
	}

}




/********************************************************************************
* 기      능   :  지사별 구를 조회하여 콤보박스에 셋팅
********************************************************************************/
function cob_BranchCd_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex) {

    // 지사별 구 콤보 셋팅 시
    fn_Part_SetComboBox_Comcode("1", cob_BranchCd.value, fn_setDsObject_Comcode("1", "cob_Gu"));
    //cob_Gu.Index = 0;
}

function cob_Branc_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	// 지사별 구 콤보 셋팅 시
    fn_SetComboBox_Comcode("1", cob_Branc.value, fn_setDsObject_Comcode("1", "cob_ZipNo1"));
    
    if (cob_Branc.Value == "" || cob_Branc.Value == null) {
    //    cob_ZipNo1.Enable = false;
    //권기철 변경 본사직원로그인시 전체가 아닌 서부가 기본제공 => 다시변경 12.17
		cob_ZipNo1.Value = "";
		//cob_ZipNo1.Index =  0;
    } else {
    //    cob_ZipNo1.Enable = true;
    	cob_ZipNo1.Value = "";
       //cob_ZipNo1.Index  = 0;
    }
    
    gfn_D1_SetComboBox_Teamcd("ds_Team",cob_Branc);
    cob_Team.Value = "";
    
    cob_PTeamCd_OnChanged();
}


