function grd_ElectScada_OnCellPosChanged(obj,nRow,nCell,nOldRow,nOldCell,nPivotIndex,nOldPivotIndex)
{
	var objDs = object(obj.BindDataset);
	var sAttachYn = objDs.GetColumn(nRow , "ATTACH_FILE_YN");
	var sFile1 = objDs.GetColumn(nRow , "ATTACH_FILE_ID1");
	var sFile2 = objDs.GetColumn(nRow , "ATTACH_FILE_ID2");

	//if(sAttachYn != "Y") return;
	
	if(nCell == 5 && !gfn_isNull(sFile1)){
		btn_DownLoad_OnClick('1');
	}
	else if(nCell == 7 && !gfn_isNull(sFile2)){
		btn_DownLoad_OnClick('2');
	}
	
}


function fn_D1_ExcelExport(){
    
    var SearchCondition= "";
    
    SearchCondition = SearchCondition + "지사 :" + RPad(cob_Branc.Text," ",20);
    SearchCondition = SearchCondition + "시/구 :" + RPad(cob_ZipNo1.Text," ",20);
    SearchCondition = SearchCondition + "팀 :" + RPad(cob_Team.Text," ",20);
    SearchCondition = SearchCondition + "구간 :" + RPad(cob_PBlockCd.Text," ",20);    
    SearchCondition = SearchCondition + "정압실번호 :" + RPad(edt_FaciNumSearch.Text," ",20);
     SearchCondition = SearchCondition + "방식구분:" + RPad(cob_ChthodicFlag_search.Text," ",20);    
    SearchCondition = SearchCondition + "정배류기명 :" + RPad(edt_RecDraNm_search.Text," ",20);
	gfn_ExportExcel("정압실 조회 ", 5, SearchCondition, "grd_Excel"); 
}

