var fn_REG_INSPECT_PLAN_YMD = "";
//---------------------------------------------------------
// 달력 팝업
//---------------------------------------------------------
function grd_GovList_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	fn_REG_INSPECT_PLAN_YMD = Trim(ds_GovReg.GetColumn(nRow,"REG_INSPECT_PLAN_YMD"));

	if("REG_INSPECT_PLAN_YMD" == grd_GovList.GetCellProp("body",nCell,"ColId"))
	{
		if(ds_GovReg.GetColumn(nRow,"CHK") != 0){
		
			if(ds_GovReg.GetColumn(ds_GovReg.row, "FIX_INSPECT_REQ_YN") == '유'){
				alert("신청서가 출력된 상태에서는 데이터 수정이 불가능합니다. ");
				return;
			}
			
			gfn_SetCalendar(obj, nRow, nCell);
			
			var str_HoliDay = gfn_Bank_Holiday(ds_GovReg.GetColumn(nRow,"REG_INSPECT_PLAN_YMD"));
			
			if(str_HoliDay == 'Y'){
				alert("휴일 일자는 등록하실 수 없습니다. ");
				ds_GovReg.SetColumn(nRow,"REG_INSPECT_PLAN_YMD", null);
				return;
			}
			
		}
	}else if("FIX_INSPECT_REQ_YN" == grd_GovList.GetCellProp("body",nCell,"ColId"))
	{
		if(ds_GovReg.GetColumn(nRow,"CHK") != 0){
			
			if(ds_GovReg.GetColumn(nRow,"INSPECT_YEAR") != null && ds_GovReg.GetColumn(nRow,"INSPECT_YEAR") != "")
			{
				if(ds_GovReg.GetRowType(nRow) != 'normal'){
					alert("변경된 데이터를 저장 후 버튼 이용 가능합니다.");
					return;
				}
			}else{
				alert("체크된 데이터를 저장 후 버튼 이용 가능합니다. ");
				return;
			}
		}
		
		if(fn_REG_INSPECT_PLAN_YMD != '' && fn_REG_INSPECT_PLAN_YMD != null){
			// 신청서를 인쇄하는 팝업 오픈한다.
			var args  = "INSPECT_SCOPE="         + quote(Replace(ds_GovReg.GetColumn(nRow,"TOWN")," ","&") +"&"+ Replace(ds_GovReg.GetColumn(nRow,"GOV_ROOM_NM")," ","&") + "&정압기");
				args += " INSPECT_BE_SCOPE="     + quote(Replace(ds_GovReg.GetColumn(nRow,"GOV_MODEL_NM")," ","&"));
				args += " FORM_FLAG=2";
				args += " INSPECT_YEAR="         + quote(ds_GovReg.GetColumn(nRow,"INSPECT_YEAR"));
				args += " GOV_NUM=" 			 + quote(ds_GovReg.GetColumn(nRow,"GOV_NUM"));
				args += " FACI_NUM=" 			 + quote(ds_GovReg.GetColumn(nRow,"FACI_NUM"));
				args += " REG_INSPECT_PLAN_YMD=" + quote(ds_GovReg.GetColumn(nRow,"REG_INSPECT_PLAN_YMD"));
				args += " SAFE_OFFICE_NUM="      + quote(ds_GovReg.GetColumn(nRow, "SAFE_OFFICE_NUM"));
				
			var str_StateFlag = gfn_Dialog("D10","D1301010012U",args,450,400,true,-1,-1);
			
			if(str_StateFlag == '' || str_StateFlag == null){
				return;
			}				
			ds_GovReg.SetColumn(nRow, "FIX_INSPECT_REQ_YN", str_StateFlag);
		}else{
			return;
		}
	}
}

//------------------------------------------------------------------------------- 
// 함수명: fn_D1_SearchPlan()
//   설명:  정기검사계획 정압기 중 등록 된 계획을 조회한다.
// arguments
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_D1_SearchPlan()
{	
	var strSvcID 	   = "fn_D1_Search";
	var strPart  	   = "D10";	
	var strURL         = "handle.do?ServiceName=D1_RegularTimeTestPlanRequeMgr-service"
					   + "&INSPECT_YEAR=" + sp_Year.Value
					   + "&BRANCH_CD="    + cob_Branch.Value
					   + "&ZIP_NO1="      + cob_ZipNo.Value
					   + "&TEAM_CD="      + cob_Team.Value
					   + "&BLOCK_CD="	  + cob_PBlockCd.Value
					   ;
	var strInDatasets  = "";
	var strOutDatasets = "ds_GovReg=ds_GovReg";
	var strArgument  = "searchGovPlan";
	
	gfn_SyncCall( 
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
			   );
			   
	if(ErrorCode < 0)
	{    
		return;
	}
	
	if(ds_GovReg.GetRowCount()==0)
	{
        grd_GovList.NoDataText="조회결과가 없습니다.";
        return;
    }
	
	//----  등록 카운트 
	var j=0;
	
	for(var i=0; i < ds_GovReg.GetRowCount();i++)
	{
		  j=j+1;
	}	
	
	Static71.Value = LPAD(j, '0', 3);    
	
	
	for(var i=0;i<ds_GovReg.RowCount();i++)
	{
		ds_GovReg.UpdateControl = false;
		ds_GovReg.SetRowType(i, "normal");
	}
	
	ds_GovRegExcel.Copy(ds_GovReg);
}

//------------------------------------------------------------------------------- 
// 함수명: fn_D1_SaveReg()
//   설명: 정기검사계획 정압기을 등록한다.
// arguments   
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_D1_SaveReg()
{
	var saveFlag = 0;
	
	var nChk = 0;
	
	if(ds_GovReg.RowCount() == 0)
	{
		return;
	}
		
	for(var i=0;i<ds_GovReg.GetRowCount();i++)
	{
		if(ds_GovReg.GetColumn(i,"CHK") == 1){
			nChk = toNumber(nChk) + toNumber(1);
		}
	}
	
	if(nChk <= 0){
		alert('체크된 항목이 없습니다. \n체크 선택 후 등록, 수정 해주세요');
		return;
	}
	
	var nCnt = 0;
	
	for(var i=0;i<ds_GovReg.GetRowCount();i++)
	{
		if(ds_GovReg.GetColumn(i,"CHK") == 1){
			var str_InspectYmd = trim(ds_GovReg.GetColumn(i,"REG_INSPECT_PLAN_YMD"));
			if(str_InspectYmd == '' || str_InspectYmd == null){
					nCnt = toNumber(nCnt) + toNumber(1);
			}
		}
	}
	
	if(nCnt > 0){
		alert("정기검사계획일자는 필수입력사항입니다. ");
		return;
	}
		
	for(var i=0; i<ds_GovReg.RowCount(); i++)
	{
		if(ds_GovReg.GetColumn(i,"CHK") == 1)
		{		
			var str_INSPECT_YEAR = sp_Year.Value;
			var str_FACI_NUM     = ds_GovReg.GetColumn(i, "FACI_NUM");
			var str_GOV_NUM      = ds_GovReg.GetColumn(i, "GOV_NUM" );
				
			if(ds_GovReg.GetColumn(i,"INSPECT_YEAR") == null || ds_GovReg.GetColumn(i,"INSPECT_YEAR") == "")
			{
				fn_D1_CheckReg(str_INSPECT_YEAR, str_FACI_NUM, str_GOV_NUM);
				
				if (ds_GovRegFlag.GetRowCount() > 0){
												
					var str_CRTDTM = ds_GovRegFlag.GetColumn(0, "CRT_TIME" );
					var str_CRTNM  = ds_GovRegFlag.GetColumn(0, "CRT_EMPNM");
					
					var str_GOV_ROOM_NM = ds_GovReg.GetColumn(i,"GOV_ROOM_NM");
						
					alert(str_INSPECT_YEAR + "년 " + str_GOV_ROOM_NM + "에 대한 계획 데이터는\n" + str_CRTNM + "님께서 " + str_CRTDTM + "에 이미 등록하신 데이터입니다.\n데이터를 조회해 주십시오. ");
					return;
					
			    }else{
			    
					ds_GovReg.UpdateControl = false;
					ds_GovReg.SetRowType(i, "insert");
					
					//저장시 사용할 사용자 데이터
					ds_GovReg.SetColumn(i, "UPD_EMPID"   , gfn_GetGlobalValue("G_USER_NO"  ));
					ds_GovReg.SetColumn(i, "UPD_IP"      , gfn_GetGlobalValue("G_IPADDRESS"));
					ds_GovReg.SetColumn(i, "CRT_EMPID"   , gfn_GetGlobalValue("G_USER_NO"  ));
					ds_GovReg.SetColumn(i, "CRT_IP"      , gfn_GetGlobalValue("G_IPADDRESS"));
					ds_GovReg.SetColumn(i, "INSPECT_YEAR", sp_Year.Value);
	
					saveFlag = 1;
			    }
			 }else if(ds_GovReg.GetColumn(i,"INSPECT_YEAR") != null && ds_GovReg.GetColumn(i,"INSPECT_YEAR") != ""){
			 
				ds_GovReg.UpdateControl = false;
				ds_GovReg.SetRowType(i, "update");
				
				if(!fn_DateCheckTodayOver(ds_GovReg.GetColumn(i,"SELF_INSPECT_PLAN_YMD"),"정기검사계획일자"))
				{
					return;
				}
				
				ds_GovReg.SetColumn(i, "UPD_EMPID", gfn_GetGlobalValue("G_USER_NO"));
				ds_GovReg.SetColumn(i, "UPD_IP"   , gfn_GetGlobalValue("G_IPADDRESS"));
				
				saveFlag = 1;			 
			 }
		 }
	}
	
	var strSvcID = "insert";
	var strPart = "D10";
	var strURL = "handle.do?ServiceName=D1_RegularTimeTestPlanRequeMgr-service";
	var strInDatasets  = "ds_GovReg=ds_GovReg:u";
	var strOutDatasets  = "";
	var strArgument  = "saveGovPlanReg";
	
	gfn_SyncCall( 
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
			   );
			   
	if(ErrorCode < 0)
	{    
		return;
	}
	
	if(ds_GovReg.RowCount() == 0)
	{
		return;
	}
	
	if(saveFlag == 0)
	{
		return;
	}
	
	gfn_SetStatusMsg(gfn_GetMessage("M000009","%AAA%","정상적으로 저장을"),"black",true);
	
	ds_GovReg_Copy.Copy(ds_GovReg);
	
	fn_CommonSearch();
	
	for(var i=0; i<ds_GovReg.GetRowCount(); i++){
		ds_GovReg.SetColumn(i, "CHK", ds_GovReg_Copy.GetColumn(i, "CHK"));
	}
}

//------------------------------------------------------------------------------- 
// 함수명: fn_D1_CheckReg()
//   설명: 정기검사 결과등록을 하기 전 기 등록 여부 확인
// arguments   
//          index: 선택된 행
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_D1_CheckReg(str_INSPECT_YEAR, str_FACI_NUM, str_GOV_NUM)
{	
	//-------------------------------------------------------------------------------
	//  Transition   : Service id
	//  ServicePart   : Service part
	//  Url   : Server url
	//  InputDsList   : Input Dataset List
	//  OutputDsList   : Output Dataset List;
	//  Argument   :  Argument
	//-------------------------------------------------------------------------------
	
	ds_GovRegFlag.ClearData();
	
    var strSvcID        = "fn_D1_CheckReg";
    var strPart         = "D10";
    var strURL          = "handle.do?ServiceName=D1_RegularTimeTestPlanRequeMgr-service";
		strURL		   += "&INSPECT_YEAR="    + str_INSPECT_YEAR    ;
		strURL		   += "&FACI_NUM="        + str_FACI_NUM        ;
		strURL		   += "&GOV_NUM="         + str_GOV_NUM         ;
					   
    var strInDatasets   = "";
    var strOutDatasets  = "ds_GovRegFlag=ds_GovRegFlag";
    var strArgument     = "findRegFlag1";   
    
    gfn_SyncCall( 
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
			   );
}

//------------------------------------------------------------------------------- 
//   함수명: fn_D1_Delete()
//   설명  :  정기검사 row 삭제
//   arguments
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_D1_Delete()
{	
	var nChk  = 0;
	var nChk1 = 0;
	
	if(ds_GovReg.GetRowCount() == 0){
		alert("삭제할 데이터가 없습니다. ");
		return;
	}
	
	var result = Confirm("데이터를 삭제하시겠습니까? ");
		
	if(result == '0'){
			return;
	}
	
	for(var i=0;i<ds_GovReg.GetRowCount();i++)
	{
		if(ds_GovReg.GetColumn(i,"CHK") == 1){
			
			nChk = toNumber(nChk) + toNumber(1);
		
			if(ds_GovReg.GetColumn(i, "FIX_INSPECT_REQ_YN") == '유'){
				nChk1 = toNumber(nChk1) + toNumber(1);
			}
		}		
	}
	
	if(nChk <= 0){
		alert('체크된 항목이 없습니다. \n체크 선택 후 삭제 해주세요');
		return;
	}
	
	if(nChk1 > 0){
		alert("신청서가 출력된 데이터가 포함되어 데이터 삭제가 불가능합니다. ");
		return;
	}
	
	ds_GovReg.UpdateControl = false;
	
	for(var i=0;i<ds_GovReg.GetRowCount();i++)
	{
		if(ds_GovReg.GetColumn(i,"CHK") == 1)
		{	
			ds_GovReg.SetRowType(i, "delete");
		}
	}	
		
    var strSvcID       = "";
	var strPart        = "D10";	
	var strURL         = "handle.do?ServiceName=D1_RegularTimeTestPlanRequeMgr-service"
					   ;
	var strInDatasets  = "ds_GovReg=ds_GovReg:u";
	var strOutDatasets = "";
	var strArgument    = "saveGovPlanReg";
	 	 
	gfn_SyncCall( 
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
			   );
	
	if(ErrorCode < 0)
	{
		return;
	}
	
	gfn_SetStatusMsg(gfn_GetMessage("M000009","%AAA%","정상적으로 삭제를"),"black",true);
	
	ds_GovReg_Copy.Copy(ds_GovReg);
	
	fn_CommonSearch();
	
	for(var i=0; i<ds_GovReg.GetRowCount(); i++){
		ds_GovReg.SetColumn(i, "CHK", ds_GovReg_Copy.GetColumn(i, "CHK"));
	}
}

function D1301010010F_T02_OnUnloadCompleted(obj)
{
	if(ds_GovReg.GetUpdate()){
		if(!confirm(gfn_GetMessage("M000016","%AAA%","변경된 내용이 있습니다.종료")))
		{
			return false;
		}
	}
}

function fn_D1_ExcelExport()
{
	var SearchCondition= "";

    SearchCondition = SearchCondition + "지사 :" + RPad(cob_Branch.Text," ",20);
    SearchCondition = SearchCondition + "시/구 :" + RPad(cob_ZipNo.Text," ",20);
    SearchCondition = SearchCondition + "팀 :" + RPad(cob_Team.Text," ",20);
    SearchCondition = SearchCondition + "구간 :" + RPad(cob_PBlockCd.Text," ",20);
    SearchCondition = SearchCondition + "년도 :" + RPad(sp_Year.Text," ",20);
	gfn_ExportExcel("정기검사계획 정압기조회 ", 5, SearchCondition, "grd_Excel"); 
}

function fn_D1_ReportExcelExport()
{	

	fn_D1_SearchGovReport();
	var SearchCondition= "";

    SearchCondition = SearchCondition + "지사 :" + RPad(cob_Branch.Text," ",20);
    SearchCondition = SearchCondition + "시/구 :" + RPad(cob_ZipNo.Text," ",20);
    SearchCondition = SearchCondition + "팀 :" + RPad(cob_Team.Text," ",20);
    SearchCondition = SearchCondition + "구간 :" + RPad(cob_PBlockCd.Text," ",20);
    SearchCondition = SearchCondition + "년도 :" + RPad(sp_Year.Text," ",20);
	gfn_ExportExcel("정압기 현황 ", 5, SearchCondition, "grd_ReportExcel"); 
}

