var fn_REG_INSPECT_PLAN_YMD = "";
//---------------------------------------------------------
// 달력 팝업
//---------------------------------------------------------
function grd_HouseList_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	fn_REG_INSPECT_PLAN_YMD = Trim(ds_HouseReg.GetColumn(nRow,"REG_INSPECT_PLAN_YMD"));

	if("REG_INSPECT_PLAN_YMD" == grd_HouseList.GetCellProp("body",nCell,"ColId"))
	{
		if(ds_HouseReg.GetColumn(nRow,"CHK") != 0){
		
			if(ds_HouseReg.GetColumn(ds_HouseReg.row, "FIX_INSPECT_REQ_YN") == '유'){
				alert("신청서가 출력된 상태에서는 데이터 수정이 불가능합니다. ");
				return;
			}
						
			gfn_SetCalendar(obj, nRow, nCell);
			
			var str_HoliDay = gfn_Bank_Holiday(ds_HouseReg.GetColumn(nRow,"REG_INSPECT_PLAN_YMD"));
			
			if(str_HoliDay == 'Y'){
				alert("휴일 일자는 등록하실 수 없습니다. ");
				ds_HouseReg.SetColumn(nRow,"REG_INSPECT_PLAN_YMD", null);
				return;
			}
			
		}
	}else if("FIX_INSPECT_REQ_YN" == grd_HouseList.GetCellProp("body",nCell,"ColId"))
	{
		if(ds_HouseReg.GetColumn(nRow,"CHK") != 0){
			
			if(ds_HouseReg.GetColumn(nRow,"INSPECT_YEAR") != null && ds_HouseReg.GetColumn(nRow,"INSPECT_YEAR") != "")
			{
				if(ds_HouseReg.GetRowType(nRow) != 'normal'){
					alert("변경된 데이터를 저장 후 버튼 이용 가능합니다.");
					return;
				}	
			}else{
				alert("체크된 데이터를 저장 후 버튼 이용 가능합니다. ");
				return;
			}
		}
				
		if(fn_REG_INSPECT_PLAN_YMD != '' && fn_REG_INSPECT_PLAN_YMD != null){
		// 신청서를 인쇄하는 팝업 오픈한다.
		var args  = "INSPECT_SCOPE="         + quote(Replace(ds_HouseReg.GetColumn(nRow,"CO_LIVE_NM"))," ","&")+"!";
			args += " INSPECT_BE_SCOPE="     + quote(Replace(ds_HouseReg.GetColumn(nRow,"ZIP_NO1")," ","&")+ "&" + Replace(ds_HouseReg.GetColumn(nRow,"LOT_NUM_UNION")," ","&"));
			args += " FORM_FLAG=3";
			args += " INSPECT_YEAR="         + quote(ds_HouseReg.GetColumn(nRow,"INSPECT_YEAR"));
			args += " FACI_NUM="             + quote(ds_HouseReg.GetColumn(nRow,"FACI_NUM"));
			args += " REG_INSPECT_PLAN_YMD=" + quote(ds_HouseReg.GetColumn(nRow,"REG_INSPECT_PLAN_YMD"));
			args += " SAFE_OFFICE_NUM="      + quote(ds_HouseReg.GetColumn(nRow,"SAFE_OFFICE_NUM"));
					
			var str_StateFlag = gfn_Dialog("D10","D1301010012U",args,450,400,true,-1,-1);
			
			if(str_StateFlag == '' || str_StateFlag == null){
				return;
			}				
			ds_HouseReg.SetColumn(nRow, "FIX_INSPECT_REQ_YN", str_StateFlag);
		}else{
			return;
		}		
	}
}

//------------------------------------------------------------------------------- 
// 함수명: fn_D1_SearchPlan()
//   설명:  정기검사계획 공동주택 중 등록 된 계획을 조회한다.
// arguments
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_D1_SearchPlan()
{	
	var strSvcID = "fn_D1_Search";
	var strPart = "D10";	
	var strURL = "handle.do?ServiceName=D1_RegularTimeTestPlanRequeMgr-service&INSPECT_YEAR="+sp_Year.Value+"&BRANCH_CD="+cob_Branch.Value+"&ZIP_NO1="+cob_ZipNo.Value;
	var strInDatasets  = "";
	var strOutDatasets  = "ds_HouseReg=ds_HouseReg";
	var strArgument  = "searchHousePlan";
	
	gfn_SyncCall( 
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
			   );
	if(ErrorCode < 0)
	{    
		return;
	}
	
	if(ds_HouseReg.GetRowCount()==0)
	{
        grd_HouseList.NoDataText="조회결과가 없습니다.";
        return;
    }
    
    //----  등록 카운트 
	var j=0;
	
	for(var i=0; i < ds_HouseReg.GetRowCount();i++)
	{
		  j=j+1;
	}	
	
	Static71.Value = LPAD(j, '0', 4);    
	
	fn_D1_CostCalHouse();
	fn_D1_LeakHouse();
	ds_HouseRegExcel.Copy(ds_HouseReg);
	ds_HouseRegReportExcel.Copy(ds_HouseReg);
}

//------------------------------------------------------------------------------- 
// 함수명: fn_D1_CostCalHouse()
//   설명:  정기 검사비 중 수수료 부가세 합계를 계산한다.
// arguments
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_D1_CostCalHouse()
{
	var strTotLeng = 0;
	var strFee = 0;
	var strTax = 0;
	var strSumFee = 0;
	
	for(var i=0;i<ds_HouseReg.RowCount();i++)
	{
		ds_HouseReg.UpdateControl = false;
		
		if(ds_HouseReg.GetColumn(i,"INSPECT_YEAR") != null && ds_HouseReg.GetColumn(i,"INSPECT_YEAR") != "")
		{
			strTotLeng = ParseInt(ds_HouseReg.GetColumn(i,"BURY_LENG"));
		    
			if(strTotLeng < 2000)
			{
				strFee = ParseInt(ds_HouseReg.GetColumn(i,"PIPE_2UND_UNIT_PRC"));
				strTax = Round(strFee * 0.1);
				strSumFee = strFee + strTax;
				
				ds_HouseReg.SetColumn(i,"FEE",strFee);
				ds_HouseReg.SetColumn(i,"TAX",strTax);
				ds_HouseReg.SetColumn(i,"SUMFEE",strSumFee);
			}else if(strTotLeng < 5000)
			{
				strFee = ParseInt(ds_HouseReg.GetColumn(i,"PIPE_2_5UD_UNIT_PRC"));
				strTax = Round(strFee * 0.1);
				strSumFee = strFee + strTax;
				
				ds_HouseReg.SetColumn(i,"FEE",strFee);
				ds_HouseReg.SetColumn(i,"TAX",strTax);
				ds_HouseReg.SetColumn(i,"SUMFEE",strSumFee);
			}else if(strTotLeng < 10000)
			{
				strFee = ParseInt(ds_HouseReg.GetColumn(i,"PIPE_5_10UD_UNIT_PRC"));
				strTax = Round(strFee * 0.1);
				strSumFee = strFee + strTax;
				
				ds_HouseReg.SetColumn(i,"FEE",strFee);
				ds_HouseReg.SetColumn(i,"TAX",strTax);
				ds_HouseReg.SetColumn(i,"SUMFEE",strSumFee);
			}else
			{
				var strOverCost = 0;
				
				strOverCost = parseInt((strTotLeng - 10000) / 1000) * ParseInt(ds_HouseReg.GetColumn(i,"PIPE_10K_OVER_UNIT_PRC"));
				
				strFee = ParseInt(ds_HouseReg.GetColumn(i,"PIPE_5_10UD_UNIT_PRC")) + strOverCost;
				strTax = Round(strFee * 0.1);
				strSumFee = strFee + strTax;
				
				ds_HouseReg.SetColumn(i,"FEE",strFee);
				ds_HouseReg.SetColumn(i,"TAX",strTax);
				ds_HouseReg.SetColumn(i,"SUMFEE",strSumFee);
				
				
				
			}
			ds_HouseReg.SetRowType(i, "normal");
		}
	}
}

//------------------------------------------------------------------------------- 
// 함수명: fn_D1_LeakHouse()
//   설명:  정기 검사 중 누설 기밀대상을 선정한다.
// arguments
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_D1_LeakHouse()
{
	var strInstYear = 0;
	var strBaseYear = parseInt(sp_Year.Value);
	
	for(var i=0;i<ds_HouseReg.RowCount();i++)
	{
		ds_HouseReg.UpdateControl = false;
		
		var InstYear = substr(ds_HouseReg.GetColumn(i,"INST_YMD"),0,4);
		
		if(parseInt(InstYear) < 1989){ // 1988 이전
			
			if(strBaseYear == 1996 || strBaseYear == 1999 || strBaseYear == 2002 || strBaseYear == 2005 || 
			   strBaseYear == 2008 || strBaseYear == 2011 || strBaseYear == 2014 || strBaseYear == 2017 || 
			   strBaseYear == 2020 || strBaseYear == 2023 || strBaseYear == 2026 || strBaseYear == 2029
			)
			{
				ds_HouseReg.SetColumn(i,"LEAK_YN","기밀");
			}else{
				ds_HouseReg.SetColumn(i,"LEAK_YN","누출");
			}	
								
		}else if(parseInt(InstYear) > 1988 && parseInt(InstYear) <= 1991 ){ // 1989 - 1991
			
			if(strBaseYear == 1997 || strBaseYear == 2000 || strBaseYear == 2003 || strBaseYear == 2006 || 
			   strBaseYear == 2009 || strBaseYear == 2012 || strBaseYear == 2015 || strBaseYear == 2018 || 
			   strBaseYear == 2021 || strBaseYear == 2024 || strBaseYear == 2027 || strBaseYear == 2030
			)
			{
				ds_HouseReg.SetColumn(i,"LEAK_YN","기밀");
			}else{
				ds_HouseReg.SetColumn(i,"LEAK_YN","누출");
			}
				
		}else if(parseInt(InstYear) >= 1992 && parseInt(InstYear) < 1995 ){ // 1992 - 1994
		
			if(strBaseYear == 1998 || strBaseYear == 2001 || strBaseYear == 2004 || strBaseYear == 2007 || 
			   strBaseYear == 2010 || strBaseYear == 2013 || strBaseYear == 2016 || strBaseYear == 2019 || 
			   strBaseYear == 2022 || strBaseYear == 2025 || strBaseYear == 2028 || strBaseYear == 2031
			)
			{
				ds_HouseReg.SetColumn(i,"LEAK_YN","기밀");
			}else{
				ds_HouseReg.SetColumn(i,"LEAK_YN","누출");
			}
		
		}else{
				
			strInstYear = InstYear;
						
			if((toNumber(strBaseYear) - toNumber(strInstYear)) % 3 == 0)
			{
				ds_HouseReg.SetColumn(i,"LEAK_YN","기밀"); 
			
			}else{
				ds_HouseReg.SetColumn(i,"LEAK_YN","누출");
			}
		}
		
		ds_HouseReg.SetRowType(i, "normal");
	}
}

//------------------------------------------------------------------------------- 
// 함수명: fn_D1_SaveReg()
//   설명: 정기검사계획 공동주택를 등록한다.
// arguments   
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_D1_SaveReg()
{
	var saveFlag = 0;
	
	var nChk = 0;
	
	if(ds_HouseReg.RowCount() == 0)
	{
		return;
	}
	
	for(var i=0;i<ds_HouseReg.GetRowCount();i++)
	{
		if(ds_HouseReg.GetColumn(i,"CHK") == 1){
			nChk = toNumber(nChk) + toNumber(1);
		}
	}
	
	if(nChk <= 0){
		alert('체크된 항목이 없습니다. \n체크 선택 후 등록, 수정 해주세요');
		return;
	}
	
	var nCnt = 0;
	
	for(var i=0;i<ds_HouseReg.GetRowCount();i++)
	{
		if(ds_HouseReg.GetColumn(i,"CHK") == 1){
			var str_InspectYmd = trim(ds_HouseReg.GetColumn(i,"REG_INSPECT_PLAN_YMD"));
			if(str_InspectYmd == '' || str_InspectYmd == null){
					nCnt = toNumber(nCnt) + toNumber(1);
			}
		}
	}
	
	if(nCnt > 0){
		alert("정기검사계획일자는 필수입력사항입니다. ");
		return;
	}
	
	for(var i=0; i<ds_HouseReg.RowCount(); i++)
	{
		if(ds_HouseReg.GetColumn(i,"CHK") == 1)
		{		
			var str_INSPECT_YEAR = sp_Year.Value;
			var str_FACI_NUM     = ds_HouseReg.GetColumn(i, "FACI_NUM");
				
			if(ds_HouseReg.GetColumn(i,"INSPECT_YEAR") == null || ds_HouseReg.GetColumn(i,"INSPECT_YEAR") == "")
			{
				fn_D1_CheckReg(str_INSPECT_YEAR, str_FACI_NUM);
				
				if (ds_HouseRegFlag.GetRowCount() > 0){
												
					var str_CRTDTM = ds_HouseRegFlag.GetColumn(0, "CRT_TIME" );
					var str_CRTNM  = ds_HouseRegFlag.GetColumn(0, "CRT_EMPNM");
					
					var str_CO_LIVE_NM = ds_HouseReg.GetColumn(i,"CO_LIVE_NM");
											
					alert(str_INSPECT_YEAR + "년 " + str_CO_LIVE_NM + "에 대한 계획 데이터는\n" + str_CRTNM + "님께서 " + str_CRTDTM + "에 이미 등록하신 데이터입니다.\n데이터를 조회해 주십시오. ");
					return;
					
			    }else{
			    
					ds_HouseReg.UpdateControl = false;
					ds_HouseReg.SetRowType(i, "insert");
					
					//저장시 사용할 사용자 데이터
					ds_HouseReg.SetColumn(i, "UPD_EMPID"   , gfn_GetGlobalValue("G_USER_NO"  ));
					ds_HouseReg.SetColumn(i, "UPD_IP"      , gfn_GetGlobalValue("G_IPADDRESS"));
					ds_HouseReg.SetColumn(i, "CRT_EMPID"   , gfn_GetGlobalValue("G_USER_NO"  ));
					ds_HouseReg.SetColumn(i, "CRT_IP"      , gfn_GetGlobalValue("G_IPADDRESS"));
					ds_HouseReg.SetColumn(i, "INSPECT_YEAR", sp_Year.Value);
	
					saveFlag = 1;
			    }
			 }else if(ds_HouseReg.GetColumn(i,"INSPECT_YEAR") != null && ds_HouseReg.GetColumn(i,"INSPECT_YEAR") != ""){
			 
				ds_HouseReg.UpdateControl = false;
				ds_HouseReg.SetRowType(i, "update");
				
				if(!fn_DateCheckTodayOver(ds_HouseReg.GetColumn(i,"SELF_INSPECT_PLAN_YMD"),"정기검사계획일자"))
				{
					return;
				}
				
				ds_HouseReg.SetColumn(i, "UPD_EMPID", gfn_GetGlobalValue("G_USER_NO"));
				ds_HouseReg.SetColumn(i, "UPD_IP"   , gfn_GetGlobalValue("G_IPADDRESS"));
				
				saveFlag = 1;
			 
			 }
		 }
	}
	
	var strSvcID = "insert";
	var strPart = "D10";
	var strURL = "handle.do?ServiceName=D1_RegularTimeTestPlanRequeMgr-service";
	var strInDatasets  = "ds_HouseReg=ds_HouseReg:u";
	var strOutDatasets  = "";
	var strArgument  = "saveHousePlanReg";
	
	gfn_SyncCall( 
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
			   );
	if(ErrorCode < 0)
	{    
		return;
	}
	
	if(ds_HouseReg.RowCount() == 0)
	{
		return;
	}
	
	if(saveFlag == 0)
	{
		return;
	}
	
	gfn_SetStatusMsg(gfn_GetMessage("M000009","%AAA%","정상적으로 저장을"),"black",true);
	
	ds_HouseReg_Copy.Copy(ds_HouseReg);
	
	fn_CommonSearch();
	
	for(var i=0; i<ds_HouseReg.GetRowCount(); i++){
		ds_HouseReg.SetColumn(i, "CHK", ds_HouseReg_Copy.GetColumn(i, "CHK"));
	}
}

//------------------------------------------------------------------------------- 
// 함수명: fn_D1_CheckReg()
//   설명: 정기검사 결과등록을 하기 전 기 등록 여부 확인
// arguments   
//          index: 선택된 행
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_D1_CheckReg(str_INSPECT_YEAR, str_FACI_NUM)
{	
	//-------------------------------------------------------------------------------
	//  Transition   : Service id
	//  ServicePart   : Service part
	//  Url   : Server url
	//  InputDsList   : Input Dataset List
	//  OutputDsList   : Output Dataset List;
	//  Argument   :  Argument
	//-------------------------------------------------------------------------------
	
	ds_HouseRegFlag.ClearData();
	
    var strSvcID        = "fn_D1_CheckReg";
    var strPart         = "D10";
    var strURL          = "handle.do?ServiceName=D1_RegularTimeTestPlanRequeMgr-service";
		strURL		   += "&INSPECT_YEAR="    + str_INSPECT_YEAR    ;
		strURL		   += "&FACI_NUM="        + str_FACI_NUM        ;
					   
    var strInDatasets   = "";
    var strOutDatasets  = "ds_HouseRegFlag=ds_PipeRegFlag";
    var strArgument     = "findRegFlag2";   
    
    gfn_SyncCall( 
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
			   );
}

//------------------------------------------------------------------------------- 
//   함수명: fn_D1_Delete()
//   설명  :  정기검사 row 삭제
//   arguments
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_D1_Delete()
{	
	var nChk  = 0;
	var nChk1 = 0;
	
	if(ds_HouseReg.GetRowCount() == 0){
		alert("삭제할 데이터가 없습니다. ");
		return;
	}
	
	var result = Confirm("데이터를 삭제하시겠습니까? ");
		
	if(result == '0'){
			return;
	}
	
	for(var i=0;i<ds_HouseReg.GetRowCount();i++)
	{
		if(ds_HouseReg.GetColumn(i,"CHK") == 1){
			
			nChk = toNumber(nChk) + toNumber(1);
		
			if(ds_HouseReg.GetColumn(i, "FIX_INSPECT_REQ_YN") == '유'){
				nChk1 = toNumber(nChk1) + toNumber(1);
			}
		}		
	}
	
	if(nChk <= 0){
		alert('체크된 항목이 없습니다. \n체크 선택 후 삭제 해주세요');
		return;
	}
	
	if(nChk1 > 0){
		alert("신청서가 출력된 데이터가 포함되어 데이터 삭제가 불가능합니다. ");
		return;
	}
	
	ds_HouseReg.UpdateControl = false;
	
	for(var i=0;i<ds_HouseReg.GetRowCount();i++)
	{
		if(ds_HouseReg.GetColumn(i,"CHK") == 1)
		{	
			ds_HouseReg.SetRowType(i, "delete");
		}
	}
		
    var strSvcID       = "";
	var strPart        = "D10";	
	var strURL         = "handle.do?ServiceName=D1_RegularTimeTestPlanRequeMgr-service"
					   ;
	var strInDatasets  = "ds_HouseReg=ds_HouseReg:u";
	var strOutDatasets = "";
	var strArgument    = "saveHousePlanReg";
	 	 
	gfn_SyncCall( 
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
			   );
	
	if(ErrorCode < 0)
	{
		return;
	}
	
	gfn_SetStatusMsg(gfn_GetMessage("M000009","%AAA%","정상적으로 삭제를"),"black",true);
	
	ds_HouseReg_Copy.Copy(ds_HouseReg);
	
	fn_CommonSearch();
	
	for(var i=0; i<ds_HouseReg.GetRowCount(); i++){
		ds_HouseReg.SetColumn(i, "CHK", ds_HouseReg_Copy.GetColumn(i, "CHK"));
	}
}

