function fn_MultiReport_Print(){
	
	var nChk  = 0;
	var nChk1 = 0;
	var nChk2 = 0;
	
	if(ds_HouseReg.GetRowCount() == 0){
		alert("출력할 데이터가 없습니다. ");
		return;
	}
		
	for(var i=0;i<ds_HouseReg.GetRowCount();i++)
	{
		if(ds_HouseReg.GetColumn(i,"CHK") == 1){
			
			nChk = toNumber(nChk) + toNumber(1);
		
			if(ds_HouseReg.GetColumn(i, "FIX_INSPECT_REQ_YN") == '유'){
				nChk1 = toNumber(nChk1) + toNumber(1);
			}
			
			var str_REG_INSPECT_PLAN_YMD = ds_HouseReg.GetColumn(i, "REG_INSPECT_PLAN_YMD");
			
			if(str_REG_INSPECT_PLAN_YMD == null || str_REG_INSPECT_PLAN_YMD == ''){
				nChk2 = toNumber(nChk2) + toNumber(1);
			}
		}		
	}
	
	if(nChk <= 0){
		alert('체크된 항목이 없습니다. \n체크 선택 후 출력 해주세요');
		return;
	}
	
	if(nChk1 > 0){
		alert("신청서가 출력된 데이터가 포함되어 데이터 일괄출력이 불가능합니다. ");
		return;
	}
	
	if(nChk2 > 0){
		alert("계획일자가 지정되지 않은 데이터가 존재하여 일괄출력이 불가능합니다. ");
		return;
	}
	
	ds_ReportList.ClearData();	
	ds_UpdateForm_Copy.ClearData();
	
	var nCount = ds_HouseReg.GetRowCount();
		
	// 함수 호출용 파라미터를 만든다
	var strTitle     ;				// 출력물 제목
    var strPart      ;         		// 시스템 명 
	var strReport    ; 				// 출력물 ID 
	var strParameter ;      		// 파라미터구문 (단 ',' 로구분할것 )	

	for(var i=0; i<nCount; i++){
		
		if(ds_HouseReg.GetColumn(i,"CHK") == 1){
		
		var nRow = ds_ReportList.addRow();		
				
		// 함수 호출용 파라미터를 설정한다
		var strTitle      = "정기 검사 신청서" + " " + nRow ; 
		var strPart       = "D10"			    ;           
		var strReport     = "D1301010012P"      ; 
		var strParameter  = "INSPECT_SCOPE="         + quote(ds_HouseReg.GetColumn(nRow,"CO_LIVE_NM"));
			strParameter += ",INSPECT_BE_SCOPE="     + quote(ds_HouseReg.GetColumn(nRow,"ZIP_NO1") + " " + ds_HouseReg.GetColumn(nRow,"LOT_NUM_UNION"));
			strParameter += ",FORM_FLAG=3";
			strParameter += ",INSPECT_YEAR="         + quote(ds_HouseReg.GetColumn(nRow,"INSPECT_YEAR"));
			strParameter += ",FACI_NUM="             + quote(ds_HouseReg.GetColumn(nRow,"FACI_NUM"));
			strParameter += ",REG_INSPECT_PLAN_YMD=" + quote(ds_HouseReg.GetColumn(nRow,"REG_INSPECT_PLAN_YMD"));
			strParameter += ",SAFE_OFFICE_NUM="      + quote(ds_HouseReg.GetColumn(nRow,"SAFE_OFFICE_NUM"));						
						
						
			ds_ReportList.SetColumn(nRow, "SYSTEM_ID"   ,strPart     ); // 시스템 명 
			ds_ReportList.SetColumn(nRow, "REPORT_ID"   ,strReport   ); // 출력물 ID 
			ds_ReportList.SetColumn(nRow, "REPORT_ARGS" ,strParameter); // 파라미터구문 
			ds_ReportList.SetColumn(nRow, "REPORT_TITLE",strTitle    ); // 출력물 제목		
			
			var nRow1 = ds_UpdateForm_Copy.addRow();
			
			ds_UpdateForm_Copy.SetColumn(nRow1, "UPD_EMPID"   , gfn_GetGlobalValue("G_USER_NO"       ));
			ds_UpdateForm_Copy.SetColumn(nRow1, "UPD_IP"      , gfn_GetGlobalValue("G_IPADDRESS"     ));
			ds_UpdateForm_Copy.SetColumn(nRow1, "INSPECT_YEAR", ds_HouseReg.GetColumn(i,"INSPECT_YEAR" ));			
			ds_UpdateForm_Copy.SetColumn(nRow1, "FACI_NUM"    , ds_HouseReg.GetColumn(i,"FACI_NUM"));
			ds_UpdateForm_Copy.SetColumn(nRow1, "FORM_FLAG"   , '3');			
		}
	}
	
	var str_StateFlag = gfn_Dialog("D10","D1301010014U","",450,400,true,-1,-1);
	
	for(var i=0; i<nCount; i++){
		
		if(ds_HouseReg.GetColumn(i,"CHK") == 1){
			
			if(str_StateFlag == '' || str_StateFlag == null){
					return;
			}					
								
			ds_HouseReg.SetColumn(i, "FIX_INSPECT_REQ_YN", str_StateFlag);
		}
	}
}
