//---------------------------------------------------------
// 달력 팝업
//---------------------------------------------------------
function grd_GovList_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	if(ds_GovReg.GetColumn(nRow,"CHK") != 0){
		
		var strGetCell = grd_GovList.GetCellProp("body",nCell,"ColId");
		
		if("SELF_INSPECT_PLAN_YMD" == strGetCell)
		{
			gfn_SetCalendar(obj, nRow, nCell);
			
			var str_HoliDay = gfn_Bank_Holiday(ds_GovReg.GetColumn(nRow,"SELF_INSPECT_PLAN_YMD"));
			
			if(str_HoliDay == 'Y'){
				alert("휴일 일자는 등록하실 수 없습니다. ");
				ds_GovReg.SetColumn(nRow,"SELF_INSPECT_PLAN_YMD", null);
				return;
			}
		}

		if(gfn_IsNull(ds_GovReg.GetColumn(nRow,"INSPECT_YEAR")) || gfn_IsNull(ds_GovReg.GetColumn(nRow,"DANGER_YN")))
	    {	
         if("GOV_DISJOINT_CHK_YMD" == strGetCell)
         {
			gfn_SetCalendar(obj, nRow, nCell);
			
			var str_HoliDay = gfn_Bank_Holiday(ds_GovReg.GetColumn(nRow,"GOV_DISJOINT_CHK_YMD"));
			
			if(str_HoliDay == 'Y'){
				alert("휴일 일자는 등록하실 수 없습니다. ");
				ds_GovReg.SetColumn(nRow,"GOV_DISJOINT_CHK_YMD", null);
				return;
			}
		 }
		 if("FILTER_CHK_YMD" == strGetCell)
         {
			gfn_SetCalendar(obj, nRow, nCell);
			
			var str_HoliDay = gfn_Bank_Holiday(ds_GovReg.GetColumn(nRow,"FILTER_CHK_YMD"));
			
			if(str_HoliDay == 'Y'){
				alert("휴일 일자는 등록하실 수 없습니다. ");
				ds_GovReg.SetColumn(nRow,"FILTER_CHK_YMD", null);
				return;
			}
		 }
		}
	}
}

//------------------------------------------------------------------------------- 
// 함수명: fn_D1_SearchPlan()
//   설명:  자율검사계획 정압기 중 등록 된 계획을 조회한다.
// arguments
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_D1_SearchPlan()
{	
	var strSvcID       = "fn_D1_Search";
	var strPart        = "D10";	
	var strURL         = "handle.do?ServiceName=D1_SelfTestPlanRegisGoveMgr-service"
					   + "&INSPECT_YEAR=" + sp_Year.Value
					   + "&BRANCH_CD="    + cob_Branch.Value
					   + "&ZIP_NO1="      + cob_ZipNo.Value
					   + "&TEAM_CD="      + cob_Team.Value
			           + "&BLOCK_CD="	  + cob_PBlockCd.Value
					   ;
	var strInDatasets  = "";
	var strOutDatasets = "ds_GovReg=ds_GovReg";
	var strArgument    = "searchGovPlan";
	
	gfn_SyncCall( 
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
			   );
	if(ErrorCode < 0)
	{    
		return;
	}
	
	if(ds_GovReg.GetRowCount()==0)
	{
        grd_GovList.NoDataText="조회결과가 없습니다.";
        return;
    }
	
	ds_GovRegExcel.Copy(ds_GovReg);
	
}

//------------------------------------------------------------------------------- 
// 함수명: fn_D1_SaveReg()
//   설명: 자율검사계획 정압기을 등록한다.
// arguments   
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_D1_SaveReg()
{
	var saveFlag = 0;
	
	var nChk = 0;
	
	if(ds_GovReg.GetRowCount() == 0){
		alert("데이터를 먼저 조회하세요. ");
		return;
	}
	
	for(var i=0;i<ds_GovReg.GetRowCount();i++)
	{
		if(ds_GovReg.GetColumn(i,"CHK") == 1){
			nChk = toNumber(nChk) + toNumber(1);
		}
	}
	if(nChk <= 0){
		alert('체크된 항목이 없습니다. \n체크 선택 후 등록, 수정 해주세요');
		return;
	}
	
	var nCnt = 0;
	
	for(var i=0;i<ds_GovReg.GetRowCount();i++)
	{
		if(ds_GovReg.GetColumn(i,"CHK") == 1){
			if(gfn_IsNull(trim(ds_GovReg.GetColumn(i,"SELF_INSPECT_PLAN_YMD")))){
					nCnt = toNumber(nCnt) + toNumber(1);
			}
		}
	}
	
	if(nCnt > 0){
		alert("자율계획일자는 필수입력사항입니다. ");
		return;
	}
	
	for(var i=0;i<ds_GovReg.GetRowCount();i++)
	{
		if(ds_GovReg.GetColumn(i,"CHK") == 1)
		{	
			if(ds_GovReg.GetColumn(i,"INSPECT_YEAR") == null || ds_GovReg.GetColumn(i,"INSPECT_YEAR") == "")
			{			
			
				var str_INSPECT_YEAR = sp_Year.Value;
				var str_FACI_NUM     = ds_GovReg.GetColumn(i, "FACI_NUM");
				var str_GOV_NUM      = ds_GovReg.GetColumn(i, "GOV_NUM" );
			
				fn_D1_CheckReg(str_INSPECT_YEAR, str_FACI_NUM, str_GOV_NUM);
				
				if (ds_GovRegFlag.GetRowCount() > 0){
												
					var str_CRTDTM = ds_GovRegFlag.GetColumn(0, "CRT_TIME" );
					var str_CRTNM  = ds_GovRegFlag.GetColumn(0, "CRT_EMPNM");
					
					var str_GOV_ROOM_NM = ds_GovReg.GetColumn(i,"GOV_ROOM_NM");
						
					alert(str_INSPECT_YEAR + "년 " + str_GOV_ROOM_NM + "에 대한 계획 데이터는\n" + str_CRTNM + "님께서 " + str_CRTDTM + "에 이미 등록하신 데이터입니다.\n데이터를 조회해 주십시오. ");
					return;
					
			    }else{
					ds_GovReg.UpdateControl = false;
					ds_GovReg.SetRowType(i, "insert");
					
					fn_D1_SeachPriYmd(ds_GovReg.GetColumn(i,"BLOCK_CD"),sp_Year.Value, i);
					
					ds_GovReg.SetColumn(i, "PRI1", ds_GovPriYmd.GetColumn(0,"PRI1"));	
					ds_GovReg.SetColumn(i, "PRI2", ds_GovPriYmd.GetColumn(0,"PRI2"));	
					ds_GovReg.SetColumn(i, "PRI3", ds_GovPriYmd.GetColumn(0,"PRI3"));	
					
					//저장시 사용할 사용자 데이터
					ds_GovReg.SetColumn(i, "UPD_EMPID"   , gfn_GetGlobalValue("G_USER_NO"  ));
					ds_GovReg.SetColumn(i, "UPD_IP"      , gfn_GetGlobalValue("G_IPADDRESS"));
					ds_GovReg.SetColumn(i, "CRT_EMPID"   , gfn_GetGlobalValue("G_USER_NO"  ));
					ds_GovReg.SetColumn(i, "CRT_IP"      , gfn_GetGlobalValue("G_IPADDRESS"));
					ds_GovReg.SetColumn(i, "INSPECT_YEAR", sp_Year.Value);
									
					saveFlag = 1;
				}
				
			}else if(ds_GovReg.GetColumn(i,"INSPECT_YEAR") != null && ds_GovReg.GetColumn(i,"INSPECT_YEAR") != ""){
			
				if(!fn_DateCheckTodayOver(ds_GovReg.GetColumn(i,"SELF_INSPECT_PLAN_YMD"),"자율계획일자"))
				{
					return;
				}
				ds_GovReg.UpdateControl = false;
				ds_GovReg.SetRowType(i, "update");
				
				ds_GovReg.SetColumn(i, "UPD_EMPID", gfn_GetGlobalValue("G_USER_NO"));
				ds_GovReg.SetColumn(i, "UPD_IP"   , gfn_GetGlobalValue("G_IPADDRESS"));				
			
				saveFlag = 1;
								
			}
		}
	}
	
	var strSvcID        = "insert";
	var strPart         = "D10";
	var strURL          = "handle.do?ServiceName=D1_SelfTestPlanRegisGoveMgr-service";
	var strInDatasets   = "ds_GovReg=ds_GovReg:u";
	var strOutDatasets  = "";
	var strArgument     = "saveGovPlanReg";
	
	gfn_SyncCall( 
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
			   );
			   
	if(ErrorCode < 0)
	{    
		return;
	}
	
	if(ds_GovReg.RowCount() == 0)
	{
		return;
	}
	
	if(saveFlag == 0)
	{
		return;
	}
	
	gfn_SetStatusMsg(gfn_GetMessage("M000009","%AAA%","정상적으로 저장을"),"black",true);
	
	ds_GovReg_Copy.Copy(ds_GovReg);
	
	fn_CommonSearch();
	
	for(var i=0; i<ds_GovReg.GetRowCount(); i++){
		ds_GovReg.SetColumn(i, "CHK", ds_GovReg_Copy.GetColumn(i, "CHK"));
	}
}

//------------------------------------------------------------------------------- 
// 함수명: fn_D1_CheckReg()
//   설명: 정기검사 결과등록을 하기 전 기 등록 여부 확인
// arguments   
//          index: 선택된 행
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_D1_CheckReg(str_INSPECT_YEAR, str_FACI_NUM, str_GOV_NUM)
{	
	//-------------------------------------------------------------------------------
	//  Transition   : Service id
	//  ServicePart   : Service part
	//  Url   : Server url
	//  InputDsList   : Input Dataset List
	//  OutputDsList   : Output Dataset List;
	//  Argument   :  Argument
	//-------------------------------------------------------------------------------
	
	ds_GovRegFlag.ClearData();
	
    var strSvcID        = "fn_D1_CheckReg";
    var strPart         = "D10";
    var strURL          = "handle.do?ServiceName=D1_SelfTestPlanRegisGoveMgr-service";
		strURL		   += "&INSPECT_YEAR="    + str_INSPECT_YEAR    ;
		strURL		   += "&FACI_NUM="        + str_FACI_NUM        ;
		strURL		   += "&GOV_NUM="         + str_GOV_NUM         ;
					   
    var strInDatasets   = "";
    var strOutDatasets  = "ds_GovRegFlag=ds_PipeRegFlag";
    var strArgument     = "findRegFlag";   
    
    gfn_SyncCall( 
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
			   );
}

//------------------------------------------------------------------------------- 
//   함수명: fn_D1_Delete()
//   설명  :  정기검사 row 삭제
//   arguments
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_D1_Delete()
{	
	var nChk = 0;
	
	if(ds_GovReg.GetRowCount() == 0){
		alert("데이터를 먼저 조회하세요. ");
		return;
	}
	
	var result = Confirm("데이터를 삭제하시겠습니까? ");
		
	if(result == '0'){
			return;
	}
	
	for(var i=0;i<ds_GovReg.GetRowCount();i++)
	{
		if(ds_GovReg.GetColumn(i,"CHK") == 1){
			nChk = toNumber(nChk) + toNumber(1);
		}
	}
	
	if(nChk <= 0){
		alert('체크된 항목이 없습니다. \n체크 선택 후 삭제 해주세요');
		return;
	}
	
	ds_GovReg.UpdateControl = false;
	
	for(var i=0;i<ds_GovReg.GetRowCount();i++)
	{
		if(ds_GovReg.GetColumn(i,"CHK") == 1)
		{	
			ds_GovReg.SetRowType(i, "delete");
		}
	}
		
	var strSvcID        = "insert";
	var strPart         = "D10";
	var strURL          = "handle.do?ServiceName=D1_SelfTestPlanRegisGoveMgr-service";
	var strInDatasets   = "ds_GovReg=ds_GovReg:u";
	var strOutDatasets  = "";
	var strArgument     = "saveGovPlanReg";
	 	 
	gfn_SyncCall( 
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
			   );
	
	if(ErrorCode < 0)
	{
		return;
	}
	
	gfn_SetStatusMsg(gfn_GetMessage("M000009","%AAA%","정상적으로 삭제를"),"black",true);
	
	ds_GovReg_Copy.Copy(ds_GovReg);
	
	fn_CommonSearch();
	
	for(var i=0; i<ds_GovReg.GetRowCount(); i++){
		ds_GovReg.SetColumn(i, "CHK", ds_GovReg_Copy.GetColumn(i, "CHK"));
	}
}

//------------------------------------------------------------------------------- 
// 함수명: fn_PriYmd_OnClick()
//   설명: 자율검사계획 정압기 직전검사일자에서 1년 후를 적용한다.
// arguments
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_PriYmd_OnClick()
{	
	for(var i =0 ;i<ds_GovReg.RowCount(); i++)
	{
		if(ds_GovReg.GetColumn(i,"CHK") == 1)
		{	
			var strPriYmd = "";
			var strInspectYmdPri = "";
			
			strInspectYmdPri = Trim(ds_GovReg.GetColumn(i,"INSPECT_YMD_PRI1"));

			strPriYmd = addMonth(strInspectYmdPri,12);
			ds_GovReg.SetColumn(i,"SELF_INSPECT_PLAN_YMD", strPriYmd);
		}
	}
}

//------------------------------------------------------------------------------- 
// 함수명: fn_D1_SeachPriYmd(strFaciNum,strGovNum,strSpYear)
//   설명: 자율검사계획 정압기 체크한 내용의 전년도 검사계획일자를 조회한다.
// arguments
//          strFaciNum : 정압실번호
//          strGovNum : 정압기번호
//          strSpYear : 검사년도
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_D1_SeachPriYmd(strFaciNum,strGovNum,strSpYear, strnRow)
{
	var strPri1 = ParseInt(strSpYear)-1;
	var strPri2 = ParseInt(strSpYear)-2;
	var strPri3 = ParseInt(strSpYear)-3;
	
	var strSvcID       = "fn_D1_Search";
	var strPart        = "D10";	
	var strURL         = "handle.do?ServiceName=D1_SelfTestPlanRegisGoveMgr-service&FACI_NUM="+strFaciNum+"&GOV_NUM="+strGovNum+"&PRIYMD1="+strPri1+"&PRIYMD2="+strPri2+"&PRIYMD3="+strPri3;
	var strInDatasets  = "";
	var strOutDatasets = "ds_GovPriYmd=ds_GovPriYmd";
	var strArgument    = "searchGovPriYmd";
	
	gfn_SyncCall( 
		     strSvcID
		   , strPart
		   , strURL
		   , strInDatasets
		   , strOutDatasets
		   , strArgument
		   );
}

function D1302020010F_OnUnloadCompleted(obj)
{
	if(ds_GovReg.GetUpdate()){
		if(!confirm(gfn_GetMessage("M000016","%AAA%","변경된 내용이 있습니다.종료")))
		{
			return false;
		}
	}
}

function fn_D1_ExcelExport()
{
	var SearchCondition= "";

    SearchCondition = SearchCondition + "지사 :" + RPad(cob_Branch.Text," ",20);
    SearchCondition = SearchCondition + "시/구 :" + RPad(cob_ZipNo.Text," ",20);
    SearchCondition = SearchCondition + "팀 :" + RPad(cob_Team.Text," ",20);
    SearchCondition = SearchCondition + "구간 :" + RPad(cob_PBlockCd.Text," ",20);
    SearchCondition = SearchCondition + "년도 :" + RPad(sp_Year.Text," ",20);
	gfn_ExportExcel("자율검사계획 정압기조회 ", 5, SearchCondition, "grd_Excel"); 
}
function D1302020010F_OnLoadCompleted(obj)
{
	gfn_D1_SetGrid_Sort(grd_GovList);
}
