// 함수명: fn_D1_SearchPlan()
//   설명:  자율 검사 대상 공동주택을 조회한다.
// arguments
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_D1_SearchPlan()
{	
	var strSvcID 	   = "fn_D1_Find";
	var strPart        = "D10";
	var strURL 	       = "handle.do?ServiceName=D1_SelfTestRsltRegisCoLiveMgr-service&BRANCH_CD="+cob_Branch.Value+"&ZIP_NO1="+cob_ZipNo.Value+"&INSPECT_YEAR="+sp_Year.Value+"&FACI_NM="+edt_Faci_Nm.text;
	var strInDatasets  = "";
	var strOutDatasets = "ds_HouseList=ds_HouseList ds_HouseListExcel=ds_HouseListExcel";
	var strArgument    = "findHouse";
	
	gfn_SyncCall( 
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
			   );
			   
	if(ErrorCode < 0)
	{    
		return;
	}
	
	
	StrInspectYear 	  = "";
	strFaciNum 		  = "";
	strLotNumUnion 	  = "";
	strZipNo1 		  = "";
	strFaciNm 	      = "";
	strFaciRmk        = "";
	strInspectYmdPri1 = "";
	strRow = 0;
	
	ds_HouseResult.ClearData();
	
	if(ds_HouseList.GetRowCount()==0)
	{
        grd_HouseList.NoDataText="조회결과가 없습니다.";
        return;
    }
    
    grd_HouseList_OnCellClick("",0,"","","","");   
}

/*===============================================================================
 * 함수명: fn_D1_DeleteRow()
   설명: 정기검사 결과등록 레코드 한줄을 삭제한다.
   return 내용: 없음
===============================================================================*/ 
function fn_D1_DeleteRow()
{	
	if(ds_HouseResult.GetRowCount() == 0){
		alert("삭제할 대상이 없습니다. ");
		return;
	}

	if(!gfn_IsNull(ds_HouseResult.GetColumn(ds_HouseResult.row, "DECIDE_YMD"))){
		alert("결재처리된 데이터는 삭제가 불가능합니다. ");
		return;
	}
	
	var result = Confirm("데이터를 삭제하시겠습니까? ");
				
	if(result == '0'){
			return;
	}
	
	fn_D1_Delete();
	
	StrInspectYear 	  = ds_HouseList.GetColumn(ds_HouseList.row,"INSPECT_YEAR" );
	strFaciNum     	  = ds_HouseList.GetColumn(ds_HouseList.row,"FACI_NUM"     );
	strFaciNm 	   	  = ds_HouseList.GetColumn(ds_HouseList.row,"CO_LIVE_NM"   );
	strLotNumUnion 	  = ds_HouseList.GetColumn(ds_HouseList.row,"LOT_NUM_UNION");
	
	fn_D1_ResultSearch(StrInspectYear, strFaciNum, strFaciNm,strLotNumUnion);
}

function fn_D1_Delete(){

	ds_HouseResultD.ClearData();
	
	ds_HouseResultD.AddRow();
	
	ds_HouseResultD.CopyRow(0, "ds_HouseResult", ds_HouseResult.row, "");	
	ds_HouseResult.DeleteRow(ds_HouseResult.row);
			
	ds_HouseResultD.UpdateControl = false;
	ds_HouseResultD.SetRowType(0, "delete");
		
	var strSvcID        = "saveHouse";
	var strPart         = "D10";
	var strURL          = "handle.do?ServiceName=D1_SelfTestRsltRegisCoLiveMgr-service";
	var strInDatasets   = "ds_HouseResult=ds_HouseResultD:u";
	var strOutDatasets  = "";
	var strArgument     = "savaHouseRst";
	
	gfn_SyncCall( 
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
			   );
			   
	if(ErrorCode < 0)
	{    
		return;
	}
	
	alert(gfn_GetMessage("M000009","%AAA%","정상적으로 삭제를"),"black",true);
}

function fn_D1_Delete1(){

	ds_HouseResultD.ClearData();
	
	var nCnt = ds_HouseResult.GetRowCount();
	
	for(var i=0; i<nCnt; i++){
		if(ds_HouseResult.GetRowType(i) == 'insert'){
			var nRow = ds_HouseResultD.AddRow();
			ds_HouseResultD.CopyRow(nRow, "ds_HouseResult", , "");
			
			ds_HouseResultD.UpdateControl = false;
			ds_HouseResultD.SetRowType(0, "delete");
		}
	}	
		
	var strSvcID       = "saveHouse";
	var strPart 	   = "D10";
	var strURL 		   = "handle.do?ServiceName=D1_SelfTestRsltRegisCoLiveMgr-service";
	var strInDatasets  = "ds_HouseResult=ds_HouseResultD:u";
	var strOutDatasets = "";
	var strArgument    = "savaHouseRst";
	
	gfn_SyncCall( 
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
			   );
			   
	if(ErrorCode < 0)
	{    
		return;
	}
}

function grd_HouseList_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{	
	gfn_SetGrdComboBox_Comcode("grd_HouseRst",5,"D10176");
	grd_HouseRst.SetCellProp("body",5,"Edit","none");
	
	StrInspectYear 	  = ds_HouseList.GetColumn(nRow,"INSPECT_YEAR");
	strFaciNum     	  = ds_HouseList.GetColumn(nRow,"FACI_NUM");
	strFaciNm 	   	  = ds_HouseList.GetColumn(nRow,"CO_LIVE_NM");
	strFaciRmk        = '연장(' + ds_HouseList.GetColumn(nRow,"TOT_LENG") + ' m'
					+ '),동수('	+ ds_HouseList.GetColumn(nRow,"DONG_QTY") 
					+ '),세대수(' + ds_HouseList.GetColumn(nRow,"HOUSE_CNT") + ')';
	strLotNumUnion 	  = ds_HouseList.GetColumn(nRow,"LOT_NUM_UNION");
	strInspectYmdPri1 = ds_HouseList.GetColumn(nRow,"INSPECT_YMD_PRI1");
	strMaterFlag 	  = ds_HouseList.GetColumn(nRow,"MATER_FLAG");
	
	fn_D1_ResultSearch(StrInspectYear, strFaciNum, strFaciNm,strLotNumUnion);
	
	strRowType = "search";
}

//------------------------------------------------------------------------------- 
// 함수명: fn_D1_ResultSearch(StrInspectYear, strFaciNum, strZipNo1,strFaciNm),strLotNumUnion
//   설명: 자율검사 결과등록을 조회 하는 함수
// arguments   
//          StrInspectYear: 검사년도
//          strFaciNum: 공동주택번호
//          strFaciNm: 시설물명
//          strLotNumUnion: 주소
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_D1_ResultSearch(StrInspectYear, strFaciNum, strFaciNm,strLotNumUnion)
{	
	ds_HouseResult.ClearData();
	//-------------------------------------------------------------------------------
	//  Transition   : Service id
	//  ServicePart   : Service part
	//  Url   : Server url
	//  InputDsList   : Input Dataset List
	//  OutputDsList   : Output Dataset List;
	//  Argument   :  Argument
	//-------------------------------------------------------------------------------
    var strSvcID       = "fn_D1_ResultSearch";
    var strPart        = "D10";
    var strURL         = "handle.do?ServiceName=D1_SelfTestRsltRegisCoLiveMgr-service&FACI_NUM="+strFaciNum+"&INSPECT_YEAR="+StrInspectYear+"&FACI_NM="+strFaciNm+"&LOT_NUM_UNION="+strLotNumUnion;
    var strInDatasets  = "";
    var strOutDatasets = "ds_HouseResult=ds_HouseResult";
    var strArgument    = "findResult";   
    
    gfn_SyncCall( 
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
			   );
	
	if(ds_HouseResult.GetRowCount()==0)
	{
        grd_HouseRst.NoDataText="조회결과가 없습니다.";
        return;
    }
}

/*===============================================================================
 * 함수명: fn_D1_AddRow()
   설명: 자율검사 결과등록 레코드 한줄을 추가한다.
   return 내용: 없음
===============================================================================*/ 
function fn_D1_AddRow()
{
	if(strFaciNum != "")
	{
		gfn_SetGrdComboBox_Comcode("grd_HouseRst",5,"D10176");
		grd_HouseRst.SetCellProp("body",5,"Edit","none");
		ds_HouseResult.InsertRow(0);
		
		ds_HouseResult.SetColumn(0,"FACI_NUM"        ,strFaciNum    );
		ds_HouseResult.SetColumn(0,"LOT_NUM_UNION"   ,strLotNumUnion);
		ds_HouseResult.SetColumn(0,"FACI_NM"         ,strFaciNm     );
		ds_HouseResult.SetColumn(0,"INSPECT_YEAR"    ,StrInspectYear);
		ds_HouseResult.SetColumn(0,"SELF_INSPECT_REG","20"          );
		strRowType = "insert";
		
    }else{
		alert('자율검사대상을 선택해주세요');
    }
}

//------------------------------------------------------------------------------- 
// 함수명: fn_D1_SaveReg()
//   설명: 자율검사 결과등록하는 함수
// arguments   
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_D1_SaveReg()
{
	fn_D1_SaveHouseRst('btn');
	
	fn_D1_ResultSearch(StrInspectYear, strFaciNum, strFaciNm,strLotNumUnion);
}

//------------------------------------------------------------------------------- 
// 함수명: fn_D1_SaveHouseRst()
//   설명: 자율검사계획 결과등록 내용을 등록 및 수정한다.
// arguments   
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_D1_SaveHouseRst(flag)
{
	if(strFaciNum != "")
	{
		var temp_1;
		var temp_2;
			
		for(var i=0; i<ds_HouseResult.GetRowCount(); i++){
	
			temp_1 = trim(ds_HouseResult.GetColumn(i,"INSPECT_YMD"));	
		
			if(temp_1 != '' && temp_1 != null){
		
				for(var j=0; j<=i; j++){
									
					if(i != j){				
						
						temp_2 = trim(ds_HouseResult.GetColumn(j,"INSPECT_YMD"));
						
						if(temp_2 != '' && temp_2 != null){							
							if(temp_1 == temp_2){
								alert("검사일자가 동일한 경우에는 등록하실 수 없습니다. 검사일자가 중복되지 않도록 선택해 주십시오. ");
								return;
							}							
						}
					}
				}
			}
		}
	}

	var saveFlag = 0;
	
	if(strFaciNum != "")
	{
		for(var i=0; i<ds_HouseResult.RowCount(); i++)
		{
			if(ds_HouseResult.GetRowType(I) == "insert"){
				
				var str_FACI_NUM     = ds_HouseResult.GetColumn(i, "FACI_NUM"    );
				var str_INSPECT_YEAR = ds_HouseResult.GetColumn(i, "INSPECT_YEAR");
				var str_INSPECT_YMD  = ds_HouseResult.GetColumn(i, "INSPECT_YMD" );
				
				var strChkFlag = fn_D1_CheckReg(str_INSPECT_YEAR, str_INSPECT_YMD, str_FACI_NUM);
				
				if(!strChkFlag) 
				{
					var str_CRTDTM = ds_HouseRstFlag.GetColumn(0, "CRT_TIME" );
					var str_CRTNM  = ds_HouseRstFlag.GetColumn(0, "CRT_EMPNM");
				
					var str_INSPECT_YMD  = substr(ds_HouseResult.GetColumn(i,"INSPECT_YMD"), 0, 4);
						str_INSPECT_YMD	+= "년 ";
						str_INSPECT_YMD	+= substr(ds_HouseResult.GetColumn(i,"INSPECT_YMD"), 4, 2);
						str_INSPECT_YMD	+= "월 ";
						str_INSPECT_YMD	+= substr(ds_HouseResult.GetColumn(i,"INSPECT_YMD"), 6, 2);
						str_INSPECT_YMD	+= "일";
				
					alert("검사일자 " + str_INSPECT_YMD + "에 대한 결과 데이터는 \n" + str_CRTNM + "님께서 " + str_CRTDTM + "에 이미 등록하신 데이터입니다. \n데이터를 조회해 주십시오. ");
					return;
				}
				
				if(ds_HouseResult.GetColumn(i,"INSPECT_YMD") == "" || ds_HouseResult.GetColumn(i,"INSPECT_YMD") == null)
				{
					alert(gfn_GetMessage("M000011","%AAA%","검사일자를"));
					return;
				}	
				
				if(!fn_DateCheckTodayGrid(ds_HouseResult.GetColumn(i,"INSPECT_YMD"),"검사일자"))
				{
					return;
				}
								
				//수정 등록시 셋팅할 사용자 정보
				ds_HouseResult.SetColumn(i, "UPD_EMPID", gfn_GetGlobalValue("G_USER_NO"));
				ds_HouseResult.SetColumn(i, "UPD_IP", gfn_GetGlobalValue("G_IPADDRESS"));
				ds_HouseResult.SetColumn(i, "CRT_EMPID", gfn_GetGlobalValue("G_USER_NO"));
				ds_HouseResult.SetColumn(i, "CRT_IP", gfn_GetGlobalValue("G_IPADDRESS"));
				
				saveFlag = 1;
			}else if(ds_HouseResult.GetRowType(i) == "update")
			{
				//수정 등록시 셋팅할 사용자 정보
				ds_HouseResult.SetColumn(i, "UPD_EMPID", gfn_GetGlobalValue("G_USER_NO"));
				ds_HouseResult.SetColumn(i, "UPD_IP", gfn_GetGlobalValue("G_IPADDRESS"));
				
				saveFlag = 1;
			}
		}
	}else{
		return;
	}
	
	var strSvcID 		= "saveHouse";
	var strPart 		= "D10";
	var strURL 		    = "handle.do?ServiceName=D1_SelfTestRsltRegisCoLiveMgr-service";
	var strInDatasets   = "ds_HouseResult=ds_HouseResult:u";
	var strOutDatasets  = "";
	var strArgument     = "savaHouseRst";
	
	gfn_SyncCall( 
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
			   );
	
	if(ErrorCode < 0)
	{    
		return;
	}
	
	if(ds_HouseResult.RowCount() == 0)
	{
		return;
	}
	
	if(saveFlag == 0)
	{
		return;
	}
	
	if(flag == 'btn'){
		alert(gfn_GetMessage("M000009","%AAA%","정상적으로 저장을"),"black",true);
	}
}

//------------------------------------------------------------------------------- 
// 함수명: fn_D1_CheckReg()
//   설명: 자율검사 결과등록을 하기 전 기 등록 여부 확인
// arguments   
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_D1_CheckReg(str_INSPECT_YEAR, str_INSPECT_YMD, str_FACI_NUM)
{	
	//-------------------------------------------------------------------------------
	//  Transition   : Service id
	//  ServicePart   : Service part
	//  Url   : Server url
	//  InputDsList   : Input Dataset List
	//  OutputDsList   : Output Dataset List;
	//  Argument   :  Argument
	//-------------------------------------------------------------------------------
	ds_HouseRstFlag.ClearData();
	
    var strSvcID       = "fn_D1_CheckReg";
    var strPart        = "D10";
    var strURL         = "handle.do?ServiceName=D1_SelfTestRsltRegisCoLiveMgr-service"
					   + "&FACI_NUM="     + str_FACI_NUM
					   + "&INSPECT_YEAR=" + str_INSPECT_YEAR
					   + "&INSPECT_YMD="  + str_INSPECT_YMD
					   ;
    var strInDatasets  = "";
    var strOutDatasets = "ds_HouseRstFlag=ds_HouseRstFlag";
    var strArgument    = "findRstFlag";   
    gfn_SyncCall( 
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
			   );
			   
	if(ds_HouseRstFlag.RowCount() != 0) return false;
	return true;
}

