// 달력 팝업 및 팝업창 열기
//---------------------------------------------------------
function grd_HouseRst_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	var str_FACI_NUM     = ds_HouseResult.GetColumn(nRow, "FACI_NUM"    );
	var str_INSPECT_YEAR = ds_HouseResult.GetColumn(nRow, "INSPECT_YEAR");
	var str_INSPECT_YMD  = ds_HouseResult.GetColumn(nRow, "INSPECT_YMD" );
	
	var nRowtType = ds_HouseResult.GetRowType(nRow);
	
	if("INSPECT_YMD" == grd_HouseRst.GetCellProp("body",nCell,"ColId"))
	{
		if(ds_HouseResult.GetColumn(nRow, "SELF_INSPECT_REG") == 20 && ds_HouseResult.GetRowType(nRow) == 'insert'){
			gfn_SetCalendar(obj, nRow, nCell);
		}
				
	}else if("SELF_INSPECT_REG" == grd_HouseRst.GetCellProp("body",nCell,"ColId"))
	{
		if(ds_HouseResult.GetColumn(nRow,"INSPECT_YMD") == null)
		{
			alert('검사일자를 먼저 등록해 주세요');
			return;
		} else if(!fn_DateCheckTodayGrid(ds_HouseResult.GetColumn(nRow,"INSPECT_YMD"),"검사일자"))
		{
			return;
		} else if(nRowtType == "insert")
		{
			var temp_1;
			var temp_2;
				
			for(var i=0; i<ds_HouseResult.GetRowCount(); i++){
		
				temp_1 = trim(ds_HouseResult.GetColumn(i,"INSPECT_YMD"));	
			
				if(temp_1 != '' && temp_1 != null){
			
					for(var j=0; j<=i; j++){
										
						if(i != j){				
							
							temp_2 = trim(ds_HouseResult.GetColumn(j,"INSPECT_YMD"));
							
							if(temp_2 != '' && temp_2 != null){			
											
								if(temp_1 == temp_2){
									alert("검사일자가 동일한 경우에는 등록하실 수 없습니다. 검사일자가 중복되지 않도록 선택해 주십시오. ");
									return;
								}							
							}
						}
					}
				}
			}
				
			/*
			var nCnt = 0;
			var nRowCount = ds_HouseResult.GetRowCount();
			
			for(var i=0; i<nRowCount; i++){
				if(ds_HouseResult.GetRowType(i) == 'insert'){
					nCnt = toNumber(nCnt) + 1;
				}
			}
			
			if(nCnt > 0){
				alert("자율검사결과 정보를 먼저 등록하신 후 자율검사서를 작성하십시오. ");
				return;
			}		
				
			var strChkFlag = fn_D1_CheckReg(str_INSPECT_YEAR, str_INSPECT_YMD, str_FACI_NUM);
		
			if(!strChkFlag) 
			{
				var str_CRTDTM = ds_HouseRstFlag.GetColumn(0, "CRT_TIME" );
				var str_CRTNM  = ds_HouseRstFlag.GetColumn(0, "CRT_EMPNM");
			
				var str_INSPECT_YMD1  = substr(ds_HouseResult.GetColumn(i,"INSPECT_YMD"), 0, 4);
					str_INSPECT_YMD1 += "년 ";
					str_INSPECT_YMD1 += substr(ds_HouseResult.GetColumn(i,"INSPECT_YMD"), 4, 2);
					str_INSPECT_YMD1 += "월 ";
					str_INSPECT_YMD1 += substr(ds_HouseResult.GetColumn(i,"INSPECT_YMD"), 6, 2);
					str_INSPECT_YMD1 += "일";
			
					alert("검사일자 " + str_INSPECT_YMD1 + "에 대한 결과 데이터는 \n" + str_CRTNM + "님께서 " + str_CRTDTM + "에 이미 등록하신 데이터입니다. \n데이터를 조회해 주십시오. ");
					return;
			}	*/
		}
	
		fn_D1_SaveHouseRst('popup');
	
		fn_D1_CheckReg1(str_INSPECT_YEAR, str_INSPECT_YMD, str_FACI_NUM);

		var str_CrtTime = ds_HouseRstFlag1.GetColumn(0, "CRT_TIME" );
		var str_CrtNm   = ds_HouseRstFlag1.GetColumn(0, "CRT_EMPNM");
			
		var strRowType = '';	
			
		if(ds_HouseRstFlag1.GetRowCount() == 0){
			strRowType = "insert";
		}else{
			strRowType = "search";
		}
		
		var retValue = parent.fn_Maint_Repai_Reque2(
													 ds_HouseResult.GetColumn(nRow,"INSPECT_YMD")
													,strInspectYmdPri1
													,Replace(strFaciNm," ","&")
													,strFaciRmk
													,strFaciNum
													,StrInspectYear
													,strRowType
													,strMaterFlag
													,str_CrtTime
												    ,str_CrtNm
													,ds_HouseResult.GetColumn(nRow,"DECIDE_YMD")
													,ds_HouseResult.GetColumn(nRow,"DECIDE_PROC_STS")
												   );
		
		if(retValue == null || retValue == 0)
		{
			return;
		}
		
		if(retValue[2] == "savePass")
		{
			ds_HouseResult.SetColumn(nRow,"SELF_INSPECT_REG","10"	    );
			ds_HouseResult.SetColumn(nRow,"INSPECT_RSLT_CD" ,"10"	    );
			ds_HouseResult.SetColumn(nRow,"INSPECT_NM_1"    ,retValue[0]);
			ds_HouseResult.SetColumn(nRow,"INSPECT_NM_2"    ,retValue[1]);
			ds_HouseResult.SetColumn(nRow,"NOT_PASS_WHY"    ,""		    );
			
		}else if(retValue[2] == "saveNotPass")
		{
			ds_HouseResult.SetColumn(nRow,"SELF_INSPECT_REG","10"       );
			ds_HouseResult.SetColumn(nRow,"INSPECT_RSLT_CD" ,"20"       );
			ds_HouseResult.SetColumn(nRow,"INSPECT_NM_1"    ,retValue[0]);
			ds_HouseResult.SetColumn(nRow,"INSPECT_NM_2"    ,retValue[1]);
		}else{
			ds_HouseResult.SetColumn(nRow,"SELF_INSPECT_REG","20");
			ds_HouseResult.SetColumn(nRow,"INSPECT_RSLT_CD" ,""  );
		}
	}
}

