function fn_D1_ExcelExport()
{
	var SearchCondition= "";

    SearchCondition = SearchCondition + "지사 :" + RPad(cob_Branch.Text," ",20);
    SearchCondition = SearchCondition + "시/구 :" + RPad(cob_ZipNo.Text," ",20);
    SearchCondition = SearchCondition + "년도 :" + RPad(sp_Year.Text," ",20);
	gfn_ExportExcel("자율검사결과조회 공동주택 ", 5, SearchCondition, "grd_Excel"); 
}
function D1302060010F_OnLoadCompleted(obj)
{
	gfn_D1_SetGrid_Sort(grd_HouseList);
	gfn_D1_SetGrid_Sort(grd_HouseRst);
	
	// 포틀릿
	if (IsExistVar("PORTLET_FLAG", true)) {
		if (PORTLET_FLAG == "p") {
			fn_D1_SearchPlanPortlet(INSPECT_YEAR, BRANCH_CD, ZIP_NO1);
		}
	}
}

//------------------------------------------------------------------------------- 
// 함수명: fn_D1_SearchPlanPortlet()
//   설명:  포틀릿을 자율 검사 대상 공동주택을 조회한다.
// arguments
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_D1_SearchPlanPortlet(strYear, strBranchCd, strZipNo)
{	
	var strFaciNum = "";
	
	if(IsExistVar("FACI_NUM",true)){
		strFaciNum = FACI_NUM;
	}
	var strSvcID = "fn_D1_FindPortlet";
	var strPart = "D10";
	var strURL = "handle.do?ServiceName=D1_SelfTestRsltRegisCoLiveMgr-service&BRANCH_CD="+strBranchCd+"&ZIP_NO1="+strZipNo+"&INSPECT_YEAR="+strYear+"&FACI_NUM="+strFaciNum;
	var strInDatasets  = "";
	var strOutDatasets  = "ds_HouseList=ds_HouseList ds_HouseListExcel=ds_HouseListExcel";
	var strArgument  = "findHouse";
	
	gfn_SyncCall( 
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
			   );
			   
	if(ErrorCode < 0)
	{    
		return;
	}
	
	
	StrInspectYear = "";
	strFaciNum = "";
	strLotNumUnion = "";
	strZipNo1 = "";
	strFaciNm = "";
	strFaciRmk = "";
	strInspectYmdPri1 = "";
	strRow = 0;
	
	ds_HouseResult.ClearData();
	
	if(ds_HouseList.GetRowCount()==0)
	{
        grd_HouseList.NoDataText="조회결과가 없습니다.";
        return;
    }
    grd_HouseList_OnCellClick("",0,"","","","");
}
