var strGovNum 		  = "";
var strFaciNum 		  = "";
var strInspectYear 	  = "";
var strRowType        = "";
var strDefaultRowType = "";

function D1302050010U_OnLoadCompleted(obj)
{
	gfn_LoadForm(obj);
	fn_Init();
}

//---------------------------------------------------------
//	초기 설정
//---------------------------------------------------------
function fn_Init()
{
	cal_DecideYmd.Text = DECIDE_YMD;
	edt_Decide_proc_sts.Text = DECIDE_PROC_STS;
	
	// 넘어온 파라메터가 있으면
	if(IsExistVar("INSPECT_YMD"))
	{
		cal_InspectYmd.Text = INSPECT_YMD;
	}
	if(IsExistVar("LOT_NUM_UNION"))
	{
		edt_LotNumUnion.Text = LOT_NUM_UNION;
		edt_LotNumUnion.Text = Replace(edt_LotNumUnion.Text,"&"," ");
	}
	if(IsExistVar("INSPECT_YMD_PRI1"))
	{
		cal_InspectYmdPri1.Text = INSPECT_YMD_PRI1;
	}
	if(IsExistVar("INST_YMD"))
	{
		cal_InstYmd.Text = INST_YMD;
	}
	if(IsExistVar("GOV_ROOM_NM"))
	{
		edt_GovRoomNm.Text = GOV_ROOM_NM;
	}
	if(IsExistVar("GOV_MODEL_NM"))
	{
		edt_GovModelNm.Text = GOV_MODEL_NM;
	}
	if(IsExistVar("FACI_NUM"))
	{
		strFaciNum = FACI_NUM;
	}
	if(IsExistVar("GOV_NUM"))
	{
		strGovNum = GOV_NUM;
	}
	if(IsExistVar("INSPECT_YEAR"))
	{
		strInspectYear = INSPECT_YEAR;
	}
	
	if(IsExistVar("CRT_EMPID"))
	{
		st_CRTNM = CRT_EMPID;
	}
	
	if(IsExistVar("CRT_DTM"))
	{
		st_CRTTIME = CRT_DTM;
	}
	
	if(IsExistVar("ROWTYPE"))
	{
		strRowType = ROWTYPE;
		
		if(strRowType == "search" || strRowType == "update")
		{
			fn_D1_FindGov();
		}
		if(strRowType == "search" )
		{
			grd_FreeGov.Editable = "false";
			edt_InspectNm1.Enable = "false";
			edt_InspectNm2.Enable = "false";
		}
	}
	
	grd_FreeGov.BindDataset = "ds_FreeGov";
	fn_D1_SignSearch();
}

function fn_CommonSearch(){
	fn_D1_FindGov();
	fn_D1_SignSearch();
}

//------------------------------------------------------------------------------- 
// 함수명: fn_D1_FindGov()
//   설명:  자율 검사 대상 정압기장표를 조회한다.
// arguments
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_D1_FindGov()
{	
	var strSvcID = "fn_D1_Find";
	var strPart = "D10";
	var strURL = "handle.do?ServiceName=D1_SelfTestRsltRegisGoveListMgr-service&INSPECT_YEAR="+strInspectYear+"&GOV_NUM="+strGovNum+"&FACI_NUM="+strFaciNum+"&INSPECT_YMD="+cal_InspectYmd.Value;
	var strInDatasets  = "";
	var strOutDatasets  = "ds_FreeGovList=ds_FreeGovList";
	var strArgument  = "findGov";
	
	gfn_SyncCall( 
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
			   );	
			   
	fn_D1_FreeGovInit();
}

function fn_CommonSelect(){
		
	fn_D1_FindGov();
	
	var strType = "";
	var strNotPass = "";
	
	if(strRowType == "update")
	{
		strType = "update";
		
	}else if(strRowType == "insert")
	{
		strType = "insert";
	
	}else if(strRowType == "search")
	{
		strType = "search";
	}

	for(var i=0; i<ds_FreeGov.RowCount(); i++)
	{
		var strRstCd = 0;
		
		if(ds_FreeGov.GetColumn(i,"CHK_PASS") == 1)
		{
			strRstCd   = 1;
		}else if(ds_FreeGov.GetColumn(i,"CHK_NOT_PASS") == 1)
		{
			strRstCd   = 2;
			strNotPass = "Y";
		}else if(ds_FreeGov.GetColumn(i,"CHK_NONE") == 1)
		{
			strRstCd = 3;
		}		
	}
	
	var nCount = 0;
	
	for(var i=0; i<ds_FreeGov.RowCount(); i++)
	{	
		if(ds_FreeGov.GetColumn(i, "GOV_INSPECT_ITEM") != '20'){
			if(ds_FreeGov.GetColumn(i,"CHK_NOT_PASS") == 1)
			{
				nCount = toNumber(nCount) + 1;
			}
		}
	}
	
	var arrList = array();
	
	arrList[0] = edt_InspectNm1.Text;
	arrList[1] = edt_InspectNm2.Text;
	
	var str_INSPECT_YEAR = strInspectYear ;
	var str_INSPECT_YMD  = cal_InspectYmd.Value ;
	var str_FACI_NUM     = strFaciNum ;
	var str_GOV_NUM		 = strGovNum ;

	var strChkFlag = fn_D1_CheckReg1(str_INSPECT_YEAR, str_INSPECT_YMD, str_FACI_NUM, str_GOV_NUM);
	
	if(!strChkFlag) 
	{
		if(nCount > 0){
			arrList[2] = "saveNotPass";
			Close(arrList);
		}else{			
			
			arrList[2] = "savePass";
			Close(arrList);
		}
	}else{
		arrList[2] = "none";
		Close(arrList);
	}
}

// ------------------------------------------------------------------------------- 
// 함수명: fn_D1_FreeGovInit()
  // 설명:  자율 검사 대상 정압기장표 해당 내용을 셋팅한다.
// arguments
  // return 내용: 없음
// ------------------------------------------------------------------------------- 
function fn_D1_FreeGovInit()
{	
	for(var i=0;i<ds_FreeGovList.RowCount();i++)
	{
		if(ds_FreeGovList.GetColumn(i,"INSPECT_RSLT_CD") == "1")
		{
			ds_FreeGov.SetColumn(i,"CHK_PASS",1);
		}else if(ds_FreeGovList.GetColumn(i,"INSPECT_RSLT_CD") == "2")
		{
			ds_FreeGov.SetColumn(i,"CHK_NOT_PASS",1);
		}else if(ds_FreeGovList.GetColumn(i,"INSPECT_RSLT_CD") == "3")
		{
			ds_FreeGov.SetColumn(i,"CHK_NONE",1);
		}
		ds_FreeGov.SetColumn(i,"RMK1",ds_FreeGovList.GetColumn(i,"RMK1"));
		edt_InspectNm1.Text = ds_FreeGovList.GetColumn(i,"INSPECT_NM_1");
		edt_InspectNm2.Text = ds_FreeGovList.GetColumn(i,"INSPECT_NM_2");
		ta_Rmk2.Text = ds_FreeGovList.GetColumn(i,"RMK2");
	}
}
//------------------------------------------------------------------------------- 
// 함수명: btn_GovProc_OnClick()
//   설명: 정압기장표에 관한 내용을 저장
// arguments   
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function btn_GovProc_OnClick()
{
	if(edt_Decide_proc_sts.text == '승인' || edt_Decide_proc_sts.text == '결재중')
	{
		alert("현재 " + edt_Decide_proc_sts.text + " 상태입니다.");
		return;
	}
	
	var InspectYmd = cal_InspectYmd.Value;
	var InspectFlag = '20';
	var InspectYear = strInspectYear;
	var Faci_num = strFaciNum;
	var Gov_num = strGovNum;

	//그룹웨어에서 차량번호(한글)파라메터 전달 불가로 로그인자 사번넘겨 차량번호 참조 오즈로 넘겨줌 
	gfv_callGWInfo("fmD11","D10","D1302050010P",5,InspectYear+","+InspectFlag+","+Gov_num+","+Faci_num+","+InspectYmd);
   // 1번 : 화면명칭   2번 : 업무그룹 (b10,b20... OZ 인경우 필수), 3번: 프로그램ID (oz인경우 필수) 4: 아규먼트 갯수 (1....5)
   // 5번 : 아규먼트 목록 (형식 , 표시로 각 아규먼트 구분자 사용)		
}

