//---------------------------------------------------------
// 검사 결과 에 따른 체크값 설정
//---------------------------------------------------------
 function ds_FreeGov_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
 {
	if(varOldValue != varNewValue)
	{	
		ds_FreeGov.FireEvent = false;
		//확인 필요 권기철	2010.01.11
		//=================
		if(strColumnID == "CHK_PASS"){
			 if( ds_FreeGov.GetColumn(nRow, "CHK_PASS") == '1'){
				 ds_FreeGov.SetColumn(nRow,"CHK_NOT_PASS", 0);
				 ds_FreeGov.SetColumn(nRow,"CHK_NONE"    , 0);
			 }
		}
		
		if(strColumnID == "CHK_NOT_PASS"){
			//alert(nrow);
			if( ds_FreeGov.GetColumn(nRow, "CHK_NOT_PASS") == '1'){
				ds_FreeGov.SetColumn(nRow,"CHK_PASS"    , 0);
				ds_FreeGov.SetColumn(nRow,"CHK_NONE"    , 0);
			}
		}
		
		if(strColumnID == "CHK_NONE"){
			if( ds_FreeGov.GetColumn(nRow, "CHK_NONE") == '1'){
				ds_FreeGov.SetColumn(nRow,"CHK_PASS"    , 0);
				ds_FreeGov.SetColumn(nRow,"CHK_NOT_PASS", 0);
			}
		}
		ds_FreeGov.FireEvent = true;
	}	
	
 }

/*===============================================================================
 * 함수명: fn_D11_SignSave()
   설명:  배관장표에 관한 서명을 조회
   return 내용: 없음
===============================================================================*/ 
function fn_D1_SignSearch()
{
	var strSvcID = "";
	var strPart  = "D10";	
	var strURL   = "handle.do?ServiceName=D1_SelfTestRsltRegisGoveListMgr-service"
	             + "&INSPECT_YEAR="       +strInspectYear 
	             + "&GOV_NUM="            +strGovNum 
	             + "&FACI_NUM="           +strFaciNum
	             + "&INSPECT_YMD="        +cal_InspectYmd.Value
	             ;
	           
	var strInDatasets   = "";
	var strOutDatasets  = "ds_SignView=ds_SignView";
	var strArgument     = "SignSearch";
	
	gfn_SyncCall( 
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
			   );	   		   
	
	if(ds_SignView.GetRowCount() < 1){
		ds_SignView.AddRow();
	}
	
	if(ds_SignView.GetColumn(0, "SIGN_1") == null || ds_SignView.GetColumn(0, "SIGN_1") == ''){
		 SignImg.Visible = false;
		 sign.Visible    = true;
		 sign.Enable     = true;
	}else{
		 SignImg.Visible = true;
		 sign.Visible    = false;
	}
			   
	 if(ds_SignView.GetColumn(0, "SIGN_2") == null || ds_SignView.GetColumn(0, "SIGN_2") == ''){
		 SignImg1.Visible = false;
		 sign1.Visible    = true;
		 sign1.Enable     = true;
	 }else{
		 SignImg1.Visible = true;
		 sign1.Visible    = false;
	 }
}

function fn_D1_SignSave()
{		
		ds_Sign.UpdateControl = false;
		ds_Sign.SetRowType(0, "update");
		
		sign.SaveFile("JPG", "C:\\test.jpg");
		File0.FileName = "C:\\test.jpg";
		
		if (File0.open('rb'))
		{
			var sbuff;
			sbuff = File0.ReadBinary();
			File0.Close();
			ds_Sign.SetColumn(0, "SIGN", sbuff);
		}
				
		ds_Sign.SetColumn(0, "INSPECT_YEAR"    , ds_FreeGov.GetColumn(0, "INSPECT_YEAR"    ));
