		var strSvcID       = "";
		var strPart        = "D10";
		var strURL         = "handle.do?ServiceName=D1_SelfTestRsltRegisGoveListMgr-service&sign=1";
		var strInDatasets  = "ds_Sign=ds_Sign:u";
		var strOutDatasets = "";
		var strArgument    = "";
		
		gfn_SyncCall(
						 strSvcID
					   , strPart
					   , strURL
					   , strInDatasets
					   , strOutDatasets
					   , strArgument
					  );
					  
		if ( ErrorCode > 0 ){return false ;}
		
		ds_Sign.UpdateControl = false;
		ds_Sign.SetRowType(0, "update");
		
		sign1.SaveFile("JPG", "C:\\test.jpg");
		File0.FileName = "C:\\test.jpg";
		
		if (File0.open('rb'))
		{
			var sbuff;
			sbuff = File0.ReadBinary();
			File0.Close();
			ds_Sign.SetColumn(0, "SIGN", sbuff);
		}
		
		var strSvcID       = "";
		var strPart        = "D10";
		var strURL         = "handle.do?ServiceName=D1_SelfTestRsltRegisGoveListMgr-service&sign1=1";
		var strInDatasets  = "ds_Sign=ds_Sign:u";
		var strOutDatasets = "";
		var strArgument    = "";
		
		gfn_SyncCall(
						 strSvcID
					   , strPart
					   , strURL
					   , strInDatasets
					   , strOutDatasets
					   , strArgument
					  );
					  
		if ( ErrorCode > 0 )
		{ 
			return false ;
		}
}

function btn_Sign_OnClick(obj)
{
	// if(ds_FreeGovList.GetRowCount() > 1){
		// return false;
	// }
	if(edt_Decide_proc_sts.text == '승인' || edt_Decide_proc_sts.text == '결재중')
	{
		alert("현재 " + edt_Decide_proc_sts.text + " 상태입니다.");
		return;
	}
		
	ds_Sign.ClearData();
	ds_Sign.AddRow();
	ds_SignView.ClearData();
	ds_SignView.AddRow();
	
	SignImg.Visible = false;
	sign.Visible    = true;
	sign.Enable     = true;
	
	SignImg1.Visible = false;
	sign1.Visible    = true;
	sign1.Enable     = true;		
}

//------------------------------------------------------------------------------- 
//  함수명: fn_D1_CheckReg1()
//  설명: 자율검사 결과등록을 하기 전 장표 등록 여부 확인
//  arguments   
//  return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_D1_CheckReg1(str_INSPECT_YEAR, str_INSPECT_YMD, str_FACI_NUM, str_GOV_NUM)
{	
	//-------------------------------------------------------------------------------
	//  Transition   : Service id
	//  ServicePart   : Service part
	//  Url   : Server url
	//  InputDsList   : Input Dataset List
	//  OutputDsList   : Output Dataset List;
	//  Argument   :  Argument
	//-------------------------------------------------------------------------------
    var strSvcID = "fn_D1_CheckReg1";
    var strPart  = "D10";
    var strURL   = "handle.do?ServiceName=D1_SelfTestRsltRegisGoveMgr-service";
		strURL	+= "&FACI_NUM="    + str_FACI_NUM    ;
		strURL	+= "&GOV_NUM="     + str_GOV_NUM	 ;
		strURL	+= "&INSPECT_YEAR="+ str_INSPECT_YEAR;
		strURL	+= "&INSPECT_YMD=" + str_INSPECT_YMD ;
					   
    var strInDatasets  = "";
    var strOutDatasets = "ds_GovRstFlag1=ds_GovRstFlag1";
    var strArgument    = "findRstFlag1";     
    
    gfn_SyncCall( 
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
			   );
	if(ds_GovRstFlag1.RowCount() != 0) return false;
	return true;
}
