//--------------------------------------------------------------------------------------------------
//	OnLoadCompleted
//--------------------------------------------------------------------------------------------------
function D1401010010F_OnLoadCompleted(obj)
{
	gfn_LoadForm(obj);
    fn_Init();
    gfn_D1_SetGrid_Sort(grd_list);
    
   	if (IsExistVar("PORTLET_FLAG", true)) 
   	{
	   
	    if (PORTLET_FLAG == "p") 
	    {
            cob_BranchCd.Value = BRANCH_CD; // 지사 
            cob_TeamCd.Value =TEAM_CD ;  // 팀 
            cob_DigKind .Value =DIG_KIND ;  //공사종류
            cob_JobCd.Value = JOB_CD;  //작업종류 
            cob_DigWorkPathFlag.Value = DIG_WORK_PATH_FLAG;  // 굴착구분 
            cob_ReciiveYmd1.Value = RECEIVE_YMD;      //접수일자 
            cob_ReciiveYmd2.Value = RECEIVE_YMD;
            
            peocs_num = EOCS_NUM;
            pdig_work_path_flag =DIG_WORK_PATH_FLAG; 
			
			
			// fn_CommonSearch();
			// fn_DetailSearchPort();
	    }
	}
	else
	{
	
		// SCG이면
		//-----------------------------------------
		//  지사,팀 콤보박스의 초기 설정
		//-----------------------------------------	
		if (gfn_GetGlobalValue("G_BRANCH_CD") == "999999") 
		{
		
			cob_BranchCd.Value = "100584"; //서부지사
			cob_BranchCd_OnChanged();
			cob_BranchCd.Enable = true;
			
		} 
		else 
		{
		
		
			//  콤보박스의 내용을 셋팅한다.
			fn_d1_Setting();
			
			if(ds_set.GetRowCount() > 0)
			{
				cob_BranchCd.Value = ds_set.GetColumn(0,"BRANCH_CD"); // 로그인자 지사 세팅
				cob_TeamCd.Value   = ds_set.GetColumn(0,"TEAM_CD");   // 로그인자 팀
				cob_StrZipNo1.Value= ds_set.GetColumn(0,"ZIP_NO1");   // 로그인자 시구 
			}
			
			cob_BranchCd.Value = gfn_GetGlobalValue("G_BRANCH_CD");
			cob_BranchCd_OnChanged();
			cob_BranchCd.Enable = false;
			cob_TeamCd.Value = gfn_GetGlobalValue("G_DEPT_CODE");
			cob_TeamCd_OnChanged();
		}
	}
	
	
	cob_ReciiveYmd1.Value = substr(today(),0,6)+"01";		// 작업의뢰일자 default 이번달 1일부터 오늘까지 
	//cob_ReciiveYmd2.Value = today();
	// 해당날의 마지막날 구하기
	cob_ReciiveYmd2.Value = adddate(addMonth(substr(today(),0,6)+"01", 1),-1);
		
}

//---------------------------------------------------------
//	초기 설정
//---------------------------------------------------------

function fn_Init()
{

    grd_list.NoDataText = gfn_GetMessage("M000022","%AAA%","조회결과가");
        
	//gfn_D1_All_SetComboBox_Comcode(strCodeGrp,objComboDs,val)  항목에 "전체" 추가 
	gfn_SetComboBox_Comcode("D10162",gfn_setDsObject_Comcode("cob_LkalarmFlag")); //누출 경보기 설치 여부 
	gfn_D1_All_SetComboBox_Comcode("D10166",gfn_setDsObject_Comcode("cob_JobCd"),""); //작업종류 
	gfn_D1_All_SetComboBox_Comcode("D10165",gfn_setDsObject_Comcode("cob_DigKind"),""); // 굴착종류 
	//gfn_D1_All_SetComboBox_Comcode("D10187",gfn_setDsObject_Comcode("cob_DigWorkProcSts"),""); // 굴착 진행상태  
	gfn_D1_All_SetComboBox_Comcode("D10201",gfn_setDsObject_Comcode("cob_DigWorkPathFlag"),"");  // 
	gfn_D1_All_SetComboBox_Comcode("D10201",gfn_setDsObject_Comcode("cob_DigWorkPathFlag2"),"");
	gfn_SetComboBox_Comcode("D10166",gfn_setDsObject_Comcode("cob_JobCd2"));
	gfn_SetComboBox_Comcode("D10165",gfn_setDsObject_Comcode("cob_DigKind2"));
	gfn_SetComboBox_Comcode("D10187",gfn_setDsObject_Comcode("cob_DigWorkProcSts2"));
	gfn_SetComboBox_Comcode("A10190",gfn_setDsObject_Comcode("edt_SafeValuaObjYn"));
	gfn_SetComboBox_Comcode("B30007",gfn_setDsObject_Comcode("cob_PipeDiam"));
	gfn_SetComboBox_Comcode("D10034",gfn_setDsObject_Comcode("cob_PipePress")); 
	gfn_SetComboBox_Comcode("A10110",gfn_setDsObject_Comcode("cob_DigTakerTelDdd"));
	gfn_SetComboBox_Comcode("A10110",gfn_setDsObject_Comcode("cob_OrderFirmTelDdd"));
	gfn_SetComboBox_Comcode("A10110",gfn_setDsObject_Comcode("cob_LargeTaker1TelDdd"));
	gfn_SetComboBox_Comcode("A10110",gfn_setDsObject_Comcode("cob_LargeTaker2TelDdd"));
    
    cob_DigKind.Value = "";         // 굴착종류
    cob_JobCd.Value   = "";         // 작업종류
    cob_DigWorkPathFlag.Value = ""; // 굴착구분
    
	cob_WorkSts.Value= "99";
	cob_DigWorkProcSts.Value = "";
	cob_Status.Value = "10";        // 진행상태 (접수)
    
	gfn_D1_SetComboBox_Branchcd(gfn_D1_setDsObject_Branchcd("cob_BranchCd"));
	gfn_D1_SetComboBox_Branchcd(gfn_D1_setDsObject_Branchcd("cob_BranchCd2"));
	
	//fn_SetComboBox_Comcode("1", cob_BranchCd.Value, fn_setDsObject_Comcode("1", "cob_StrZipNo1")); // 시/구
	//var tempbr=cob_BranchCd.Value;
	//fn_d1_team(tempbr);
	//teamcd = cob_TeamCd.Value;
	//fn_d1_block(branchcd,teamcd);
	
	cob_BranchCd2.Enable=false;
	//cob_TeamCd_OnChanged();
	
	//fn_CommonSearch();

}

//-------------------------------------------------------------------------------
//   함수명: fn_CommonExcelExport()
//   설명  :  엑셀다운 
//   arguments   
//   obj
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_CommonExcelExport()
{
	var SearchCondition= "";
    
    SearchCondition = SearchCondition + "지사 :" + RPad(cob_BranchCd.Text," ",20);
    SearchCondition = SearchCondition + "팀 :" + RPad(cob_TeamCd.Text," ",20);
    
    //SearchCondition = SearchCondition + "구간 :" + RPad(cob_BlockCd2.Text," ",20);
    SearchCondition = SearchCondition + "구간 :" + RPad(cob_BlockCd.Text," ",20);
    
    SearchCondition = SearchCondition + "굴착종류 :" + RPad(cob_DigKind.Text," ",20);
    SearchCondition = SearchCondition + "작업종류 :" + RPad(cob_JobCd.Text," ",20);
    SearchCondition = SearchCondition + "굴착구분 :" + RPad(cob_DigWorkPathFlag.Text," ",20);
    SearchCondition = SearchCondition + "진행상태 :" + RPad(cob_Status.Text," ",20);
    SearchCondition = SearchCondition + "접수일자 :" + RPad(cob_ReciiveYmd1.Text +" ~ "+ cob_ReciiveYmd2.Text," ",20);
    gfn_ExportExcel("굴착공사내역", 5, SearchCondition, "grd_excel"); 
	
}
//-------------------------------------------------------------------------------
//   함수명: fn_CommonSave()
//   설명  :  저장 버튼 클릭시 
//   arguments   
//   obj
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_CommonSave()
{   
    
    if(!ds_Multi.GetUpdate())
    {
		alert("변동사항이 없습니다.");
		return;
    }
    
    if(gubun=="Y")
    {  
		fn_D10_pkUpDate();
    }
    else
    {
		if(ds_multi.GetRowType(0)=="insert")
		{

			//max 값 불러오기
			fn_D10_Eocs_Chk();
			strMax = ds_chk.GetColumn(0,"EOCS_VIRTUAL_NUM");
			ds_Multi.SetColumn(0, "EOCS_NUM", strMax);

		}
		
		if(cob_DigKind2.Value != '10')
		{

			fn_D10_dt_save();  // 공사가 대형이 아닐때 insert 함수 호출 
			
	 
		}else
		{
		
			fn_D10_dt_saveLarge();   // 공사가 대형일때 insert 함수 호출 
		}
		
		
    }
    gubun="N";
    save_flag="";
}

//---------------------------------------------------------
//	공통 버튼 클릭 - 행추가
//---------------------------------------------------------
function fn_CommonAddRow()
{
	ds_Multi.ClearData();
	ds_Multi.AddRow();  
	cob_DigWorkPathFlag2.Enable=false;
	cob_DigWorkPathFlag2.Value= "2";
	cob_DigWorkProcSts2.Value = "90";
	save_flag="insert";
}
//-------------------------------------------------------------------------------
//	공통 버튼 클릭 - 삭제
//-------------------------------------------------------------------------------
function fn_CommonDeleteRow()
{
	if(ds_Search.RowCount() == 0)
	{
		alert("삭제할 데이터가 존재하지 않습니다.");
		return;
	}
	else
	{
	
		var strEOCS_NUM = ds_Search.GetColumn(ds_Search.row,"EOCS_NUM");
		var strDIG_WORK_PATH_FLAG = ds_Search.GetColumn(ds_Search.row,"DIG_WORK_PATH_FLAG");

	
		if(confirm("["+strEOCS_NUM+"]를 삭제하시겠습니까? "))
		{	
	
			ds_DeteleFlag.ClearData();
			var nAddRow = ds_DeteleFlag.AddRow();
			
			ds_DeteleFlag.UpdateControl = false;
			ds_DeteleFlag.SetRowType(nAddRow, "Update");
			ds_DeteleFlag.SetColumn(nAddRow,"EOCS_NUM",strEOCS_NUM);
			ds_DeteleFlag.SetColumn(nAddRow,"DIG_WORK_PATH_FLAG",strDIG_WORK_PATH_FLAG);
			ds_DeteleFlag.SetColumn(nAddRow,"UPD_IP",gfn_GetGlobalValue("G_IPADDRESS")); 
			ds_DeteleFlag.SetColumn(nAddRow,"UPD_EMPID",gfn_GetGlobalValue("G_USER_NO"));
			ds_DeteleFlag.SetColumn(nAddRow,"CRT_IP",gfn_GetGlobalValue("G_IPADDRESS")); 
			ds_DeteleFlag.SetColumn(nAddRow,"CRT_EMPID",gfn_GetGlobalValue("G_USER_NO"));
				
			ds_Search.DeleteRow(ds_Search.row);
			
			var strSvcID = "deleteflag";
			var strPart = "D10";
			var strURL  = "handle.do?ServiceName=D1_DiggingWorkMgr-service&deleteflag=1";
			var strInDatasets   = "ds_DeteleFlag=ds_DeteleFlag:u";
			var strOutDatasets  = "";
			var strArgument     = "";
			
			gfn_SyncCall( 
							 strSvcID
						   , strPart
						   , strURL
						   , strInDatasets
						   , strOutDatasets
						   , strArgument
						  
					   );
					   
				
			if(ErrorCode < 0)
			{    
				return;
			}
			else
			{
				alert(gfn_GetMessage("M000009","%AAA%","정상적으로 삭제를"));
				fn_CommonSearch();
			}
		}
		
	}
}

//-------------------------------------------------------------------------------
//   함수명: fn_CommonSearch()
//   설명  :  조회 클릭시 굴착공사 조회 
//   arguments   
//   obj
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_CommonSearch()
{
	
	//2013.06.28 굴착공사 조회시 달력체크 처리 변경 요청(서부지사 이인현과장)  by Moo-Jin Paeng.
	if(cob_ReciiveYmd1.Enable = true){
	
		if(!gfn_C2_ValidationCalendar(cob_ReciiveYmd1,cob_ReciiveYmd2)) return false; //날자 from  ~  to 체크
	
	}
	
	save_flag="";
	
    ds_SearchTop.SetColumn(0,"BRANCH_CD"          , cob_BranchCd.Value );  // 지사
    ds_SearchTop.SetColumn(0,"AREA"               , cob_StrZipNo1.Value );  // 시군구
    ds_SearchTop.SetColumn(0,"TEAM_CD"            , cob_TeamCd.Value   );  // 팀
    ds_SearchTop.SetColumn(0,"BLOCK_CD"           , cob_BlockCd.Value  );  // 구간
    ds_SearchTop.SetColumn(0,"DIG_KIND"           , cob_DigKind.Value  );  // 굴착종류
	ds_SearchTop.SetColumn(0,"JOB_CD"             , cob_JobCd.Value    );  // 작업종류
	ds_SearchTop.SetColumn(0,"DIG_WORK_PATH_FLAG" , cob_DigWorkPathFlag.Value);  // 굴착구분
    ds_SearchTop.SetColumn(0,"RECEIVE_YMD1"       , cob_ReciiveYmd1.Value );  // 접수일자
    ds_SearchTop.SetColumn(0,"RECEIVE_YMD2"       , cob_ReciiveYmd2.Value );  // 접수일자
	ds_SearchTop.SetColumn(0,"WORK_CHARGE_NM"     , edt_work_charge_nm.text);  // 담당자
	ds_SearchTop.SetColumn(0,"EOCS_NUM"       , edt_eocs_num.text);  // EOCS번호
	
	
    ds_Search.ClearData();
    ds_Multi.ClearData();
    
    var str_STATUS = cob_Status.Value; // 진행상태
    
	var strSvcID = "searchList";
	var strPart  = "D10";
	var strURL  = "handle.do?ServiceName=D1_DiggingWorkMgr-service";
	var strInDatasets   = "ds_SearchTop=ds_SearchTop";
	var strOutDatasets  = "ds_Search=ds_Search ds_excel=ds_excel";
	var strArgument     = "searchList_"+str_STATUS+"=1";
	
	gfn_SyncCall( 
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
				  
			   );
			   
		
	if(ErrorCode < 0)
	{    
		return;
	}
	else
	{
		edt_cnt.Text = ds_Search.GetRowCount();
	}


}

//-------------------------------------------------------------------------------
//   함수명: fn_DetailSearchPort()
//   설명  :  포틀릿  굴착공사 조회 
//   arguments :  
//   obj
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_DetailSearchPort()
{

	ds_Multi.ClearData();
    var strSvcID = "";
	var strPart = "D10";

	var strURL  = "handle.do?ServiceName=D1_DiggingWorkMgr-service&selectList=1"
	            + "&EOCS_NUM="+peocs_num
	            + "&DIG_WORK_PATH_FLAG="+pdig_work_path_flag
	            ;
	       
	var strInDatasets   = "";
	var strOutDatasets  = "ds_Multi=ds_List";
	var strArgument     = "";
	
	gfn_SyncCall( 
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
				  
			   );
			   
		
	if(ErrorCode < 0)
	{    
		return;
	}
	
    if(ds_Multi.GetColumn(0,"DIG_WORK_PATH_FLAG")!="1")
    {
	
		cob_BranchCd2.Enable=true;
    }
    else
    {
		cob_BranchCd2.Enable=false;
    }
    var temp1=ds_Multi.GetColumn(0,"BRANCH_CD");
    var temp2=ds_Multi.GetColumn(0,"TEAM_CD");
   
    fn_d1_block2(temp1,temp2);
    fn_fn_ChangeLarge();
    gubun="";
    veocsnum=ds_Multi.GetColumn(0,"EOCS_NUM");
    fn_d1_FileSearch();// 첨부파일 조회 
    cob_BranchCd2.Enable=false;
   
}

//-------------------------------------------------------------------------------
//   함수명: fn_DetailSearch()
//   설명  :  그리드 셀 선택시  굴착공사 조회 
//   arguments : nRow  
//   obj
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_DetailSearch(nRow)
{

	ds_Multi.ClearData();
	
	//trace(nRow);
	if(nRow < 0) return;

    var str_eocs_num           = ds_Search.GetColumn(nRow, "EOCS_NUM");
    var str_dig_work_path_flag = ds_Search.GetColumn(nRow, "DIG_WORK_PATH_FLAG");
   
    var strSvcID = "fn_DetailSearch";
	var strPart  = "D10";
	var strURL   = "handle.do?ServiceName=D1_DiggingWorkMgr-service&selectList=1";
	var strInDatasets   = "";
	var strOutDatasets  = "ds_Multi=ds_List";
	var strArgument     = "EOCS_NUM="+quote(str_eocs_num);
	    strArgument    += " DIG_WORK_PATH_FLAG="+quote(str_dig_work_path_flag);
	
	gfn_SyncCall( 
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
				  
			   );
			   
		
	if(ErrorCode < 0)
	{    
		return;
	}
	
    if(ds_Multi.GetColumn(0,"DIG_WORK_PATH_FLAG") != "1")
    {
	
		cob_BranchCd2.Enable=true;
    }
    else
    {
		cob_BranchCd2.Enable=false;
    }
    
    var temp1=ds_Multi.GetColumn(0,"BRANCH_CD");
    var temp2=ds_Multi.GetColumn(0,"TEAM_CD");
   
	fn_d1_team2(temp1);
	   
    fn_d1_block2(temp1,temp2);
    fn_fn_ChangeLarge();
    gubun="";
    veocsnum=ds_Multi.GetColumn(0,"EOCS_NUM");
    fn_d1_FileSearch();// 첨부파일 조회 
    
    cob_BranchCd2.Enable=false;
   
   
}


//-------------------------------------------------------------------------------
//   함수명: fn_D10_dt_save()
//   설명  :  굴착 종류가 대형이 아닐때  저장  
//   arguments   
//   obj
//   return 내용: 없음
//------------------------------------------------------------------------------- 	
function  fn_D10_dt_save()
{
    var Result = fn_D10_Validate();
    
    if(Result=="1")
    {
		return;
    }


	ds_Multi.AddColumn("CRT_EMPID");
	ds_Multi.AddColumn("CRT_IP");
	ds_Multi.SetColumn(0, "CRT_EMPID", gfn_GetGlobalValue("G_USER_NO"));
	ds_Multi.SetColumn(0, "CRT_IP",gfn_GetGlobalValue("G_IPADDRESS"));
	
	//ds_Multi.SetColumn(0, "EOCS_VIRTUAL_NUM", edt_EocsNum.text); 

	var strSvcID = "save";
	var strPart = "D10";
	var strURL  = "handle.do?ServiceName=D1_DiggingWorkMgr-service&fmodify=1";
	var strInDatasets   = "ds_Multi=ds_Multi:u ds_Search=ds_Search:u";
	var strOutDatasets  = "";
	var strArgument     = "";
	gfn_syncCall( 
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
				  
			   );
			   
		
	if(ErrorCode < 0)
	{    
		return;
	}
	else
	{
		alert(gfn_GetMessage("M000009","%AAA%","정상적으로 저장을"));
	}

	fn_CommonSearch();
	save_flag="";
}

//-------------------------------------------------------------------------------
//   함수명: fn_D10_dt_save2()
//   설명  :  굴착 종류가 대형일때  저장  
//   arguments   
//   obj
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_D10_dt_saveLarge()
{
    
    var Result = fn_D10_Validate();
    
    if(Result==1)
    {
		return;
    }
    
    var Result2 = fn_D10_Validate_Large();
    
    if(Result2==1)
    {
		return;
    }
	   
	ds_Multi.AddColumn("CRT_EMPID");
	ds_Multi.AddColumn("CRT_IP");
	//ds_Multi.SetColumn(0, "EOCS_VIRTUAL_NUM", edt_EocsNum.text); 
	
	ds_Multi.SetColumn(0, "CRT_EMPID", gfn_GetGlobalValue("G_USER_NO"));
	ds_Multi.SetColumn(0, "CRT_IP", gfn_GetGlobalValue("G_IPADDRESS"));

	var strSvcID = "save";
	var strPart = "D10";
	var strURL  = "handle.do?ServiceName=D1_DiggingWorkMgr-service&smodify=1";
	var strInDatasets   = "ds_MultiD=ds_Multi:u";
	var strOutDatasets  = "";
	var strArgument     = "";

	gfn_syncCall( 
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
				  
			   );
			   
	
	if(ErrorCode < 0)
	{    
		return;
	}
	else
	{
		alert(gfn_GetMessage("M000009","%AAA%","정상적으로 저장을"));
	}
	
    fn_CommonSearch();
    save_flag="";
}


//-------------------------------------------------------------------------------
//   함수명: fn_ZipSearch()
//   설명: 우편번호 조회클릭시 우편번호 조회 popup화면 로딩
//   arguments   
//   obj: 사용된 객체를 전달 한다.
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_ZipSearch(obj)
{
	//  주소검색 팝업창을 부른다 
	var arrList = gfn_Dialog("A10","A1101020010U","",1090,1095,true,-1,-1);
	
		ds_Multi.SetColumn(0,"STR_ZIP_NO1",arrList[2] ); // 우편번호 1
		ds_Multi.SetColumn(0,"STR_ZIP_NO2",arrList[3] ); // 우편번호 2
		ds_Multi.SetColumn(0,"STR_ZIP_SEQ",arrList[0] ); // 주소일련번호 
		ds_Multi.SetColumn(0,"STR_HILL",arrList[10] );    // 산번지
		ds_Multi.SetColumn(0,"STR_ADDR_M",arrList[11] );  // 본번 
		ds_Multi.SetColumn(0,"STR_ADDR_S",arrList[12] );  // 부번 
		ds_Multi.SetColumn(0,"STR_SECOND_ADDR",arrList[13] ); //보조주소
		ds_Multi.SetColumn(0,"STR_DONG",arrList[14] ); // 동
		ds_Multi.SetColumn(0,"STR_HOSU",arrList[15] ); // 호수
		ds_Multi.SetColumn(0,"STR_LOT_NUM_UNION",arrList[17] );  // 지번주소조합 
		// ds_Multi.SetColumn(0,"STR_NEW_ADDR_BLD_SEQ",arrList[18] ); // 새주소건물일련번호
		// ds_Multi.SetColumn(0,"STR_NEW_HOSU",arrList[21] );         // 새주소 호수 
		// ds_Multi.SetColumn(0,"STR_NEW_ADDR_UNION",arrList[23] );   // 새주소 조합 

		//edt_StrZipNo1.Text    = arrList[0];
		//edt_StrZipNo2.Text    = arrList[1];
		//ds_Multi.SetColumn(0,"STR_ZIP_SEQ",arrList[2] );
		//edt_StrLotNumUnion.Text = arrList[3];
		
		fn_D10_branch();
		branchcd = cob_BranchCd2.Value;
		fn_d1_team2(branchcd);
	
}

