// 대형공사일때와 아닐때 구분 
function fn_ChangeLarge(obj,strCode,strText,nOldIndex,nNewIndex)
{
	fn_fn_ChangeLarge();
}


function fn_fn_ChangeLarge()
{
	if(cob_DigKind2.Value !='10')
	{
		edt_SafeValuaObjYn.Enable=false;
		cob_LkalarmFlag.Enable   =false;
		edt_LkAlarmCnt.Enable    =false;
		cob_ChkPsway.Enable      =false;
		edt_ChkPswqyCnt.Enable   =false;
		edt_BigEmpid1.Enable     =false;
		edt_BigEmpid2.Enable     =false;
		cob_LargeTaker1TelDdd.Enable    =false;
		edt_LargeTaker1TelExn.Enable    =false;
		edt_LargeTaker1TelNum.Enable    =false;
		cob_LargeTaker2TelDdd.Enable    =false;
		edt_LargeTaker2TelExn.Enable    =false;
		edt_LargeTaker2TelNum.Enable    =false;
		cob_PipePress.Enable     =false;
		cob_PipeDiam.Enable      =false;
		edt_CutValveLoc.Enable   =false;
		edt_BigLeng.Enable = false;
	}
	if(cob_DigKind2.Value =='10')
	{
		edt_SafeValuaObjYn.Enable=true;
		cob_LkalarmFlag.Enable   =true;
		edt_LkAlarmCnt.Enable    =true;
		cob_ChkPsway.Enable      =true;
		edt_ChkPswqyCnt.Enable   =true;
		edt_BigEmpid1.Enable     =true;
		edt_BigEmpid2.Enable     =true;
		cob_LargeTaker1TelDdd.Enable    =true;
		edt_LargeTaker1TelExn.Enable    =true;
		edt_LargeTaker1TelNum.Enable    =true;
		cob_LargeTaker2TelDdd.Enable    =true;
		edt_LargeTaker2TelExn.Enable    =true;
		edt_LargeTaker2TelNum.Enable    =true;
		cob_PipePress.Enable     =true;
		cob_PipeDiam.Enable      =true;
		edt_CutValveLoc.Enable   =true;
		edt_BigLeng.Enable = true;
	}
}

//-------------------------
// 가상 eocs번호 체크 
//-------------------------
function fn_D10_Eocs_Chk()
{

	ds_chk.ClearData();
	
	var strSvcID = "searchchk";
	var strPart = "D10";
	var strURL  = "handle.do?ServiceName=D1_DiggingWorkMgr-service&chk=1";
	var strInDatasets   = "";
	var strOutDatasets  = "ds_chk=ds_chk";
	var strArgument     = "";
	
	gfn_SyncCall( 
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
				  
			   );
	
	
}

//----------------------
//  입회 점검 
//----------------------
function fn_D10_Join_Chk()
{
	var strSvcID = "";
	var strPart = "D10";
	var strURL  = "handle.do?ServiceName=D1_DiggingWorkMgr-service&chk1=1"
	       	    + "&EOCS_NUM="+ ds_Multi.GetColumn(0,"EOCS_NUM")
	            + "&DIG_WORK_PATH_FLAG="+ ds_Multi.GetColumn(0,"DIG_WORK_PATH_FLAG")
	            + "&JOIN_YMD="+ AddDate(today(),-7)
	            ;
	var strInDatasets   = "";
	var strOutDatasets  = "ds_joinchk=ds_chkcnt";
	var strArgument     = "";
	
	gfn_SyncCall( 
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
				  
			   );
			   
		
	if(ErrorCode < 0)
	{    
		return;
	}
	
	
}


