//---------------------------
//   굴착공사협의서팝업
//
//---------------------------
function fn_D10_Popup1(obj)
{   
    if(ds_Multi.GetRowCount()<1)
    {
		alert("조회를 먼저 하세요 ");
		return;
    }
    
	fn_D10_Discu_Chk(); 
	ds_ReportList.ClearData();		

	if(ds_discu.GetRowCount()>0)
	{
 
		for(var i=0; i<ds_discu.GetRowCount();i++)
		{
			ds_ReportList.addRow();
			var strTitle    = "굴착공사 협의서"+ds_discu.GetColumn(i,"DISCU_YMD") ;               // 출력물 제목
			var strPart     = "D10" ;                           // 시스템 명 
			var strReport   = "D1401010010P";
			var strParameter= "EOCS_NUM=" + ds_discu.GetColumn(i,"EOCS_NUM")  // 파라미터구문 (단 ',' 로구분할것 ) 
		  		             +",DIG_WORK_PATH_FLAG="+ds_discu.GetColumn(i,"DIG_WORK_PATH_FLAG")
					         +",DISCU_YMD="+ds_discu.GetColumn(i,"DISCU_YMD")
					         ;
			ds_ReportList.SetColumn(i, "SYSTEM_ID"   ,strPart     ); // 시스템 명 
			ds_ReportList.SetColumn(i, "REPORT_ID"   ,strReport   ); // 출력물 ID 
			ds_ReportList.SetColumn(i, "REPORT_ARGS" ,strParameter); // 파라미터구문 
			ds_ReportList.SetColumn(i, "REPORT_TITLE",strTitle    ); // 출력물 제목
		}
			gfn_MultiReportPopup("",ds_ReportList, "false");
		}
		else
		{
			alert(gfn_GetMessage("M000008","%AAA%","작성된 협의서가 "));
		}
}


//-------------------------
//   입회내역서  
//-------------------------
function fn_D10_Popup2(obj)
{
    
    if(ds_Multi.GetRowCount()<1)
    {
		alert("조회를 먼저 하세요 ");
		return;
    }
    
   	alert("입회보고서는 1주일전까지 내역만 조회가 가능합니다..\n이전은 굴착공사 입회내역 조회를 이용하십시요");
   	    
    fn_D10_Join_Chk();
	ds_ReportList.ClearData();		
	
	if(ds_joinchk.GetRowCount()>0)
	{
 
		for(var i=0; i<ds_joinchk.GetRowCount();i++)
		{
			ds_ReportList.addRow();
			var strTitle    = "입회내역"+ds_joinchk.GetColumn(i,"JOIN_YMD") ;               // 출력물 제목
			var strPart     = "D10" ;                           // 시스템 명 
			var strReport="";
			if(cob_DigKind2.Value=="10")
			{
				strReport   = "D1402010010P" ;                   // 출력물 ID 
			}
			else
			{
				strReport   = "D1402010013P" ;
			}
			var strParameter= "EOCS_NUM=" + ds_joinchk.GetColumn(i,"EOCS_NUM")  // 파라미터구문 (단 ',' 로구분할것 ) 
		  		             +",DIG_WORK_PATH_FLAG="+ds_joinchk.GetColumn(i,"DIG_WORK_PATH_FLAG")
					         +",JOIN_YMD="+ds_joinchk.GetColumn(i,"JOIN_YMD")
					         +",SEQ="+ds_joinchk.GetColumn(i,"SEQ")
					         ;
			ds_ReportList.SetColumn(i, "SYSTEM_ID"   ,strPart     ); // 시스템 명 
			ds_ReportList.SetColumn(i, "REPORT_ID"   ,strReport   ); // 출력물 ID 
			ds_ReportList.SetColumn(i, "REPORT_ARGS" ,strParameter); // 파라미터구문 
			ds_ReportList.SetColumn(i, "REPORT_TITLE",strTitle    ); // 출력물 제목
		}
		
		gfn_MultiReportPopup("",ds_ReportList, "false");
	}
	else
	{
		alert(gfn_GetMessage("M000008","%AAA%","작성된 입회내역서가 "));
	}
}

//-----------------------------------
//   대형굴착점검
//-----------------------------------
function fn_D10_Popup3(obj)
{
    if(ds_Multi.GetRowCount()<1)
    {
		alert("조회를 먼저 하세요 ");
		return;
    }
    
   	alert("대형점검일지는 1주일전까지 내역만 조회가 가능합니다..\n이전은 굴착공사 입회내역 조회를 이용하십시요");
    	
    fn_D10_Big_Chk();

	ds_ReportList.ClearData();		
	
	if(ds_bigchk.GetRowCount()>0)
	{
 
		for(var i=0; i<ds_bigchk.GetRowCount();i++)
		{
			ds_ReportList.addRow();
			var strTitle    = "대형굴착점검"+ds_bigchk.GetColumn(i,"JOIN_YMD") ;               // 출력물 제목
			var strPart     = "D10" ;                           // 시스템 명 
			var strReport="";
				strReport   = "D1402010012P" ;                   // 출력물 ID 

			var strParameter= "EOCS_NUM=" + ds_bigchk.GetColumn(i,"EOCS_NUM")  // 파라미터구문 (단 ',' 로구분할것 ) 
		  		             +",DIG_WORK_PATH_FLAG="+ds_bigchk.GetColumn(i,"DIG_WORK_PATH_FLAG")
					         +",JOIN_YMD="+ds_bigchk.GetColumn(i,"JOIN_YMD")
					         ;
			ds_ReportList.SetColumn(i, "SYSTEM_ID"   ,strPart     ); // 시스템 명 
			ds_ReportList.SetColumn(i, "REPORT_ID"   ,strReport   ); // 출력물 ID 
			ds_ReportList.SetColumn(i, "REPORT_ARGS" ,strParameter); // 파라미터구문 
			ds_ReportList.SetColumn(i, "REPORT_TITLE",strTitle    ); // 출력물 제목
		}
			gfn_MultiReportPopup("",ds_ReportList, "false");
	}
	else
	{
		alert(gfn_GetMessage("M000008","%AAA%","대형굴착점검내역이 "));
	}
}

