}

//-----------------------------------------
//  가스안전평가영향서 
//-----------------------------------------
function fn_D10_Popup5(obj)
{
    
    if(ds_Multi.GetRowCount()<1)
    {
		alert("조회를 먼저 하세요 ");
		return;
    }
    if(cob_DigKind2.Value!="10")
    {
		alert("대형굴착공사 일때 조회 가능합니다.");
		return;	
    }
	if(ds_Multi.GetColumn(0,"SAFE_VALUA_OBJ_YN")==NULL||ds_Multi.GetColumn(0,"SAFE_VALUA_OBJ_YN")==" ")
	{  
		alert("대형굴착공사 일때 조회 가능합니다.");
		return;
	}
	var arg  = "EOCS_NUM=" + quote(ds_Multi.GetColumn(0,"EOCS_NUM"));
	    arg += " DIG_WORK_PATH_FLAG=" + quote(ds_Multi.GetColumn(0,"DIG_WORK_PATH_FLAG"));
	    arg += " DIG_WORK_NM=" +quote(ds_Multi.GetColumn(0,"DIG_WORK_NM"));
	gfn_Dialog("D10","D1402020011U",arg,1005,554,"Scroll",-1,-1);
}

