	    arg += " STR_ZIP_NO1 =" +quote( cob_StrZipNo1.Value);
	    arg += " DIG_KIND=" + quote(cob_DigKind.Value);
	    arg += " JOB_CD=" +   quote(cob_JobCd.Value);
	    arg += " DIG_WORK_PROC_STS=" + quote(cob_DigWorkProcSts.Value); 
	    arg += " RECEIVE_YMD1="  + quote(cob_ReciiveYmd1.Value); 
