	gfn_Dialog("D10","D1401010016U",arg,1005,577,"Scroll",-1,-1);
	
}


//----------------------------------
// function : fn_bt_OnClick7()
// comment  : 공사조회 팝업(가상제외) 
// return   :
//----------------------------------
function fn_bt_OnClick7(obj)
{
    var temp = ds_Multi.GetColumn(0,"EOCS_NUM");
    var temp2 = substr(temp,0,1) ;
    
    if(ds_Multi.GetRowCount() > 0)
    {
        if(temp2 == "V")
		{
			var arg  = "BRANCH_CD  =" + quote(cob_BranchCd2.Value);
				arg += " STR_ZIP_NO1 =" + quote(cob_StrZipNo1.Value);
				arg += " PDIG_KIND   =" + quote(cob_DigKind.Value);
				arg += " PJOB_CD     =" + quote(cob_JobCd.Value);
				arg += " PRECEIVE_YMD1 =" + quote(cob_ReciiveYmd1.Value);
				arg += " PRECEIVE_YMD2 =" + quote(cob_ReciiveYmd2.Value);
				arg += " OLD_EOCS_NUM =" + quote(temp);
		
			var arrList = gfn_Dialog("D10","D1401010015U",arg,1005,577,"Scroll",-1,-1);
		
			
			if(length(arrList) > 0)
			{
				if(arrList[0] == "Y")
				{
					fn_CommonSearch();	
				}
			}
		
		}
		else
		{
			alert(ds_Multi.GetColumn(0,"EOCS_NUM")+"공사번호는 가상공사번호가 아닙니다.");
			return;
		}
	}
	else
	{
		alert("조회를 먼저 해주십시오.");
	}
}

