//*********************************
//* 사원 찾기 팝업
//* 
//*********************************
function btn_CardUserEmpNm(obj)
{
	if(cob_DigKind2.Value=="10")
	{
		var retValue = gfn_E2_SearchOutEmplDialog(edt_BigEmpid1.Text); //사용자코드(조회조건용)
		ds_Multi.SetColumn(0,"BIG_EMPID_1",retValue[0]);
		edt_BigEmpid1.Text=retValue[1];
	}
}

function btn_CardUserEmpNm2(obj)
{
	if(cob_DigKind2.Value=="10")
	{
		var retValue = gfn_E2_SearchOutEmplDialog(edt_BigEmpid2.Text); //사용자코드(조회조건용)
		ds_Multi.SetColumn(0,"BIG_EMPID_2",retValue[0]);
		edt_BigEmpid2.Text=retValue[1];
	}
}


//----------------------------------
// 함수명  : fn_D10_Validate()
// return  : 1 
// comment : Validate check
//----------------------------------
function fn_D10_Validate()
{
	if(edt_EocsNum.Text ==""||edt_EocsNum.Text == null)
	{
		alert(gfn_GetMessage("M000015","%AAA%","EOCS번호는"));
		edt_EocsNum.SetFocus();
		return 1;
	}
	
	/*
	else if(cob_DigWorkProcSts2.Value ==""||cob_DigWorkProcSts2.Value == null)
	{
		alert(gfn_GetMessage("M000015","%AAA%","진행상태는"));	
		cob_DigWorkProcSts2.SetFocus();
		return 1;
	}
	*/
	else if(cob_DigKind2.Value ==""||cob_DigKind2.Value == null)
	{
		alert(gfn_GetMessage("M000015","%AAA%","굴착종류는"));		
		cob_DigKind2.SetFocus();
		return 1;
	}
	else if(cob_JobCd2.Value ==""||cob_JobCd2.Value == null)
	{
		alert(gfn_GetMessage("M000015","%AAA%","작업종류는"));
		cob_JobCd2.SetFocus();
		return 1;
	}
	else if(cob_DigWorkPathFlag2.Value ==""||cob_DigWorkPathFlag2.Value == null)
	{
		
		alert(gfn_GetMessage("M000015","%AAA%","굴착구분은"));
		cob_DigWorkPathFlag2.SetFocus();
		return 1;
	}
	else if(edt_DigWorkNm2.Text ==""||edt_DigWorkNm2.Text == null)
	{
		alert(gfn_GetMessage("M000015","%AAA%","굴착공사명은"));
		edt_DigWorkNm2.SetFocus();
		return 1;
	}
	else if(edt_StrZipNo1.Text ==""||edt_StrZipNo1.Text == null)
	{
		alert(gfn_GetMessage("M000015","%AAA%","공사시점은"));
		edt_StrZipNo1.SetFocus();
		return 1;
	}
	else if(edt_EndZipNo1.Text ==""||edt_EndZipNo1.Text == null)
	{
		alert(gfn_GetMessage("M000015","%AAA%","공사종점은"));
		edt_EndZipNo1.SetFocus();
		return 1;
	}
	else if(edt_leng.Text ==""||edt_leng.Text == null)
	{
		alert(gfn_GetMessage("M000015","%AAA%","연장은"));	
		edt_leng.SetFocus();
		return 1;
	}
	else if(cal_DigPalnYmd.Value ==""||cal_DigPalnYmd.Value == null)
	{
		alert(gfn_GetMessage("M000015","%AAA%","굴착예정일자는"));	
		cal_DigPalnYmd.SetFocus();
		return 1;
	}
	else if(cal_DigEndPlanYmd.Value ==""||cal_DigEndPlanYmd.Value == null)
	{
		alert(gfn_GetMessage("M000015","%AAA%","굴착완료일자는"));
		cal_DigEndPlanYmd.SetFocus();
		return 1;
	}
	else if(cob_DigTakerTelDdd.Value ==""||cob_DigTakerTelDdd.Value == null)
	{
		alert(gfn_GetMessage("M000015","%AAA%","굴착담당자연락처는"));
		cob_DigTakerTelDdd.SetFocus();
		return 1;
	}
	else if(edt_DigTakerTelExn.Text ==""||edt_DigTakerTelExn.Text == null)
	{
		alert(gfn_GetMessage("M000015","%AAA%","굴착담당자연락처는"));
		edt_DigTakerTelExn.SetFocus();
		return 1;
	}
	else if(edt_DigTakerTelNum.Text ==""||edt_DigTakerTelNum.Text == null)
	{
		alert(gfn_GetMessage("M000015","%AAA%","굴착담당자연락처는"));
		edt_DigTakerTelNum.SetFocus();
		return 1;
	}
	else if(cal_ReceiveYmd.Value ==""||cal_ReceiveYmd.Value == null)
	{
		alert(gfn_GetMessage("M000015","%AAA%","접수일자는"));	
		cal_ReceiveYmd.SetFocus();
		return 1;
	}
	else if(cob_TeamCd2.Value ==""||cob_TeamCd2.Value == null)
	{
		alert(gfn_GetMessage("M000015","%AAA%","팀은"));
		cob_TeamCd2.SetFocus();
		return 1;
	}
	else if(cob_BlockCd2.Value ==""||cob_BlockCd2.Value == null)
	{
		alert(gfn_GetMessage("M000015","%AAA%","구간은"));
		cob_BlockCd2.SetFocus();
		return 1;
	}
}
//-----------------------------------------------------------------------
// Function : fn_D10_Validate_Large()
// comment  : 대형굴착공사일때 Validate check
// arg      :
// return   : 1
//-----------------------------------------------------------------------
function fn_D10_Validate_Large()
{
	if(cob_BranchCd2.Value==""||cob_BranchCd2.Value==null)
	{
		gfn_GetMessage("M000015","&ITEM","지사는");
		cob_BranchCd2.SetFocus();
		return 1;
	}
	if(edt_SafeValuaObjYn.Value==""||cob_LkalarmFlag.Value==null)
	{
		gfn_GetMessage("M000015","&ITEM","안전평가는");
		edt_SafeValuaObjYn.SetFocus();
		return 1;
	}
	if(cob_LkalarmFlag.Value==""||cob_LkalarmFlag.Value==null)
	{
		gfn_GetMessage("M000015","&ITEM","누출경보기는");
		cob_LkalarmFlag.SetFocus();
		return 1;
	}
	if(cob_ChkPsway.Value==""||cob_ChkPsway.Value==null)
	{
		alert(gfn_GetMessage("M000015","%AAA%","점검통로는"));
		cob_ChkPsway.SetFocus();
		return 1;
	}
	if(edt_BigEmpid1.Text==""||edt_BigEmpid1.Text==null)
	{
		alert(gfn_GetMessage("M000015","%AAA%","담당자1은"));
		edt_BigEmpid1.SetFocus();
		return 1;
	}
	if(edt_BigEmpid2.Text==""||edt_BigEmpid2.Text==null)
	{
		alert(gfn_GetMessage("M000015","%AAA%","담당자2는"));
		edt_BigEmpid2.SetFocus();
		return 1;
	}

}

