//------------------------------------------
// Function : fn_D10_branch()
// comment  : 우편번호에 따른 지사 
// return   :
//------------------------------------------
function fn_D10_branch()
{
	var strSvcID = "";
	var strPart = "D10";
	var strURL  = "handle.do?ServiceName=D1_DiggingWorkMgr-service&zip=1"
	            + "&ZIP_CD = "+Trim(edt_StrZipNo1.Text)
	            ;
	var strInDatasets   = "";
	var strOutDatasets  = "ds_zip=ds_zip";
	var strArgument     = "";
	gfn_syncCall( 
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
				  
			   );
			   
		
	if(ErrorCode < 0)
	{    
		return;
	}
	cob_BranchCd2.Value=ds_zip.GetColumn(0,"BRANCH_CD");
}

//----------------------------------------------------------------
// Function : fn_D10_pkUpDate()
// comment  : 가상 eocs 공사번호를 실제 eocs공사번호로 Update 
// arg      :
// return   :
//----------------------------------------------------------------
function fn_D10_pkUpDate()
{
	ds_uall.AddRow();
	ds_uall.UpdateControl=false;
	ds_uall.SetRowType(0,"delete");
	ds_uall.SetColumn(0, "EOCS_NUM", edt_EocsNum.text); 
	ds_uall.AddRow();
	ds_uall.SetRowType(1,"update");
	ds_uall.SetColumn(1, "CRT_EMPID", gfn_GetGlobalValue("G_USER_NO"));
	ds_uall.SetColumn(1, "CRT_IP",gfn_GetGlobalValue("G_IPADDRESS"));
	ds_uall.SetColumn(1, "EOCS_NUM", edt_EocsNum.text); 
	ds_uall.SetColumn(1, "VEOCS_NUM", veocsnum); 

	var strSvcID = "";
	var strPart = "D10";
	var strURL  = "handle.do?ServiceName=D1_DiggingWorkMgr-service&allmodify=1";
	var strInDatasets   = "ds_uall=ds_uall:u";
	var strOutDatasets  = "";
	var strArgument     = "";
	gfn_syncCall( 
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
				  
			   );
			   
		
	if(ErrorCode < 0)
	{    
		return;
	}
	else
	{
		gfn_SetStatusMsg(gfn_GetMessage("M000009","%AAA%","정상적으로 변경"),"black",true);
	}
	
}

// 첨부파일 팝업호출 
function bt_d1_FileUpload(obj)
{
    if(save_flag=="insert")
    {
		alert("저장을 먼저 해주십시오.");
		return;
    }
	var arg = "fa_Url=handle.do?ServiceName=D1_DiggingWorkMgr-service&EOCS_NUM="+ds_multi.GetColumn(0,"EOCS_NUM")+"&DIG_WORK_PATH_FLAG="+ds_multi.GetColumn(0,"DIG_WORK_PATH_FLAG")+" fa_Part=D10";
	var arrList = gfn_Dialog("D10","D1101010018U",arg,450,280,true,-1,-1);
	edtFileList.text = "";
	for(var i=0; i<arrList.length; i++)
	{
		edtFileList.text += arrList[i];
		if(arrList.length-1 != i) edtFileList.text += "/";
	}

}
// 첨부파일 조회 
function fn_d1_FileSearch()
{
	var strSvcID = "";
	var strPart = "D10";
	var strURL = "handle.do?ServiceName=D1_DiggingWorkMgr-service&findFile=1"
	           + "&EOCS_NUM="+ds_multi.GetColumn(0,"EOCS_NUM")
	           + "&DIG_WORK_PATH_FLAG="+ds_multi.GetColumn(0,"DIG_WORK_PATH_FLAG")
	           ;
	var strInDatasets  = "";
	var strOutDatasets  = "";
	    strOutDatasets += "ds_FileList=ds_FileList";
	var strArgument  = "";
	gfn_SyncCall( 
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
			   );
	
	if(ErrorCode < 0)
	{
		return;
	}
	edtFileList.text = "";
	for(var i=0; i<ds_FileList.RowCount(); i++)
	{
		edtFileList.text += ds_FileList.GetColumn(i,"ORG_FILE_NAME");
		if(ds_FileList.RowCount()-1 != i) edtFileList.text += "/";
		}
	
}


// 대형 굴착 점검 전자결재 
function bt_d1_bigSanction(obj)
{
	fn_D10_Big_Chk();
	if(ds_Multi.GetRowCount()<1)
	{
		alert(gfn_GetMessage("M000040","%AAA%","결재할 대형굴착공사를 먼저"));
		return;
	}
	if(ds_bigchk.GetRowCount()<1)
	{
		alert(gfn_GetMessage("M000008","%AAA%","결재할 장표가"));
		return;
	}
	var bigindex = ds_bigchk.GetRowCount();
	var args = ds_bigchk.GetColumn(bigindex-1,"EOCS_NUM")+","+ds_bigchk.GetColumn(bigindex-1,"DIG_WORK_PATH_FLAG")+","+ds_bigchk.GetColumn(bigindex-1,"JOIN_YMD") ;
	
	gfv_callGWInfo("fmd15","d10","D1402010012P",3," "+args+" ");
}

// 입회내역서 대형 
function fn_d1_SanctionBig()
{
	
	if(ds_Multi.GetRowCount()<1)
	{
		alert(gfn_GetMessage("M000040","%AAA%","결재할 굴착공사를 먼저"));
		return;
	}
	fn_D10_Join_Chk();
	if(ds_joinchk.GetRowCount()<1)
	{
		alert(gfn_GetMessage("M000008","%AAA%","결재할 장표가"));
		return;
	}
    var bigindex =ds_joinchk.GetRowCount();
	var args = ds_joinchk.GetColumn(bigindex-1,"EOCS_NUM")+","+ds_joinchk.GetColumn(bigindex-1,"DIG_WORK_PATH_FLAG")+","+ds_joinchk.GetColumn(bigindex-1,"JOIN_YMD")+","+ds_joinchk.GetColumn(bigindex-1,"SEQ");
	gfv_callGWInfo("fmd13","d10","D1402010010P",4,""+args+"");
}

function bt_d1_join_Sanction()
{
	if(cob_DigKind2.Value=="10")
	{
		fn_d1_SanctionBig();
	}
	else
	{
		fn_d1_SanctionNormal();
	}
}

// 입회내역서 일반
function fn_d1_SanctionNormal()
{
	if(ds_Multi.GetRowCount()<1)
	{
		alert(gfn_GetMessage("M000040","%AAA%","결재할 굴착공사를 먼저"));
		return;
	}
	fn_D10_Join_Chk();
	if(ds_joinchk.GetRowCount()<1)
	{
		alert(gfn_GetMessage("M000008","%AAA%","결재할 장표가"));
		return;
	}
    var bigindex =ds_joinchk.GetRowCount();
	var args = ds_joinchk.GetColumn(bigindex-1,"EOCS_NUM")+","+ds_joinchk.GetColumn(bigindex-1,"DIG_WORK_PATH_FLAG")+","+ds_joinchk.GetColumn(bigindex-1,"JOIN_YMD")+","+ds_joinchk.GetColumn(bigindex-1,"SEQ");
	gfv_callGWInfo("fmd14","d10","D1402010013P  ",4,""+args+"");
}


// 지사에 대한 팀 조회 
function fn_d1_team(branchcd)
{
	ds_team.ClearData();
	
	var strSvcID = "fn_d1_team";
	var strPart = "D10";
	var strURL = "handle.do?ServiceName=D1_DiggingWorkMgr-service&teamchk=1";
	var strInDatasets  = "";
	var strOutDatasets = "ds_team=ds_team";
	var strArgument  = "BRANCH_CD="+quote(branchcd);
	
	gfn_SyncCall( 
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
			   );
	
	if(ErrorCode < 0)
	{
		alert(ErrorMsg);
		return;
	}
	else
	{
	
		var nAddRow = ds_team.AddRow();
		ds_team.SetColumn(nAddRow,"BRANCH_CD","");
		ds_team.SetColumn(nAddRow,"DEPRT_NM_KOR","전체");
		ds_team.SetColumn(nAddRow,"DEPART_DPT_CD","");
	}
    
	
}

