{

	ds_block.ClearData();
	
	var strSvcID = "fn_d1_block";
	var strPart = "D10";
	var strURL = "handle.do?ServiceName=D1_DiggingWorkMgr-service&block=1";
	var strInDatasets  = "";
	var strOutDatasets = "ds_block=ds_block";
	var strArgument  = "BRANCH_CD="+quote(StrBRANCH_CD);
	    strArgument += " TEAM_CD="+quote(StrTEAM_CD);
	
	gfn_SyncCall( 
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
			   );
	
	if(ErrorCode < 0)
	{
		alert(ErrorMsg);
		return;
	}
	else
	{
		var nAddRow = ds_block.AddRow();
		ds_block.SetColumn(nAddRow,"BLOCK_CD","");
		ds_block.SetColumn(nAddRow,"BLOCK_NM","전체");
	}
	
}
