// 지사 콤보 이벤트 
function cob_BranchCd_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{ 
	fn_SetComboBox_Comcode("1", cob_BranchCd.Value, fn_setDsObject_Comcode("1", "cob_StrZipNo1")); // 시/구
	
	fn_d1_team(cob_BranchCd.Value);

	cob_StrZipNo1.Value = "";
		
	cob_TeamCd.Value = "";
	cob_TeamCd_OnChanged();
	
}
// 조회조건 _ 팀 체인지시 구간조회 
function cob_TeamCd_OnChanged(obj,strPreText,strPostText)
{
	 
	fn_d1_block( cob_BranchCd.Value, cob_TeamCd.Value);
	cob_BlockCd.Value = "";
	
	/*
	if(cob_StrZipNo1.Value=="")
	{
		cob_TeamCd.Value="";
	}
	*/
}
// 상세조회_팀 체인지시 구간조회 
function cob_TeamCd2_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
