// 처리상태 이벤트 
function cob_WorkSts_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if(cob_WorkSts.Value=="10")
	{   
		cob_DigWorkProcSts.Enable=false;
		cob_DigWorkProcSts.Value="";
	}
	else if(cob_WorkSts.Value=="20")
	{
		cob_DigWorkProcSts.Enable=false;
		cob_DigWorkProcSts.Value="";
	}
	else if(cob_WorkSts.Value=="30")
	{
		cob_DigWorkProcSts.Enable=false;
		cob_DigWorkProcSts.Value="";
	}
	else if(cob_WorkSts.Value=="40")
	{
		cob_DigWorkProcSts.Enable=false;
		cob_DigWorkProcSts.Value="";
	}	
	else if(cob_WorkSts.Value=="99")
	{   
		cob_DigWorkProcSts.Enable=true;
	}
}

