//  세션에 따른 지사 , 시/구 , 팀 조회 
function fn_d1_Setting()
{
	ds_set.ClearData();
	
	var strUSER_NO = gfn_GetGlobalValue("G_USER_NO");
	
	var strSvcID = "fn_d1_Setting";
	var strPart  = "D10";
	var strURL   = "handle.do?ServiceName=D1_DiggingWorkMgr-service&setting=1";
	var strInDatasets  = "";
	var strOutDatasets = "ds_set=ds_set";
	var strArgument  = "EMPID="+quote(strUSER_NO);
	
	gfn_SyncCall( 
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
			   );
	
	if(ErrorCode < 0)
	{
		return;
	}
	
}



