#include "JS::COMMONA10001.js";
#include "JS::COMMONA10002.js";
#include "JS::COMMONA10005.js";
#include "JS::COMMOND10001.js";
#include "JS::COMMONA10008.js";
#include "JS::COMMONE20001.js";

var save_flag = "";

function D1401010012U_OnLoadCompleted(obj)
{
	gfn_LoadForm(obj);

	fn_Init();
	gfn_D1_SetGrid_Sort(grd_Insert);
}

//---------------------------------------------------------
//	초기 설정
//---------------------------------------------------------
function fn_Init()
{
    gfn_D1_All_SetComboBox_Branchcd(gfn_D1_setDsObject_Branchcd("cob_BranchCd")); // 지사 
    fn_SetComboBox_Comcode("1", cob_BranchCd.Value, fn_setDsObject_Comcode("1", "cob_ZipNo")); // 시/구
    gfn_D1_All_SetComboBox_Comcode("D10166",gfn_setDsObject_Comcode("cob_JobCd"),""); //작업종류
	gfn_D1_SetGrdComboBox_Comcode("grd_Insert",1);
	//gfn_SetGrdComboBox_Comcode("grd_Insert",1,"B10037");
	gfn_SetGrdComboBox_Comcode("grd_Insert",4,"D10166");
	gfn_SetGrdComboBox_Comcode("grd_Insert",2,"B10004");
	gfn_SetGrdComboBox_Comcode("grd_Insert",6,"A10110");
	gfn_SetGrdComboBox_Comcode("grd_Insert",10,"A10110");
	fn_CommonSearch();
}

//-------------------------------------------------------------------------------
//   함수명: fn_CommonExcelExport()
//   설명  :  엑셀다운 
//   arguments   
//   obj
//   return 내용: 없음
//------------------------------------------------------------------------------- 

function fn_CommonExcelExport()
{
	var SearchCondition= "";
    
    SearchCondition = SearchCondition + "지사 :" + RPad(cob_BranchCd.Text," ",20);
    SearchCondition = SearchCondition + "시/구  :" + RPad(cob_ZipNo.Text," ",20);
    SearchCondition = SearchCondition + "공사종류 :" + RPad(cob_JobCd.Text," ",20);
    gfn_ExportExcel("굴착업체정보", 5, SearchCondition, "grd_Insert"); 
	
}
//----------------------
// 업체 추가 버튼 
//----------------------
function fn_CommonAddRow()
{   
	save_flag = "insert";
	ds_Multi.AddRow();				
}

//-------------------------
// 업체 삭제 버튼 
//-------------------------
function fn_CommonDeleteRow()
{
	if(confirm("삭제하시겠습니까?"))
    {
		save_flag = "delete";
		ds_Multi.DeleteRow(grd_Insert.ClickedRow);
		fn_CommonSave();
		fn_CommonSearch();
	}
}


//-------------------------------------------------------------------------------
//   함수명: fn_CommonSearch()
//   설명  :  조회 클릭시 굴착공사업체정보 조회 
//   arguments   
//   obj
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_CommonSearch()
{
    
	var strSvcID = "";
	var strPart = "D10";
	var strURL  = "handle.do?ServiceName=D1_DiggingWorkEnterpriseInfoMgr-service&Search=1"
		        + "&BRANCH_CD="+cob_BranchCd.VALUE
	            + "&ZIP_NO="+cob_ZipNo.Value
	            + "&JOB_CD="+cob_JobCd.Value
	            ;       
	var strInDatasets   = "";
	var strOutDatasets  = "ds_Multi=ds_Multi";
	var strArgument     = "";
	
	gfn_SyncCall( 
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
				  
			   );
			   
		
	if(ErrorCode < 0)
	{    
		return;
	}
	if(ds_Multi.GetRowCount()<1)
	{
		
		grd_Insert.NoDataText = gfn_GetMessage("M000022","%AAA%","조회결과가");
	}
    save_flag="";
   
}

//---------------------------------------------------------------------
//  Function : fn_CommonSave()
//  comment  : 업체를 등록 , 수정한다. 
//  arg      :
//  return   : 없음 
//---------------------------------------------------------------------
function fn_CommonSave()
{   
    for(var i=0;i<ds_Multi.GetRowCount();i++)
    {
        
		ds_Multi.SetColumn(i, "CRT_EMPID",gfn_GetGlobalValue("G_USER_NO"));
		ds_Multi.SetColumn(i, "CRT_IP",gfn_GetGlobalValue("G_IPADDRESS"));	
	}
	
	var strSvcID = "";
	var strPart  = "D10";
	var strURL   = "handle.do?ServiceName=D1_DiggingWorkEnterpriseInfoMgr-service&Insert=1";       
	var strInDatasets   = "ds_Multi=ds_Multi:u";
	var strOutDatasets  = "";
	var strArgument     = "";
	
	gfn_SyncCall( 
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
			   );
			   
		
	if(ErrorCode < 0)
	{    
		return;
	}
    else
    {
        if(save_flag == "delete")
        {
			alert(gfn_GetMessage("M000009", "%AAA%", "정상적으로 삭제를"));
		}
		else
		{
			alert(gfn_GetMessage("M000009", "%AAA%", "정상적으로 저장을"));
		}
		save_flag="";
    }
}


// 화면 종료시 이벤트 
function D1401010012U_OnUnloadCompleted(obj)
{
	if(ds_Multi.GetUpdate())
	{
		if(!confirm(gfn_GetMessage("M000016","%AAA%","변경된 값이 있습니다. 종료")))
		 {
			 return false;
		 }	
	}
}

// 지사선택시 이벤트 
function cob_BranchCd_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	fn_SetComboBox_Comcode("1", cob_BranchCd.Value, fn_setDsObject_Comcode("1", "cob_ZipNo")); // 시/구
}
