//---------------------------------------------------------
//	초기 설정
//---------------------------------------------------------

function fn_Init()
{
	//  콤보박스의 내용을 셋팅한다.	
	gfn_D1_SetComboBox_Branchcd(gfn_D1_setDsObject_Branchcd("cob_BranchCd"));
	gfn_D1_SetComboBox_Branchcd(gfn_D1_setDsObject_Branchcd("cob_BranchCd3"));
	gfn_D1_All_SetComboBox_Comcode("D10166",gfn_setDsObject_Comcode("cob_jobCd"),"");  //작업종류
    gfn_D1_All_SetComboBox_Comcode("D10165",gfn_setDsObject_Comcode("cob_digKind"),"");// 굴착종류 
    gfn_D1_All_SetComboBox_Comcode("D10187",gfn_setDsObject_Comcode("cob_digWorkProcSts"),""); //진행상태 
	gfn_SetGrdComboBox_Comcode("Grid1",11,"D10034");
	gfn_SetGrdComboBox_Comcode("Grid1",12,"B20031");
	gfn_SetGrdComboBox_Comcode("Grid1",14,"A10100");
	gfn_SetGrdComboBox_Comcode("Grid1",19,"A10100");
	
	cob_BranchCd.Value = cob_BranchCd2;
	cob_BranchCd3.Value = cob_BranchCd2;
	
	if(cob_DigKind2 == null)
	{
		cob_digKind.Index = 0;
	}
	else
	{
		cob_digKind.Value = cob_DigKind2;
	}
	
	if(cob_JobCd2 == null)
	{
		cob_jobCd.Index = 0;
	}
	else
	{
		cob_jobCd.Value = cob_JobCd2;
	}

	if(cob_DigWorkProcSts2 == null)
	{
		cob_digWorkProcSts.Index = 0;
	}
	else
	{
		cob_digWorkProcSts.Value = cob_DigWorkProcSts2;
	}

	if(cob_WorkSts == null)
	{
		cob_Sts.Index = 0;
	}
	else
	{
		cob_Sts.Value = cob_WorkSts;
	}
	cob_Sts_OnChanged();
	fn_D10_Serach();

	cal_HandoverYmd1.Value = Today();
	cal_HandoverYmd2.Value = Today();
	
	if (gfn_GetGlobalValue("G_BRANCH_CD") == "999999") 
	{
		cob_BranchCd.Enable = true;
		cob_BranchCd3.Enable = true;
	} 
	else 
	{	
		cob_BranchCd.Enable = false;
		cob_BranchCd3.Enable = false;
	}	

}

