//-------------------------------------------------------------------------------
//   함수명: fn_CommonExcelExport()
//   설명  :  엑셀다운 
//   arguments   
//   obj
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_CommonExcelExport()
{
	var SearchCondition= "";
    
    SearchCondition = SearchCondition + "지사 :" + RPad(cob_BranchCd.Text," ",20);
    SearchCondition = SearchCondition + "인수일계일자 :" + RPad(cal_HandoverYmd1.Text +" ~ "+ cal_HandoverYmd2.Text," ",20);
    gfn_ExportExcel("굴착공사인수인계내역", 5, SearchCondition, "Grid1"); 
	
}

