//-------------------------
// 공통 삭제 버튼 
//-------------------------
function fn_CommonDeleteRow()
{   

	if(ds_list.GetRowCount() > 0) 
	{
		if(confirm("삭제하시겠습니까?"))
		{
			
			cnt=ds_list.GetRowCount()-1;
			for(cnt; cnt >= 0; cnt--)
			{
				if(ds_list.GetColumn(cnt,"CHK")=="1")
				{
					ds_list.DeleteRow(cnt);
				}
			}
			fn_D10_Delete();
		}
	}
	else{
		alert("삭제할 내용이 없습니다.");
	}
	
}

//----------------------------
// 저장 
//---------------------------
function fn_CommonSave()
{   
	if(!ds_list.GetUpdate())
	{
		alert("변동사항이 없습니다.");
		return;
	}
	for(var i=0; i < ds_list.GetRowCount(); i++)
	{  
		if(ds_list.GetColumn(i,"CHK")=="1")
		{   
		    if(ds_list.GetRowType(i)=="insert")
		    {
				var streocs = ds_list.GetColumn(i,"EOCS_NUM");
				var strflag = ds_list.GetColumn(i,"DIG_WORK_PATH_FLAG");
				var strymd  = ds_list.GetColumn(i,"HANDOVER_YMD");
				
				fn_D10_Chk(streocs,strflag,strymd);

				if(ds_chk.GetRowCount() > 0)
				{
					alert(ds_list.GetColumn(i,"EOCS_NUM")+" 공사번호의 인수인계일자가 이미 등록되어 있습니다.");
					return;
				}
		    }
		    
		    var Result = fn_d1_Chkvalue(i);
		    if(Result=="1")
		    {
				return;
		    }
			ds_list.SetColumn(i, "CRT_EMPID", gfn_GetGlobalValue("G_USER_NO"));
	        ds_list.SetColumn(i, "CRT_IP",gfn_GetGlobalValue("G_IPADDRESS"));
		}
		else
		{
			ds_list.UpdateControl=false;
			ds_list.SetRowType(i,"normal");
		}
	}
    fn_D10_Save();
	fn_D10_Serach();
}
 
//-----------------------------------------
//   CHECK 함수 
//-----------------------------------------
function fn_D10_Chk(streocs,strflag,strymd)
{
	var strSvcID = "";
	var strPart = "D10";
	var strURL  = "handle.do?ServiceName=D1_DiggingWorkHandOverMgr-service&chk=1"
	            + "&EOCS_NUM="+ streocs
	            + "&DIG_WORK_PATH_FLAG="+ strflag
	            + "&HANDOVER_YMD="+ strymd
	            ;
	var strInDatasets   = "";
	var strOutDatasets  = "ds_chk=ds_chk";
	var strArgument     = "";

	gfn_SyncCall( 
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument

			   );

	if(ErrorCode < 0)
	{    
		return;
	}
}

  
//-----------------------------------------
//   조회 함수 
//-----------------------------------------
function fn_D10_Serach()
{

    var brcd= cob_BranchCd.VALUE;   
	var strSvcID = "";
	var strPart = "D10";
	
	// 2013-05-27 팽무진 부모창에서 넘겨주는 변수가 없는 상황이 연출 되면 에러가 남. 강제 변수처리.
	var brcd_local = brcd;
	var eocs_num_local =  eocs_num;
	var dig_work_flag_local =  dig_work_flag;

  // 2013-05-27 팽무진 부모창에서 넘겨주는 변수가 없는 상황이 연출 되면 에러가 남. 강제 변수처리.
	var strURL  = "handle.do?ServiceName=D1_DiggingWorkHandOverMgr-service&Search=1"
	            + "&BRANCH_CD="+ brcd_local
	             //+ "&DIG_KIND="+ cob_digWorkPathFlag.Value
	             + "&DIG_KIND="+ cob_digkind.Value
	             + "&JOB_CD="+ cob_jobCd.Value
	             + "&DIG_WORK_PROC_STS="+ cob_digWorkProcSts.Value
	             + "&RECEIVE_YMD1="+ cal_Receive1.Value
	             + "&RECEIVE_YMD2="+ cal_Receive2.Value
	             + "&EOCS_NUM="+ eocs_num_local           //타블렛에서는 안넘어옴
	             + "&DIG_WORK_PATH_FLAG="+ Replace(dig_work_flag_local,"_"," ") //타블렛에서는 안넘어옴  
	            ;
	            
/*
	var strURL  = "handle.do?ServiceName=D1_DiggingWorkHandOverMgr-service&Search=1"
	            + "&BRANCH_CD=1"
	             + "&DIG_KIND=2"
	             + "&JOB_CD=3"
	             + "&DIG_WORK_PROC_STS=0"
	             + "&RECEIVE_YMD1=20120102"
	             + "&RECEIVE_YMD2=20130102"
	             + "EOCS_NUM="+ eocs_num  
	             + "&DIG_WORK_PATH_FLAG=111"
	            ;
*/
	var strInDatasets   = "";
	var strOutDatasets  = "ds_Search=ds_Search ds_list=ds_list";
	var strArgument     = "";

	gfn_SyncCall( 
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument

			   );

	if(ErrorCode < 0)
	{    
		return;
	}
    st_totalcnt.Text = ds_Search.GetRowCount();
}

//---------------------------------------------------------------------------------
// Function : fn_D10_Serach2()
// comment  :  인수인계 공사대상 조회 
// arg      : 
// return   : 
//---------------------------------------------------------------------------------
function fn_D10_Serach2()
{
	var vsts = cob_Sts.Value;
	var digWorkSts1 = "";
	var digWorkSts2 = "";
	var digWorkSts3 = "";
	var digWorkSts4 = "";
	var digWorkSts5 = "";
	var digWorkSts6 = "";
	var digWorkSts7 = "";
	var digWorkSts8 = "";
	var digWorkSts9 = "";
	var digWorkSts10 = "";
	var digWorkSts11 = "";
	if(vsts=="10") // 요청중
	{
		digWorkSts1 = "20";
		digWorkSts2 = "30";
	}
	else if(vsts=="20") // 진행중
	{
		digWorkSts3 = "40";
		digWorkSts4 = "50";
		digWorkSts5 = "60";
		digWorkSts6 = "65";
		digWorkSts7 = "70";
		digWorkSts8 = "80";

	}
	else if(vsts=="30") // 처리완료
	{
		digWorkSts10="90";
	}
	else if(vsts=="40") // 취소
	{
		digWorkSts11="15";
	}
	else if(vsts=="99")
	{
		digWorkSts1 =  "20";
		digWorkSts2 =  "30";
		digWorkSts3 =  "40";
		digWorkSts4 =  "50";
		digWorkSts5 =  "60";
		digWorkSts6 =  "65";
		digWorkSts7 =  "70";
		digWorkSts8 =  "80";
		digWorkSts10 = "90";
		digWorkStS11 = "15";
		
		
	}
	    
    var brcd= cob_BranchCd.VALUE;
	var strSvcID = "";
	var strPart = "D10";
	var strURL  = "handle.do?ServiceName=D1_DiggingWorkHandOverMgr-service&Search1=1"
	            + "&BRANCH_CD="+ brcd
	            + "&DIG_KIND="+ cob_digWorkPathFlag.Value
	            + "&JOB_CD="+ cob_jobCd.Value
	            + "&DIG_WORK_PROC_STS="+ cob_digWorkProcSts.Value
	            + "&RECEIVE_YMD1="+ cal_Receive1.Value
	            + "&RECEIVE_YMD2="+ cal_Receive2.Value
	            + "&DIG_WORK_STS1="+digWorkSts1
	            + "&DIG_WORK_STS2="+digWorkSts2
	            + "&DIG_WORK_STS3="+digWorkSts3
	            + "&DIG_WORK_STS4="+digWorkSts4
	            + "&DIG_WORK_STS5="+digWorkSts5
	            + "&DIG_WORK_STS6="+digWorkSts6
	            + "&DIG_WORK_STS7="+digWorkSts7
	            + "&DIG_WORK_STS8="+digWorkSts8
	            + "&DIG_WORK_STS10="+digWorkSts10
                + "&DIG_WORK_STS11="+digWorkSts11
	            ;
	var strInDatasets   = "";
	var strOutDatasets  = "ds_Search=ds_Search ds_list=ds_list";
	var strArgument     = "";

	gfn_SyncCall( 
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument

			   );

	if(ErrorCode < 0)
	{    
		return;
	}
	else
	{
		
		Grid0.NoDataText = gfn_GetMessage("M000022","%AAA%","조회결과가");
	}
	st_totalcnt.Text = ds_Search.GetRowCount();

}

