//---------------------------------------------------------------------------------
// Function : fn_D10_Save()
// comment  : 인수인계 저장 및 수정 한다.
// arg      : 
// return   : 없음
//---------------------------------------------------------------------------------
function fn_D10_Save()
{  
    var v_UpdatType ="N";
    
    for(var i=0; i<ds_list.GetRowCount();i++)
    {
		if(ds_list.GetRowType(i)=="insert"||ds_list.GetRowType(i)=="update")
		{
			v_UpdatType ="Y";
		}
    }
    if(v_UpdatType=="N")
    {
		return;
    }
	var strSvcID = "";
	var strPart = "D10";
	var strURL  = "handle.do?ServiceName=D1_DiggingWorkHandOverMgr-service&save=1";       
	var strInDatasets   = "ds_list=ds_list:u";
	var strOutDatasets  = "";
	var strArgument     = "";
	
	gfn_SyncCall( 
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument

			   );

	if(ErrorCode < 0)
	{    
		return;
	}else
	{
		alert(gfn_GetMessage("M000009","%AAA%","정상적으로 저장"));
	}
}

//---------------------------------------------------------
// 달력 팝업
//---------------------------------------------------------
function grd_MultiSave_OnExpandEdit(obj,nRow,nCell,strVal,nPivotIndex)
{
	if("HANDOVER_YMD" == Grid1.GetCellProp("body",nCell,"ColId"))
	{
		gfn_SetCalendar(obj, nRow, nCell);
	}
	if("DIG_PLAN_YMD" == Grid1.GetCellProp("body",nCell,"ColId"))
	{
		gfn_SetCalendar(obj, nRow, nCell);
	}
	if("LEAK_YMD" == Grid1.GetCellProp("body",nCell,"ColId"))
	{
		gfn_SetCalendar(obj, nRow, nCell);
	}
}



