// 그리드 클릭시 이벤트 
function Grid1_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	if(nCell=="22")
	{
		var args  = "BUNGI=14" ;
	        args += " EOCS_NUM=" + ds_list.GetColumn(nRow,"EOCS_NUM") ;
	        args += " DIG_WORK_PATH_FLAG=" + ds_list.GetColumn(nRow,"DIG_WORK_PATH_FLAG");
	    
		gfn_Dialog("D10","D1402060011U",args,1208,840,"Scroll",-1,-1);
	}
	if(nCell=="13")
	{
		var retValue = gfn_E2_SearchOutEmplDialog(ds_list.GetColumn(Grid1.ClickedRow,"HANDO_EMPID")); //사용자코드(조회조건용)

		ds_list.SetColumn(Grid1.ClickedRow,"HANDO_EMPID",retValue[0]);
		ds_list.SetColumn(Grid1.ClickedRow,"HANDO_EMPID_NM",retValue[1]);
	}
	
}

