function cob_Sts_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if(cob_Sts.Value=="10")
	{   
		cob_digWorkProcSts.Enable=false;
		cob_digWorkProcSts.Value="";
	}
	else if(cob_Sts.Value=="20")
	{
		cob_digWorkProcSts.Enable=false;
		cob_digWorkProcSts.Value="";
	}
	else if(cob_Sts.Value=="30")
	{
		cob_digWorkProcSts.Enable=false;
		cob_digWorkProcSts.Value="";
	}
	else if(cob_Sts.Value=="40")
	{
		cob_digWorkProcSts.Enable=false;
		cob_digWorkProcSts.Value="";
	}	
	else if(cob_Sts.Value=="99")
	{   
		cob_digWorkProcSts.Enable=true;
	}	
}
