/***************************************************************************************************
*                                          화면 변수 선언부                                        *
***************************************************************************************************/
var fv_FindRow_ShowRow = -1; 		  // 조회후 보여줄 컬럼 체크변수
var fv_FindRow_ShowKey = -1;          // KEY값

var danger_job_sts_flag = "";					//위험작업상태값 구분

var WorkNum = "";
var Chk_Mainter_Num = "";

var fv_COOP_YN = false;  // 협력사여부

/***************************************************************************************************
*                                         공통 Event 처리 부분                                     *
***************************************************************************************************/
//--------------------------------------------------------------------------------------------------
//	OnLoadCompleted
//--------------------------------------------------------------------------------------------------
function D1501010010F_OnLoadCompleted(obj)
{
	gfn_LoadForm(obj);
	fn_Init();
		
	if(gfn_GetGlobalValue("G_COM_ID") == "92") // 시공사-협력사 여부
	{
		fv_COOP_YN = true;
		Image20.Visible = true;
		edt_WorkFirmCdSearch.Visible = true;
		edt_WorkFirmNmSearch.Visible = true;
	}	
	else
	{
		Image20.Visible = false;
		edt_WorkFirmCdSearch.Visible = false;
		edt_WorkFirmNmSearch.Visible = false;	
	}		
			
	edt_DangerNum.Text = "";
	edt_DangerJobStsFlagNm.Text = "";
	fn_D10_CommonNeed_pers();           // 소요일원 초기화
	fn_d10_CommonResetApporval();		// 체크버튼 all false
	
	fn_D10_Common9();					//위험작업내역 false
	fn_D10_Common11();					//검토 false
	
	fn_D10_CommonChgAppor( false );		//변경승인요청 false
	
	btn_Report.Enable = false;
	btn_gis.Enable = false;	

	btn_Change.Enable = false;				// 변경승인버튼 비활성화 
	btn_job_chg.Visible = false;
	btn_Safety.Enable = true;
	//-----------------------------------------
	//  조회조건 초기값 설정
	//-----------------------------------------	
	
 	// 시공사이면
 	if( fv_COOP_YN )
 	{
		btn_Safety.Enable = false;
		cob_BranchCd.Enable = true;
		cob_BranchCd.Value = "";
		cob_BranchCd_OnChanged();
		cob_TeamCdSearch.Enable = true;	
		cob_TeamCdSearch.Value = "";
		edt_WorkFirmCdSearch.Text = gfn_GetGlobalValue("G_DEAL_CD");
		edt_WorkFirmNmSearch.Text = gfn_GetGlobalValue("G_COM_NAME");
 	}
 	else
 	{
		if (gfn_GetGlobalValue("G_BRANCH_CD") == "999999") 
		{
			cob_BranchCd.Enable = true;
			cob_BranchCd.Value = "";
			cob_BranchCd_OnChanged();
			cob_TeamCdSearch.Enable = true;	
			cob_TeamCdSearch.Value = "";
		} 
		else 
		{
			cob_BranchCd.Enable = false;
			cob_BranchCd.Value = gfn_GetGlobalValue("G_BRANCH_CD");
			cob_BranchCd_OnChanged();
			cob_TeamCdSearch.Enable = true;	
			cob_TeamCdSearch.Value = gfn_GetGlobalValue("G_DEPT_CODE");
		}		
 	}
	cal_ReqYmdStart.Value = AddDate(today(),-20);
	cal_ReqYmdEnd.Value = AddDate(today(),+10);
	cob_JobFlagSearch.Value = '';    // 작업종류
	cob_DangerJobStsFlag.Value = ''; // 진행상태

	// ###############################################################################
	// 최종파라미터에 따라 화면을 다르게 구성
	// ###############################################################################
	// BPM 
	if(IsExistVar("FLAG",true))
	{
		// BPM에서 넘어온 화면
		if(FLAG == "BPM")
		{
			fv_FindRow_ShowRow = 1;
			fv_FindRow_ShowKey = DANGER_NUM; //위험작업번호
	
			fn_D1_FindListOne(DANGER_NUM, DANGER_JOB_FLAG);
		}
	}
	
	//포틀릿 EP화면에서 넘어온것
    if (IsExistVar("dangerNumPK", true)) 				
    {
		//alert("dangerNumPK:"+dangerNumPK);
		//alert("dangerJobFlag:"+dangerJobFlag);

		fv_FindRow_ShowRow = 1;
		fv_FindRow_ShowKey = dangerNumPK; //위험작업번호

		fn_D1_FindListOne(dangerNumPK, dangerJobFlag);

	}
}

//--------------------------------------------------------------------------------------------------
//	fn_Init
//--------------------------------------------------------------------------------------------------
function fn_Init()
{
	gfn_D1_All_SetComboBox_Branchcd(gfn_D1_setDsObject_Branchcd("cob_BranchCd"));	

	gfn_D1_SetComboBox_Branchcd(gfn_D1_setDsObject_Branchcd("cob_BranchCd01"));			//지사코드
	gfn_SetComboBox_Comcode("D10002",gfn_setDsObject_Comcode("cob_BefoMaterFlag1")); 		//재질코드 (현재 운영계만 적용)(06.08)
	gfn_SetComboBox_Comcode("D10002",gfn_setDsObject_Comcode("cob_BefoMaterFlag2")); 		//재질코드 (현재 운영계만 적용)(06.08)
	gfn_SetComboBox_Comcode("D10034",gfn_setDsObject_Comcode("cob_PressFlag")); 			//압력구분 
	gfn_SetComboBox_Comcode("D10034",gfn_setDsObject_Comcode("cob_NewPressFlag")); 			//압력구분 
	gfn_D1_NULL_SetComboBox_Comcode("D10218",gfn_setDsObject_Comcode("cob_StopAdvMethod"));
	gfn_SetComboBox_Comcode("D10219",gfn_setDsObject_Comcode("cob_JobMothodCd")); 			//작업방법
	gfn_D1_SetComboBox_Comcode("A10110",gfn_setDsObject_Comcode("cob_WorkFirmTelDdd"),"010#011#016#017#018#019"); 		//시공사 
	gfn_D1_SetComboBox_Comcode("A10110",gfn_setDsObject_Comcode("cob_WorkManagerTelDdd"),"0130#02#031#032#033#041#042#043#0502#0505#051#052#053#054#055#061#062#063#064");  //시공관리자
	gfn_D1_SetComboBox_Comcode("A10110",gfn_setDsObject_Comcode("cob_SiteChiefTelDdd"),"0130#02#031#032#033#041#042#043#0502#0505#051#052#053#054#055#061#062#063#064");    //현장소장
	gfn_SetComboBox_Comcode("D10133",gfn_setDsObject_Comcode("cob_JobFlag"));
	gfn_D1_All_SetComboBox_Comcode("D10133",gfn_setDsObject_Comcode("cob_JobFlagSearch")); 
	
	gfn_SetGrdComboBox_Comcode ("Grid1",4,"D10155");
	
	gfn_SetComboBox_Comcode("A10411",gfn_setDsObject_Comcode("cob_state")); 				//결재진행상태 
	gfn_SetComboBox_Comcode("A10411",gfn_setDsObject_Comcode("cob_chg_state")); 			//변경결재진행상태 
		
	st_chg_rmk.Visible = false;
}

//-------------------------------------------------------------------------------
// 설명     : 공통 조회 
//-------------------------------------------------------------------------------
function fn_CommonSearch()
{
	ds_Multi.ClearData();
	
    
    ds_SearchTop.SetColumn(0,"ZIP_NO1"            , cob_ZipNo1.Value          );  // 시/구
  
    //'팀코드가 200138','200148','200149','200151' 이면 지사코드를 ''으로 넣어준다 20110923 이상목 권기철과장요청
    if(cob_TeamCdSearch.Value == "200138" || cob_TeamCdSearch.Value == "200148" || cob_TeamCdSearch.Value == "200149" || cob_TeamCdSearch.Value == "200151")
    {
		ds_SearchTop.SetColumn(0,"BRANCH_CD"          , ""       );  // 지사
    }else{
		ds_SearchTop.SetColumn(0,"BRANCH_CD"          , cob_BranchCd.Value        );  // 지사
    }
    ds_SearchTop.SetColumn(0,"TEAM_CD"            , cob_TeamCdSearch.Value    );  // 팀
    ds_SearchTop.SetColumn(0,"REQ_YMD_START"      , cal_ReqYmdStart.Value     );  // 작업요청일자
    ds_SearchTop.SetColumn(0,"REQ_YMD_END"        , cal_ReqYmdEnd.Value       );  // 작업요청일자
    ds_SearchTop.SetColumn(0,"JOB_NM"             , edt_JobNm.Text            );  // 작업명
	ds_SearchTop.SetColumn(0,"JOB_FLAG"           , cob_JobFlagSearch.Value   );  // 작업종류
	ds_SearchTop.SetColumn(0,"DANGER_JOB_STS_FLAG", cob_DangerJobStsFlag.Value);  // 진행상태
	ds_SearchTop.SetColumn(0,"WORK_FIRM_CD"       , edt_WorkFirmCdSearch.Text );  // 시공사

	
	var strSvcID = "findList";
	var strPart = "D10";	
	var strURL = "handle.do?ServiceName=D1_DangerWorkRequirEMgr-service";  
	var strInDatasets  = "ds_SearchTop=ds_SearchTop";
	var strOutDatasets = "ds_Multi=ds_Multi";
	var strArgument  = "searchList=1";
	
	gfn_SyncCall( 
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
			   );
			   
	edt_Count.Text = ds_Multi.GetRowCount();		   
}

//--------------------------------------------------------------------------------------------------
// 위험작업 내역 변경시
//--------------------------------------------------------------------------------------------------
function ds_Multi_OnRowPosChanged(obj,nOldRow,nRow)
{
	//trace("ds_Multi_OnRowPosChanged - " +nRow +" "+danger_num);
	st_chg_rmk.Visible = false;
	ds_Chk.ClearData();
	
	if(nRow < 0) 
	{
		//로우 없을경우 초기화
		ds_Multi_Re.ClearData(); // 위험작업내역등록
		
		ds_Grid1.ClearData();    // 시간대별작업계획
		
		// 안전장비
		fn_D10_Common21();
		// SCG안전장비
		fn_D10_Common22(); 
		
		ds_Multi_Re2.ClearData(); // 작업수행 및 비상연락망
		
		ds_Grid3.ClearData();     // 가스방출량
		ds_Multi_Re4.ClearData(); // 작업결과
	
		ds_Multi_Re.ClearData();  // 결재/반려
		ds_Multi_Re_chg.ClearData(); // 변경승인요청
		
		// 소요인원
		need_pers50.Text = "";
		need_pers40.Text = "";
		need_pers30.Text = "";
		need_pers20.Text = "";
		need_pers10.Text = "";
	}
	else
	{		
		fn_D1_SearchDetail(); 		// 위험작업 상세 조회	
	}
}

//--------------------------------------------------------------------------------------------------
// 상세조회 - 위험작업내역 상세조회 
//--------------------------------------------------------------------------------------------------
function fn_D1_SearchDetail()
{	
	ds_Search.ClearData();
	ds_Search.AddRow();
	ds_Multi_Re.ClearData();
	ds_Grid1.ClearData();
	ds_Gost.ClearData();
	ds_Grid3.ClearData();
	ds_DangerEqup02.ClearData();
	ds_DangerEqupScg02.ClearData();
	ds_Multi_Ok_chg.ClearData();
	
	ds_Search.SetColumn(0,"DANGER_NUM"      , ds_Multi.GetColumn(ds_Multi.Row, "DANGER_NUM"));  // 위험작업번호
	ds_Search.SetColumn(0,"DANGER_JOB_FLAG"      , ds_Multi.GetColumn(ds_Multi.Row, "DANGER_JOB_FLAG"));  // 위험작업번호

	// var strSvcID = "findDetail";
	// var strPart = "D10";	
	// var strURL = "handle.do?ServiceName=D1_DangerWorkRequirEMgr-service";  
	// var strInDatasets  = "ds_Search=ds_Search";
	// var strOutDatasets = "ds_Multi_Re=ds_Multi_Re ds_Multi_Ok_chg=ds_Multi_Ok_chg ds_Grid1=ds_Grid1 ds_Gost=ds_Gost ds_Grid3=ds_Grid3 ds_DangerEqup02=ds_DangerEqup02 ds_DangerEqupScg02=ds_DangerEqupScg02";
	// var strArgument    = "searchDetail=1";
		// strArgument	  += " DANGER_NUM=" + quote(ds_Multi.GetColumn(ds_Multi.Row, "DANGER_NUM"));
		// strArgument	  += " DANGER_JOB_FLAG=" + quote(ds_Multi.GetColumn(ds_Multi.Row, "DANGER_JOB_FLAG"));
	// var strCallbackFunc = "fn_D1_CallBack";	
		
	var strSvcID = "findDetail";
	var strPart = "D10";	
	var strURL = "handle.do?ServiceName=D1_DangerWorkRequirEMgr-service";  
	var strInDatasets  = "ds_Search=ds_Search";
	var strOutDatasets = "ds_Multi_Re=ds_Multi_Re ds_Multi_Ok_chg=ds_Multi_Ok_chg ds_Grid1=ds_Grid1 ds_Gost=ds_Gost ds_Grid3=ds_Grid3 ds_DangerEqup02=ds_DangerEqup02 ds_DangerEqupScg02=ds_DangerEqupScg02";
	var strArgument  = "searchDetail=1";
	var strCallbackFunc = "fn_D1_CallBack";	
			
	gfn_AsyncCall( 
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
				   , strCallbackFunc
			   );
	
}

//--------------------------------------------------------------------------------------------------
//	서비스 CallBack
//--------------------------------------------------------------------------------------------------
function fn_D1_CallBack(SrvID,ErrorCode,ErrorMsg)
{
	if(ErrorCode < 0) {
	
		alert(ErrorMsg);
		//에러시 초기화
		fv_FindRow_ShowRow = -1;
		return;
	}
	
    switch(SrvID){

		//상세 조회
        case "findDetail":
        	//scg담당자 선택시 팀정보 가져오는 콤보
        	
        	// 2013.06.26. 이건민 - 권기철 과장님 요청사항으로 주석처리
            //gfn_D1_SetComboBox_Teamcd_all("ds_Team_01",cob_BranchCd01);
            
            // 2013.06.26. 이건민 - 권기철 과장님 요청사항(지사 선택 시 팀정보에 공사관리팀, 수요개발팀 빼기)
            gfn_D1_SetComboBox_Teamcd("ds_Team_01",cob_BranchCd01);
            
			// 첨부화일
			fn_File_List();
			
			cob_BefoMaterFlag1.Value = ds_Multi_Re.GetColumn(0, "BEFO_MATER_FLAG");
			cob_BefoMaterFlag2.Value = ds_Multi_Re.GetColumn(0, "NEW_MATER_FLAG" );
			
			if(!gfn_IsNull(ds_Multi_Re.GetColumn(0, "BEFO_MATER_FLAG"))){
				cob_BefoMaterFlag_OnChanged(cob_BefoMaterFlag1);
			}
			
			if(!gfn_IsNull(ds_Multi_Re.GetColumn(0, "NEW_DIAM_CD"))){
				cob_BefoMaterFlag_OnChanged(cob_BefoMaterFlag2);
			}
	        
			// 소요인원 
			if(ds_Gost.GetRowCount() > 0) 
			{
				need_pers10.Text = ds_Gost.GetColumn(0,"PERS");
				need_pers20.Text = ds_Gost.GetColumn(1,"PERS");
				need_pers30.Text = ds_Gost.GetColumn(2,"PERS");
				need_pers40.Text = ds_Gost.GetColumn(3,"PERS");
				need_pers50.Text = ds_Gost.GetColumn(4,"PERS");
				ds_Grid2.SetColumn(0,"TYPE","1");
				ds_Grid2.SetColumn(1,"TYPE","1");
				ds_Grid2.SetColumn(2,"TYPE","1");
				ds_Grid2.SetColumn(3,"TYPE","1");
				ds_Grid2.SetColumn(4,"TYPE","1");
				
			}
			
			// 안전장비 
			ds_DangerEqup.SetColumn(0,"EA1",ds_DangerEqup02.GetColumn(0,"EQUIP_QTY")); 
			ds_DangerEqup.SetColumn(0,"EA2",ds_DangerEqup02.GetColumn(1,"EQUIP_QTY")); 
			ds_DangerEqup.SetColumn(1,"EA1",ds_DangerEqup02.GetColumn(2,"EQUIP_QTY")); 
			ds_DangerEqup.SetColumn(1,"EA2",ds_DangerEqup02.GetColumn(3,"EQUIP_QTY")); 
			ds_DangerEqup.SetColumn(2,"EA1",ds_DangerEqup02.GetColumn(4,"EQUIP_QTY")); 
			ds_DangerEqup.SetColumn(2,"EA2",ds_DangerEqup02.GetColumn(5,"EQUIP_QTY")); 
			ds_DangerEqup.SetColumn(3,"EA1",ds_DangerEqup02.GetColumn(6,"EQUIP_QTY")); 
			ds_DangerEqup.SetColumn(3,"EA2",ds_DangerEqup02.GetColumn(7,"EQUIP_QTY")); 	
			
			// SCG안전장비
			ds_DangerEqupScg.SetColumn(0,"EA1",ds_DangerEqupScg02.GetColumn(0,"EQUIP_QTY")); 
			ds_DangerEqupScg.SetColumn(0,"EA2",ds_DangerEqupScg02.GetColumn(1,"EQUIP_QTY")); 
			ds_DangerEqupScg.SetColumn(1,"EA1",ds_DangerEqupScg02.GetColumn(2,"EQUIP_QTY")); 
			ds_DangerEqupScg.SetColumn(1,"EA2",ds_DangerEqupScg02.GetColumn(3,"EQUIP_QTY")); 
			ds_DangerEqupScg.SetColumn(2,"EA1",ds_DangerEqupScg02.GetColumn(4,"EQUIP_QTY")); 
			ds_DangerEqupScg.SetColumn(2,"EA2",ds_DangerEqupScg02.GetColumn(5,"EQUIP_QTY")); 
			ds_DangerEqupScg.SetColumn(3,"EA1",ds_DangerEqupScg02.GetColumn(6,"EQUIP_QTY")); 
			ds_DangerEqupScg.SetColumn(3,"EA2",ds_DangerEqupScg02.GetColumn(7,"EQUIP_QTY")); 
			ds_DangerEqupScg.SetColumn(4,"EA1",ds_DangerEqupScg02.GetColumn(8,"EQUIP_QTY"));
	
			fn_D1_OnRowPosChanged();
        
			// 로우선택하기
			if(fv_FindRow_ShowRow > 0)
			{
				var nFocusRow = ds_Multi.FindRow("DANGER_NUM",fv_FindRow_ShowKey);
				if (nFocusRow != -1)
				{
					ds_Multi.row = nFocusRow;
				}	
				fv_FindRow_ShowRow = -1;
			}
        	break;	
        	
		//리스트 (위험작업번호로 조회)
        case "findListOne":
        
			if( ds_Multi.GetRowCount() > 0)
			{
				alert("위험작업 데이터가 조회 되었습니다.");
			}
			else
			{
				alert("위험작업 데이터가 존재하지 않습니다.");				
			}
			
        	break;	        

        default:
    }
}

//-------------------------------------------------------------------------------
// 위험작업번호로만  조회
//-------------------------------------------------------------------------------
function fn_D1_FindListOne(strDANGER_NUM, strDANGER_JOB_FLAG)
{	
	ds_Multi.ClearData();
	
	ds_SearchTop.ClearData();
	ds_SearchTop.AddRow();
    ds_SearchTop.SetColumn(0,"DANGER_NUM"      , strDANGER_NUM      );  // 위험작업번호
    ds_SearchTop.SetColumn(0,"DANGER_JOB_FLAG" , strDANGER_JOB_FLAG );  // 위험작업구분
    
	var strSvcID = "findListOne";
	var strPart = "D10";	
	var strURL = "handle.do?ServiceName=D1_DangerWorkRequirEMgr-service";  
	var strInDatasets  = "ds_SearchTop=ds_SearchTop";
	var strOutDatasets = "ds_Multi=ds_Multi";
	var strArgument  = "searchOne=1";
	var strCallbackFunc = "fn_D1_CallBack";	
	
	gfn_AsyncCall( 
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
				   , strCallbackFunc
			   );
}

function fn_D1_OnRowPosChanged()
{	
// 위험작업 요청전	00
// 위험작업 요청	01
// 반려	            02
// 변경요청	        03
// 승인         	04
// 결재반려	        05
// 변경요청반려	    06
// 결재승인	        07
// 변경요청승인	    08
// 변경요청승인	    10
// 변경요청승인	    90
	//############################################################################
	// 모든화면을 막는다.
	//############################################################################

	btn_gis.Enable = true;              // GIS 위치보기
	Btn_Danger_Req.Enable = false; 		// 위험작업요청버튼
	
	// 시간대별 작업계획
	btn_RowAdd01.Enable = false;		//Grid1 추가버튼  9, 10
	btn_RowDel01.Enable = false;		//Grid1 삭제버튼	

	// ----------------------------------------------------------------------------
	// SCG
	// ----------------------------------------------------------------------------
	//btn_ChangeFlag.Enable = false;      // 위험작업 요청내용변경 
	
	// 가스방출량
	btn_RowAdd.Enable = false; 			//Grid4 추가버튼  11. 12
	btn_RowDel.Enable = false; 			//Grid4 추가버튼
			
	btn_Valuation.Enable = false; 		//업체평가
	btn_approval.Enable = false; 		//전자결재버튼(결재처리)
	btn_Report.Enable = false;			//장표버튼
	
	cal_ChgApproReqYmd.Enable = false; // 변경승인일자
	edt_ChgApproWhy.Enable = false;    // 변경사유
	
	fn_D10_CommonJobResult(false);      // 6. 작업결과
	fn_D10_CommonChgAppor( false );     // 변경승인요청 비활성화
	
	fn_d10_CommonResetApporval();       // 검토 초기화
	fn_D10_Common9();	                // 위험작업내역 비활성화
	fn_D10_Common11();					// 검토  비활성화
	
	// 데이터 바인딩
	fn_D10_Common2();                   // 작업수행 및 비상연락망, 공급중단내역, 검토, 
	fn_D10_Common1();                   // 변경승인요청
	fn_D10_Common4();                   // 6. 작업결과	
	
	brn_work_save.Enable = false;
	btn_Change.Enable = false;
	btn_job_chg.Visible = false;
	edt_cancel_rmk.Enable = true;
	Btn_cancel.Enable = true;
	
	//############################################################################
	// 권한에 따라서 화면을 풀어준다.
	//############################################################################
	
	if(ds_Multi.GetColumn(ds_Multi.Row, "DANGER_JOB_STS_FLAG") == "00" || ds_Multi.GetColumn(ds_Multi.Row, "DANGER_JOB_STS_FLAG") == "02")	// 위험작업 요청전,검토 반려
	{
		if( fv_COOP_YN || gfn_GetGlobalValue("G_USER_NO") == "199443297") // 협력사이면 수정가능
		{
			fn_D10_Common10();	 			//위험작업내역 등록 활성화
			cal_JobReqWmd1.Enable = true;
			Btn_Danger_Req.Enable = true; 	//위험작업요청버튼
		}
		edt_cancel_rmk.Enable = false;	
		st_chg_rmk.Visible = false;
	}	
	// else if(gfn_GetGlobalValue("G_USER_NO") == "199443297")
	// {
		// edt_cancel_rmk.Enable = false;	
		// st_chg_rmk.Visible = false;	
	// }
	else if ( fv_COOP_YN )
	{
		edt_cancel_rmk.Enable = false;	
		st_chg_rmk.Visible = false;
	}
	else if(ds_Multi.GetColumn(ds_Multi.Row, "DANGER_JOB_STS_FLAG") == "01")  // 위험작업요청
	{
		//trace("ds_Multi_OnRowPosChanged - " +ds_Multi.Row +" "+danger_num);	
		fn_D10_Common10();	 			//위험작업내역 등록 활성화
		
		cal_JobReqWmd1.Enable = false;
		fn_D10_Common12();	           // 검토 true
		Check_02.Enable = true; // 반려
		btn_approval.Enable = true; // 전자결재버튼(결재처리)
		
		edt_ReviewOpni.Enable = true; // 검토의견
		btn_job_chg.Visible = true;
		
		cob_JobFlag.Enable = false; 	//위험작업요청버튼
		btn_WorkNumButton.Enable = false; 	//위험작업요청버튼
		btn_ScgCharge.Enable = false; 	//위험작업요청버튼
	}else if(ds_Multi.GetColumn(ds_Multi.Row, "DANGER_JOB_STS_FLAG") == "04")	//검토 승인
	{
		btn_approval.Enable = true; // 전자결재버튼(결재처리)
		btn_Report.Enable = true;   // 장표
	}else if(ds_Multi.GetColumn(ds_Multi.Row, "DANGER_JOB_STS_FLAG") == "05")	//전자 결재 반려
	{
		fn_D10_Common10();	 			//위험작업내역 등록 활성화
		cal_JobReqWmd1.Enable = false;		
		fn_D10_Common12();					//검토 true
		//검토사항 제외시킴 2010.12.6
		//Check_01.Enable = true;
		Check_02.Enable = true;
		btn_approval.Enable = true; // 전자결재버튼(결재처리)
		edt_ReviewOpni.Enable = true; // 검토의견	
		
		cob_JobFlag.Enable = false; 	//위험작업요청버튼
		btn_WorkNumButton.Enable = false; 	//위험작업요청버튼
		btn_ScgCharge.Enable = false; 	//위험작업요청버튼	
		btn_job_chg.Visible = true;		
	}else if(ds_Multi.GetColumn(ds_Multi.Row, "DANGER_JOB_STS_FLAG") == "07")	//전자 결재 승인
	{
		//변경 검토가능시 변경일자 등록 및 사유등록2010.12.7
		cal_ChgApproReqYmd.Enable = true; // 변경승인일자
		edt_ChgApproWhy.Enable = true;    // 변경사유
		fn_D10_CommonJobResult(true);	 // 6. 작업결과 작성 활성화
		btn_Report.Enable = true;
		btn_Valuation.Enable = true; // 업체평가 
		brn_work_save.Enable = true;
		btn_Change.Enable = true;
	}else if(ds_Multi.GetColumn(ds_Multi.Row, "DANGER_JOB_STS_FLAG") == "03") // 변경요청
	{
			cal_ChgApproReqYmd.Enable = true; // 변경승인일자
			edt_ChgApproWhy.Enable = true;    // 변경사유
			btn_Change.Enable = true;
	}else if(ds_Multi.GetColumn(ds_Multi.Row, "DANGER_JOB_STS_FLAG") == "06")    // 변경요청반려 (06)
	{
			cal_ChgApproReqYmd.Enable = true; // 변경승인일자
			edt_ChgApproWhy.Enable = true;    // 변경사유
			btn_Change.Enable = true;
	}else if(ds_Multi.GetColumn(ds_Multi.Row, "DANGER_JOB_STS_FLAG") == "08")       // 변경요청완료(08)
	{
			fn_D10_CommonJobResult(true);	 // 6. 작업결과 작성 활성화 
			btn_Valuation.Enable = true; // 업체평가 
			btn_Report.Enable = true;	
			brn_work_save.Enable = true;


	// 2012.08.28 이건민 - 권기철 과장님 추가 요청사항 (작업결과내용은 수정가능 하도록 변경요청)
	}else if(ds_Multi.GetColumn(ds_Multi.Row, "DANGER_JOB_STS_FLAG") == "10")       // 변경요청완료(08)
	{
		brn_work_save.Enable = true;
		edt_JobResult.Enable = true;
	}
	
	/*
	else if(ds_Multi.GetColumn(ds_Multi.Row, "DANGER_JOB_STS_FLAG") == "10" || ds_Multi.GetColumn(ds_Multi.Row, "DANGER_JOB_STS_FLAG") == "90")       // 공사완료(10),취소(90)
	{
		edt_cancel_rmk.Enable = false;
		Btn_cancel.Enable = false;
	}
	*/
	//2012.04.23 권기철 과장님 추가요청_이건민
	else if(ds_Multi.GetColumn(ds_Multi.Row, "DANGER_JOB_STS_FLAG") == "00" || ds_Multi.GetColumn(ds_Multi.Row, "DANGER_JOB_STS_FLAG") == "02" || ds_Multi.GetColumn(ds_Multi.Row, "DANGER_JOB_STS_FLAG") == "10" || ds_Multi.GetColumn(ds_Multi.Row, "DANGER_JOB_STS_FLAG") == "90")       // 공사완료(10),취소(90)
	{
		edt_cancel_rmk.Enable = false;
		Btn_cancel.Enable = false; 
	}
}	


//--------------------------------------------------------------------------------------------------
// 첨부화일 조회
//--------------------------------------------------------------------------------------------------
function fn_File_List()
{  

	ds_FileList.ClearData();
	var strWORK_FLAG = "10";
	
	var strSvcID = "findFile";
	var strPart = "D10";	
	var strURL = "handle.do?ServiceName=D1_DangerWorkRequirEMgr-service";  
	var strInDatasets  = "";
	var strOutDatasets = "ds_FileList=ds_FileList";
	var strArgument    = "findFile=1";
	    strArgument   += " DANGER_NUM=" + quote(ds_Multi_Re.GetColumn(0,"DANGER_NUM"));
		strArgument	  += " DANGER_JOB_FLAG=" + quote(ds_Multi_Re.GetColumn(0,"DANGER_JOB_FLAG"));
		strArgument	  += " WORK_FLAG=" +quote(strWORK_FLAG);
 	
	gfn_SyncCall( 
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
			   );

	edtFileList.text = "";
	for(var i=0; i<ds_FileList.RowCount(); i++)
	{   
		edtFileList.text += ds_FileList.GetColumn(i,"ORG_FILE_NAME");
		if(ds_FileList.RowCount()-1 != i) edtFileList.text += "/";
	}
}

//-------------------------------------------------------------------------------
// 함수명   : fn_CommonAddRow()
// 설명     : 공통 추가 
// return 내용 : 
//-------------------------------------------------------------------------------
function fn_CommonAddRow()
{   

	if(!fv_COOP_YN)
	{
		// 권기철 과장님설정
		if(gfn_GetGlobalValue("G_USER_NO") != "199443297" && gfn_GetGlobalValue("G_USER_NO") != "700004008")
		{
			alert(" 시공사만 등록 가능합니다.");
			return;
		}	
	}
	
	if( gfn_IsNull( cob_BranchCd.Value ) )
	{
		alert("지사를 먼저 선택하시기 바랍니다.");
		cob_BranchCd.SetFocus();
		return;
	}

	fn_D10_Common10();					//위험작업내역 true
	
	cal_JobReqWmd1.Enable = true;
    edt_DangerNum.Text = "";  			// 새로 등록할 위험작업번호 안보이기
    cob_TeamCd.Enable = false;			// 팀 비활성화
	cob_BefoPipeDiamCd1.Enable = false;
	cob_BefoPipeDiamCd2.Enable = false;

	btn_gis.Enable = false;             // GIS 위치보기
	Btn_Danger_Req.Enable = false; 		// 위험작업요청버튼
	btn_Safety.Enable = false; 			// 안전수칙
	btn_approval.Enable = false; 		// 전자결재버튼(결재처리)
	//권기철 2010.12.3
	//btn_Change.Enable = false; 			// 변경승인요청버튼		
	btn_Report.Enable = false;			// 장표버튼
	//btn_DangerBpm.Enable = false;		// bpm결과 버튼

	ds_Multi_Re.ClearData();
	ds_Multi_Re.AddRow();
	
	edt_DangerJobStsFlag.Text = "00";
	edt_DangerJobStsFlagNm.Text = "위험작업요청전";

    ds_DangerEqup02.ClearData();			//조회시 가져온 장비수량 초기화
    ds_DangerEqupScg02.ClearData();

	ds_Grid1.ClearData();					// 시간대별 작업계획
	
	for(var i=0; i<5; i++)					// 위험작업소요인원
	{
		ds_Grid2.SetColumn(i,"TYPE",0);
	}
	
	ds_Grid3.ClearData();					// 가스방출량
	
	fn_D10_CommonNeed_pers();           // 소요일원 초기화
	fn_D10_Common6();
	fn_d10_CommonResetApporval();		// 체크버튼 all false
	
	fn_D10_Common7();
	fn_d10_CommonResetApporval();
	fn_D10_Common21();						//위험작업장비 화면초기화
	fn_D10_Common22();
	
	gfn_SetGrdComboBox_Comcode ("Grid1",4,"D10155");	
	
	edtFileList.Text = "";
	
	edt_ReqEmpid.Text = gfn_GetGlobalValue("G_USER_NO");
	edt_ReqNm.Text = gfn_GetGlobalValue("G_NAME");

    //업체정보 공사번호 선택시 적용에서 추가번튼 클릭시 적용변경 2010.12.6
	edt_WorkFirmCd.Text     = gfn_GetGlobalValue("G_DEAL_CD"); // 업체코드
	edt_WorkFirmCdNm.Text   = gfn_GetGlobalValue("G_COM_NAME");  // 업체명

	cob_BranchCd01.Value = cob_BranchCd.Value;  // 지사코드
	cob_BranchCd01_OnChanged();
}	

//-------------------------------------------------------------------------------
//	공통 버튼 클릭 - 삭제
//-------------------------------------------------------------------------------
function fn_CommonDeleteRow()
{
	if(ds_Multi.RowCount() == 0)
	{
		return;
	}

	if(ds_Multi.GetColumn(ds_Multi.Row, "DANGER_JOB_STS_FLAG")  == "00" || ds_Multi.GetColumn(ds_Multi.Row, "DANGER_JOB_STS_FLAG")  == "02")
	{
		if(confirm("등록된 위험작업 내역을 삭제하시겠습니까?"))
		{
			ds_Multi.DeleteRow(ds_Multi.row);
			
			var strSvcID        = "delete";
			var strPart         = "D10";
			var strURL          = "handle.do?ServiceName=D1_DangerWorkRequirEMgr-service";  
			var strInDatasets   = "ds_Multi=ds_Multi:u";
			var strOutDatasets  = "";
			var strArgument     = "delete=1";	
			//var strCallbackFunc = "fn_D1_CallBack";
			
			gfn_SyncCall(strSvcID,
					  strPart,
					  strURL,
					  strInDatasets,
					  strOutDatasets,
					  strArgument,
					 );
					 
			if(ErrorCode < 0)
			{    
				alert(ErrorMsg);
				return;
			}
			else
			{
				gfn_SetStatusMsg(gfn_GetMessage("M000009","%AAA%","정상적으로 삭제를"),"black",true); 
				alert(gfn_GetMessage("M000009","%AAA%","정상적으로 삭제를"));
				fn_CommonSearch();
			}	
		}		
	}
	else
	{
		// alert("진행상태가 위험작업요청전 상태만 삭제가 가능합니다. 시스템관리자에게 문의바랍니다. ");
		// 2012.04.23. 권기철 과장님 추가요청_이건민
		alert("진행상태가 위험작업요청전, 검토반려 상태만 삭제가 가능합니다. 시스템관리자에게 문의바랍니다. ");
		return;
	} 
}
//-------------------------------------------------------------------------------
// 함수명   : fn_CommonSave()
// 설명     : 공통 저장
// return 내용 : 
//-------------------------------------------------------------------------------
function fn_CommonSave()
{
	// if(!ds_Multi_Re.GetUpdate()||!ds_Grid1.GetUpdate()||!ds_Grid2.GetUpdate()||!ds_DangerEqup02.GetUpdate())
	// {
		// return;
	// }
	danger_job_sts_flag = edt_DangerJobStsFlag.Text;
	
// 위험작업 요청전	00
// 위험작업 요청	01
// 반려	            02
// 변경요청	        03
// 승인         	04
// 결재반려	        05
// 변경요청반려	    06
// 결재승인	        07
// 변경요청승인	    08	
// 취소     	    99
	
	
	if( fv_COOP_YN ) // 협력사이면 수정가능
	{
		if(danger_job_sts_flag == "00" || danger_job_sts_flag == "02")
		{
		}
		else
		{
			alert("[시공사] 저장할 수 없습니다. 수정가능한 진행상태까지 기다려 주십시오.");
			return;
		}
	}
	else // SCG
	{
		if((danger_job_sts_flag == "00" || danger_job_sts_flag == "02") && gfn_GetGlobalValue("G_USER_NO") == "199443297" 
		  || (danger_job_sts_flag == "00" || danger_job_sts_flag == "02") && gfn_GetGlobalValue("G_USER_NO") == "700004008")
		{	
			//alert("권기철 시공사 권한과 현 진행상태에서 동일");
		}
		else if(danger_job_sts_flag == "01" || danger_job_sts_flag == "05")
		{
		}
		else	
		{		
			alert("[SCG] 저장할 수 없습니다. 수정가능한 진행상태까지 기다려 주십시오. \n- <<  >>");		
			return;
		}
	}	
	
	if(danger_job_sts_flag == "00" || danger_job_sts_flag == "02") // 요청전(00), 반려(02)
	{
		var chk01 = fn_D10_Validation01();
		if(chk01 == "1")
		{
			return;
		}
		else
		{
			if(fn_D10_Save1()== "0") //위험작업 작업내용	
			{
				gfn_SetStatusMsg(gfn_GetMessage("M000009","%AAA%","[1]정상적으로 저장을"),"black",true); 
				alert( gfn_GetMessage("M000009","%AAA%","[1]정상적으로 저장을") );
				fn_CommonSearch();	
			}	
		}


	}else if(danger_job_sts_flag == "01") // 요청(01)
	{
		alert("작업의뢰내역 수정은 아래 작업의뢰 수정 버튼을 이용 바랍니다..");
		return;
	}else if(danger_job_sts_flag == "04")	// 검토 승인
	{
		alert("검토 승인되어 내역 수정이 불가능합니다..");
		return;
	}else if(danger_job_sts_flag == "05")	// 전자결재 반려 
	{
		alert("작업의뢰내역 수정은 아래 작업의뢰 수정 버튼을 이용 바랍니다..");
		return;	
		// var chk01 = fn_D10_Validation02();
		// if(chk01 == "1")
		// {
			// return;
		// }
		// else
		// {
			// if(Confirm("저장시 자동 검토 처리됩니다. 저장 하시겠습니까? ") == true)
			// {
				// fn_D10_Save2(); //도시가스 위험작업장비 등록
			// }
			// else
			// {
				// return;
			// }	
		// }
		
	}else if(danger_job_sts_flag == "07")	// 전자결재 승인 
	{
		alert("작업결과 등록은 아래 작업 결과등록 옆의 저장 버튼을 이용바랍니다..");
		return;		
	}else if(danger_job_sts_flag == "03")	// 변경승인 요청 
	{

	}else if(danger_job_sts_flag == "06")	// 변경요청반려 
	{

	}else if(danger_job_sts_flag == "08")	// 변경요청승인
	{
		alert("작업결과 등록은 아래 작업 결과등록 옆의 저장 버튼을 이용바랍니다..");
		return;
	}else if(danger_job_sts_flag == "10")	// 공사완료
	{
		alert("작업이 완료 되었습니다...");
		return;
	}else if(danger_job_sts_flag == "90")	// 취소건
	{
		alert("작업 취소건입니다...");
		return;
	}		
}

//-------------------------------------------------------------------------------
// 함수명   : fn_max()
// 설명     : 위험작업번호의 max 값 조회
// return 내용 : 
//-------------------------------------------------------------------------------
function fn_max()
{
    
    ds_Max.ClearData();
    
	var strSvcID = "find_max";
	var strPart = "D10";	
	var strURL = "handle.do?ServiceName=D1_DangerWorkRequirEMgr-service";  
	var strInDatasets  = "";
	var strOutDatasets  = "ds_Max=ds_Max";
	var strArgument  = "max=1";
	
	gfn_SyncCall( 
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
			   );
}
//-------------------------------------------------------------------------------
// 함수명   : fn_D10_Save1()
// 설명     : 위험작업내역 등록
// return 내용 : 
//-------------------------------------------------------------------------------
function fn_D10_Save1()
{

	if(ds_Multi_Re.GetRowType(0) == "insert")
	{
		//위험작업번호 max 값 불러오기	
		fn_max();
		var strMax = ds_Max.GetColumn(0, "MAX");
		ds_Multi_Re.SetColumn(0,"DANGER_NUM",strMax);
	}
	
	
	ds_Multi_Re.SetColumn(0,"DANGER_JOB_STS_FLAG",edt_DangerJobStsFlag.Text);

	ds_Multi_Re.SetColumn(0,"UPD_IP",gfn_GetGlobalValue("G_IPADDRESS")); 
	ds_Multi_Re.SetColumn(0,"UPD_EMPID",gfn_GetGlobalValue("G_USER_NO"));
	ds_Multi_Re.SetColumn(0,"CRT_IP",gfn_GetGlobalValue("G_IPADDRESS")); 
	ds_Multi_Re.SetColumn(0,"CRT_EMPID",gfn_GetGlobalValue("G_USER_NO"));
	
	ds_Multi_Re.SetColumn(0,"DANGER_JOB_FLAG","10");

	
	// 1. 작업요청시간 
	var strSTR_TIME = ds_Grid1.GetColumn(0, "JOB_STR_TIME");                    // 제일 첫번째
	var strEND_TIME = ds_Grid1.GetColumn(ds_Grid1.RowCount()-1, "JOB_END_TIME");  // 제일 마지막

	ds_Multi_Re.SetColumn(0,"JOB_REQ_STR_TIME", strSTR_TIME); // 작업요청시간 (시작)
	ds_Multi_Re.SetColumn(0,"JOB_REQ_END_TIME", strEND_TIME); // 작업요청시간 (종료)


	if( gfn_IsNull( edt_JobReqStartTime.Value ) )
	{
		alert(gfn_GetMessage("M000015","%AAA%","작업요청시작시간은 "));
		return 1;
	}
	if( gfn_IsNull( edt_JobReqEndTime.Value ) )
	{
		alert(gfn_GetMessage("M000015","%AAA%","작업요청종료시간은 "));
		return 1;
	}

	// 2. 시간대별 작업계획
	for(var i=0; i<ds_Grid1.RowCount(); i++)
	{	
		ds_Grid1.SetColumn(i,"DANGER_JOB_FLAG","10");
		
		// 추가된 로우 셋팅
		if (ds_Grid1.GetRowType(i) == "insert") {

			ds_Grid1.SetColumn(i, "DANGER_NUM",  ds_Multi_Re.GetColumn(0,"DANGER_NUM"));
			ds_Grid1.SetColumn(i, "CRT_EMPID",   gfn_GetGlobalValue("G_USER_NO"));
			ds_Grid1.SetColumn(i, "CRT_IP",      gfn_GetLocalIpAddress()); 
			ds_Grid1.SetColumn(i, "UPD_EMPID",   gfn_GetGlobalValue("G_USER_NO"));	
			ds_Grid1.SetColumn(i, "UPD_IP",      gfn_GetLocalIpAddress()); 
		}
		
		// 수정된 로우 셋팅
		if (ds_Grid1.GetRowType(i) == "update") {

			ds_Grid1.SetColumn(i, "UPD_EMPID", gfn_GetGlobalValue("G_USER_NO"));
			ds_Grid1.SetColumn(i, "UPD_IP",    gfn_GetLocalIpAddress());
		
		}	// update end	

	}

	// 3. 시간대별 작업계획
	ds_Grid2.UpdateControl = false;
	
	for(var i=0; i<ds_Grid2.RowCount(); i++)
	{
		grid_type = ds_Grid2.GetColumn(i,"TYPE");
		
		if(grid_type == "1"){
			ds_Grid2.SetRowType(i,"update");
		}else{
			ds_Grid2.SetRowType(i,"insert");
		}
		
		var strPersCnt = "";
		if(i==0){
			strPersCnt = need_pers50.Text;
		}else if(i==1){
			strPersCnt = need_pers40.Text;
		}else if(i==2){
			strPersCnt = need_pers30.Text;
		}else if(i==3){
			strPersCnt = need_pers20.Text;
		}else if(i==4){
			strPersCnt = need_pers10.Text;
		}
		
		ds_Grid2.SetColumn(i,"PERS",strPersCnt);
		ds_Grid2.SetColumn(i,"DANGER_NUM",ds_Multi_Re.GetColumn(0,"DANGER_NUM"));
		ds_Grid2.SetColumn(i,"DANGER_JOB_FLAG","10");
		ds_Grid2.SetColumn(i,"CRT_IP",gfn_GetGlobalValue("G_IPADDRESS")); 
		ds_Grid2.SetColumn(i,"CRT_EMPID",gfn_GetGlobalValue("G_USER_NO"));
		ds_Grid2.SetColumn(i,"UPD_IP",gfn_GetGlobalValue("G_IPADDRESS")); 
		ds_Grid2.SetColumn(i,"UPD_EMPID",gfn_GetGlobalValue("G_USER_NO"));
	}

	fn_D10_DangerEqup(); //안전장비(값 넘겨주기)
	
	// 4. 안전장비
	
	for(var i=0; i<ds_DangerEqup01.RowCount(); i++)
	{
		if(ds_DangerEqup02.GetColumn(i,"EQUIP_QTY") == "" ||ds_DangerEqup02.GetColumn(i,"EQUIP_QTY") == null){
			ds_DangerEqup02.SetRowType(i,"insert");
			ds_DangerEqup02.AddRow();
		}else{
			ds_DangerEqup02.SetRowType(i,"update");
		}	
		ds_DangerEqup02.SetColumn(i,"EQUIP_QTY",ds_DangerEqup01.GetColumn(i,"EQUIP_QTY"));
		ds_DangerEqup02.SetColumn(i,"EQUIP_CD",ds_DangerEqup01.GetColumn(i,"EQUIP_CD"));
			
		if(ds_DangerEqup02.GetRowType(i) == "insert"){  
				ds_DangerEqup02.SetColumn(i,"DANGER_NUM",ds_Multi_Re.GetColumn(0,"DANGER_NUM"));
		}			
		ds_DangerEqup02.SetColumn(i,"DANGER_JOB_FLAG","10");
		ds_DangerEqup02.SetColumn(i,"EQUIP_USER_FLAG","01");	//작업장비(시공사)
		ds_DangerEqup02.SetColumn(i,"CRT_IP",gfn_GetGlobalValue("G_IPADDRESS")); 
		ds_DangerEqup02.SetColumn(i,"CRT_EMPID",gfn_GetGlobalValue("G_USER_NO"));
		ds_DangerEqup02.SetColumn(i,"UPD_IP",gfn_GetGlobalValue("G_IPADDRESS")); 
		ds_DangerEqup02.SetColumn(i,"UPD_EMPID",gfn_GetGlobalValue("G_USER_NO"));
	}
	
	fv_FindRow_ShowRow = 1;
	fv_FindRow_ShowKey = ds_Multi_Re.GetColumn(0,"DANGER_NUM"); //위험작업번호

	var strSvcID = "fn_D10_Save1";
	var strPart = "D10";
	var strURL = "handle.do?ServiceName=D1_DangerWorkRequirEMgr-service";
	var strInDatasets  = "ds_Multi_Re=ds_Multi_Re:u ds_Grid1=ds_Grid1:u ds_Grid2=ds_Grid2:u ds_DangerEqup02=ds_DangerEqup02:u";
	var strOutDatasets  = "";
	var strArgument  = "save=1";	
	
	gfn_SyncCall(
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
				  );
				  
	if(ErrorCode < 0)
	{    
		alert(ErrorMsg);
		return 1;
	}else{
		return 0;	
	}
}

function fn_D10_DangerEqup()
{	
	ds_DangerEqup01.SetColumn(0,"EQUIP_QTY",Grid2.GetCellValue(0,1));
	ds_DangerEqup01.SetColumn(1,"EQUIP_QTY",Grid2.GetCellValue(0,3));
	ds_DangerEqup01.SetColumn(2,"EQUIP_QTY",Grid2.GetCellValue(1,1));
	ds_DangerEqup01.SetColumn(3,"EQUIP_QTY",Grid2.GetCellValue(1,3));
	ds_DangerEqup01.SetColumn(4,"EQUIP_QTY",Grid2.GetCellValue(2,1));
	ds_DangerEqup01.SetColumn(5,"EQUIP_QTY",Grid2.GetCellValue(2,3));
	ds_DangerEqup01.SetColumn(6,"EQUIP_QTY",Grid2.GetCellValue(3,1));
	ds_DangerEqup01.SetColumn(7,"EQUIP_QTY",Grid2.GetCellValue(3,3));	
}
//-------------------------------------------------------------------------------
// 함수명   : fn_D10_Save2()
// 설명     : SCG 위험작업내역 등록
// return 내용 : 
//-------------------------------------------------------------------------------	
function fn_D10_Save2()
{	
	ds_Multi_Re2.UpdateControl = false;
	ds_Multi_Re2.SetRowType(0,"update");

	ds_Multi_Re2.SetColumn(0,"UPD_IP",gfn_GetGlobalValue("G_IPADDRESS"));
	ds_Multi_Re2.SetColumn(0,"UPD_EMPID",gfn_GetGlobalValue("G_USER_NO"));     
	ds_Multi_Re2.SetColumn(0,"DANGER_NUM",ds_Multi_Re.GetColumn(0,"DANGER_NUM"));
	ds_Multi_Re2.SetColumn(0,"DANGER_JOB_FLAG","10");
	
	ds_Multi_Re2.SetColumn(0,"DANGER_JOB_STS_FLAG","04");
	ds_Multi_Re2.SetColumn(0,"REVIEW_FLAG","Y");	
	
	ds_Multi_Re2.SetColumn(0,"REVIEW_EMPID",gfn_GetGlobalValue("G_USER_NO"));
	ds_Multi_Re2.SetColumn(0,"REVIEW_EMPID_NM",gfn_GetGlobalValue("G_NAME"));
	ds_Multi_Re2.SetColumn(0,"REVIEW_YMD",today());	
	
	// SCG담당자, 팀 변경 불가능하게 2010.12.6
	// ds_Multi_Re2.SetColumn(0,"TEAM_CD",    cob_TeamCd.Value);
	// ds_Multi_Re2.SetColumn(0,"TAKER_EMPID",edt_Taker_EmpId.Text);


	// 1. 작업요청시간 
	var strSTR_TIME = ds_Grid1.GetColumn(0, "JOB_STR_TIME");                      // 제일 첫번째
	var strEND_TIME = ds_Grid1.GetColumn(ds_Grid1.RowCount()-1, "JOB_END_TIME");  // 제일 마지막

	ds_Multi_Re.SetColumn(0,"JOB_REQ_STR_TIME", strSTR_TIME); // 작업요청시간 (시작)
	ds_Multi_Re.SetColumn(0,"JOB_REQ_END_TIME", strEND_TIME); // 작업요청시간 (종료)
	
	ds_Multi_Re2.SetColumn(0,"JOB_REQ_STR_TIME", strSTR_TIME); // 작업요청시간 (시작)
	ds_Multi_Re2.SetColumn(0,"JOB_REQ_END_TIME", strEND_TIME); // 작업요청시간 (종료)


	if( gfn_IsNull( edt_JobReqStartTime.Value ) )
	{
		alert(gfn_GetMessage("M000015","%AAA%","작업요청시작시간은 "));
		return;
	}
	if( gfn_IsNull( edt_JobReqEndTime.Value ) )
	{
		alert(gfn_GetMessage("M000015","%AAA%","작업요청종료시간은 "));
		return;
	}

	// 2. 시간대별 작업계획
	for(var i=0; i<ds_Grid1.RowCount(); i++)
	{	
		// 수정된 로우 셋팅
		if (ds_Grid1.GetRowType(i) == "update") {

			ds_Grid1.SetColumn(i, "UPD_EMPID", gfn_GetGlobalValue("G_USER_NO"));
			ds_Grid1.SetColumn(i, "UPD_IP",    gfn_GetLocalIpAddress());
		
		}	// update end	

	}

	// 위험작업 가스 방출량 (INSERT & UPDATE)
	for( var i=0; i<ds_Grid3.RowCount(); i++ ){
			
		// 추가된 로우 셋팅
		if (ds_Grid3.GetRowType(i) == "insert") {

			ds_Grid3.SetColumn(i, "DANGER_NUM",  ds_Multi_Re.GetColumn(0,"DANGER_NUM"));
			ds_Grid3.SetColumn(i, "DANGER_JOB_FLAG","10");
			//ds_Grid3.SetColumn(i,"GAS_RELAEASE_MANAGE_NUM",gas_max);
			ds_Grid3.SetColumn(i, "CRT_EMPID",   gfn_GetGlobalValue("G_USER_NO"));
			ds_Grid3.SetColumn(i, "CRT_IP",      gfn_GetLocalIpAddress()); 
			ds_Grid3.SetColumn(i, "UPD_EMPID",   gfn_GetGlobalValue("G_USER_NO"));	
			ds_Grid3.SetColumn(i, "UPD_IP",      gfn_GetLocalIpAddress()); 
		}
		
		// 수정된 로우 셋팅
		if (ds_Grid3.GetRowType(i) == "update") {

			ds_Grid3.SetColumn(i, "UPD_EMPID", gfn_GetGlobalValue("G_USER_NO"));
			ds_Grid3.SetColumn(i, "UPD_IP",    gfn_GetLocalIpAddress());
		
		}	// update end	
	}
	
	fn_D10_DangerEqupScg(); //SCG안전장비(값 넘겨주기)
	
	for(var i=0; i<ds_DangerEqupScg01.RowCount(); i++)
	{
		if(ds_DangerEqupScg02.GetColumn(i,"EQUIP_QTY") == "" ||ds_DangerEqupScg02.GetColumn(i,"EQUIP_QTY") == null){
			ds_DangerEqupScg02.SetRowType(i,"insert");
			ds_DangerEqupScg02.AddRow();
		}else{
			ds_DangerEqupScg02.SetRowType(i,"update");
		}	
		
		ds_DangerEqupScg02.SetColumn(i,"EQUIP_QTY",ds_DangerEqupScg01.GetColumn(i,"EQUIP_QTY"));
		ds_DangerEqupScg02.SetColumn(i,"EQUIP_CD",ds_DangerEqupScg01.GetColumn(i,"EQUIP_CD"));
		
		if(ds_DangerEqupScg02.GetRowType(i) == "insert"){  
			ds_DangerEqupScg02.SetColumn(i,"DANGER_NUM",ds_Multi_Re.GetColumn(0,"DANGER_NUM"));
		}			
		ds_DangerEqupScg02.SetColumn(i,"DANGER_JOB_FLAG","10");
		ds_DangerEqupScg02.SetColumn(i,"EQUIP_USER_FLAG","02");		//작업장비(SGC)
		ds_DangerEqupScg02.SetColumn(i,"CRT_IP",gfn_GetGlobalValue("G_IPADDRESS")); 
		ds_DangerEqupScg02.SetColumn(i,"CRT_EMPID",gfn_GetGlobalValue("G_USER_NO"));
		ds_DangerEqupScg02.SetColumn(i,"UPD_IP",gfn_GetGlobalValue("G_IPADDRESS")); 
		ds_DangerEqupScg02.SetColumn(i,"UPD_EMPID",gfn_GetGlobalValue("G_USER_NO"));				
	}
	
	fv_FindRow_ShowRow = 1;
	fv_FindRow_ShowKey = ds_Multi_Re.GetColumn(0,"DANGER_NUM"); //위험작업번호
	
	var strSvcID = "fn_D10_Save2";
	var strPart = "D10";
	var strURL = "handle.do?ServiceName=D1_DangerWorkRequirEMgr-service";
	var strInDatasets  = "ds_Multi_Re2=ds_Multi_Re2:u ds_Grid1=ds_Grid1:u ds_Grid3=ds_Grid3:u ds_DangerEqupScg02=ds_DangerEqupScg02:u";
	var strOutDatasets  = "";
	var strArgument  = "save2=1";	
	
	gfn_SyncCall(
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
				  );
				  
				  
				  
	if(ErrorCode < 0)
	{    
		alert(ErrorMsg);
		return;
	}else{
		alert("정상적으로 검토처리되었습니다..");
		fn_CommonSearch();	
	}		
}
function fn_D10_DangerEqupScg()
{	
	ds_DangerEqupScg01.SetColumn(0,"EQUIP_QTY",Grid3.GetCellValue(0,1));
	ds_DangerEqupScg01.SetColumn(1,"EQUIP_QTY",Grid3.GetCellValue(0,3));
	ds_DangerEqupScg01.SetColumn(2,"EQUIP_QTY",Grid3.GetCellValue(1,1));
	ds_DangerEqupScg01.SetColumn(3,"EQUIP_QTY",Grid3.GetCellValue(1,3));
	ds_DangerEqupScg01.SetColumn(4,"EQUIP_QTY",Grid3.GetCellValue(2,1));
	ds_DangerEqupScg01.SetColumn(5,"EQUIP_QTY",Grid3.GetCellValue(2,3));
	ds_DangerEqupScg01.SetColumn(6,"EQUIP_QTY",Grid3.GetCellValue(3,1));
	ds_DangerEqupScg01.SetColumn(7,"EQUIP_QTY",Grid3.GetCellValue(3,3));	
	ds_DangerEqupScg01.SetColumn(8,"EQUIP_QTY",Grid3.GetCellValue(4,1));
}	




//-------------------------------------------------------------------------------
// 설명     : 위험작업 요청(버튼 클릭시 상태값 강제 01) 변경
//-------------------------------------------------------------------------------
function Btn_Danger_Req_OnClick(obj)
{   
	danger_job_str_flag = ds_Multi_Re.GetColumn(0,"DANGER_JOB_STS_FLAG");

	if(danger_job_str_flag == "00" || danger_job_str_flag == "02")
	{	
		if(Confirm("위험작업 요청을 하시겠습니까? ") == true)
		{
			ds_Multi_Re.UpdateControl = false;
			ds_Multi_Re.SetRowType(0,"update");
			
			ds_Multi_Re.SetColumn(0,"DANGER_JOB_STS_FLAG","01");
			ds_Multi_Re.SetColumn(0,"UPD_IP",gfn_GetGlobalValue("G_IPADDRESS"));   
			ds_Multi_Re.SetColumn(0,"UPD_EMPID",gfn_GetGlobalValue("G_USER_NO"));   
			ds_Multi_Re.SetColumn(0,"DANGER_NUM",ds_Multi_Re.GetColumn(0,"DANGER_NUM"));
			ds_Multi_Re.SetColumn(0,"DANGER_JOB_FLAG","10");
				
			fv_FindRow_ShowRow = 1;
			fv_FindRow_ShowKey = ds_Multi_Re.GetColumn(0,"DANGER_NUM"); //위험작업번호
	
	
			// BPM Call 
			var strDANGER_NUM      = ds_Multi_Re.GetColumn(0, "DANGER_NUM");
			var strDANGER_JOB_FLAG = ds_Multi_Re.GetColumn(0, "DANGER_JOB_FLAG");
			var strBRANCH_CD       = cob_BranchCd01.Value;
			var strBRANCH_CD_NM    = cob_BranchCd01.Text;
			var strTEAM_CD         = cob_TeamCd.Value;
			var strTEAM_CD_NM      = cob_TeamCd.Text;
			var strTAKER_EMPID     = ds_Multi_Re.GetColumn(0, "TAKER_EMPID");
			var strLOT_NUM_UNION   = ds_Multi_Re.GetColumn(0, "LOT_NUM_UNION");
			var strJOB_NM          = ds_Multi_Re.GetColumn(0, "JOB_NM");
			var strWORK_FIRM_NM    = ds_Multi_Re.GetColumn(0, "WORK_FIRM_NM");
			var strWORK_NUM        = ds_Multi_Re.GetColumn(0, "WORK_NUM");
			
			// 공백처리
			if(gfn_isNull( strBRANCH_CD )) strBRANCH_CD = " ";
			if(gfn_isNull( strBRANCH_CD_NM )) strBRANCH_CD_NM = " ";
			if(gfn_isNull( strTEAM_CD )) strTEAM_CD = " ";
			if(gfn_isNull( strTEAM_CD_NM )) strTEAM_CD_NM = " ";
			
			if(gfn_isNull( strLOT_NUM_UNION )) strLOT_NUM_UNION = " ";
			if(gfn_isNull( strJOB_NM )) strJOB_NM = " ";
			if(gfn_isNull( strWORK_FIRM_NM )) strWORK_FIRM_NM = " ";
			if(gfn_isNull( strWORK_NUM )) strWORK_NUM = " ";
		
			var strSvcID = "updateflag";
			var strPart = "D10";
			var strURL = "handle.do?ServiceName=D1_DangerWorkRequirEMgr-service";
			var strInDatasets  = "ds_Multi_Re=ds_Multi_Re:u";
			var strOutDatasets = "";
			var strArgument  = "updateflag=1";	
			
			gfn_SyncCall(
							 strSvcID
						   , strPart
						   , strURL
						   , strInDatasets
						   , strOutDatasets
						   , strArgument
						  );
						  
			if(ErrorCode < 0)
			{    
				alert(ErrorMsg);
				return;
			}else{
				gfn_SetStatusMsg(gfn_GetMessage("M000009","%AAA%","[2]정상적으로 저장을"),"black",true); 
				fn_CommonSearch();	
			}			
		}else{
			return;
		}
	}else{
		alert("이미 위험작업 요청을 하였습니다.");
		return;
	}	
}

//-------------------------------------------------------------------------------
// 설명     : 변경승인 요청
//-------------------------------------------------------------------------------
function btn_Change_OnClick(obj)
{
	if (ds_Multi.GetColumn(ds_Multi.row,"DANGER_JOB_STS_FLAG") == '10')
	{
		alert("작업 완료건으로 일자변경이 불가합니다.");
		return;
	}
	else if (ds_Multi.GetColumn(ds_Multi.row,"DANGER_JOB_STS_FLAG") == '90')
	{
		alert("취소건으로 일자변경이 불가합니다..");
		return;
	}	
		
	if(ds_Multi_Ok_chg.GetColumn(0, "DANGER_JOB_STS_FLAG") == "03")
	{
		if(cob_chg_state.Value == '0' || cob_chg_state.Value == '1' || cob_chg_state.Value == '2')
		{
			alert("현재 [" + cob_chg_state.Text + "] 입니다...");
			return;
		}
	}
	
	if ((gfn_GetGlobalValue("G_USER_NO") != "199443297") && (gfn_GetGlobalValue("G_DEPT_NAME") != cob_TeamCd.Text))
	{
		alert("작업일자 변경 처리는 " + cob_TeamCd.Text + "팀원만 가능합니다.");
		return;
	}		
	
	if( gfn_IsNull(cal_ChgApproReqYmd.Value) )
	{
		alert("변경 작업일자를 입력하여 주세요.");
		cal_ChgApproReqYmd.SetFocus();
		return;
	}
			
	if( gfn_IsNull(edt_ChgApproWhy.Text) )
	{
		alert("작업일자 변경사유를 입력하여 주세요.");
		edt_ChgApproWhy.SetFocus();
		return;
	}

	 //변경작업일자
	fv_FindRow_ShowRow = 1;
	fv_FindRow_ShowKey = ds_Multi_Re.GetColumn(0,"DANGER_NUM");	 //위험작업번호
	//03변경요청
	//06변경결재 반려
	//07결재 승인
	
	if(ds_Multi_Re.GetColumn(0,"DANGER_JOB_STS_FLAG") == '03' || ds_Multi_Re.GetColumn(0,"DANGER_JOB_STS_FLAG") == '06' || ds_Multi_Re.GetColumn(0,"DANGER_JOB_STS_FLAG") == '07')
	{	
		if(ds_Multi_Ok_chg.RowCount() >0)
		{
			ds_Multi_Ok_chg.UpdateControl = false;
			ds_Multi_Ok_chg.SetRowType(0,"update");		
		}
		else
		{
			ds_Multi_Ok_chg.ClearData();
			ds_Multi_Ok_chg.AddRow();
			ds_Multi_Ok_chg.UpdateControl = false;
			ds_Multi_Ok_chg.SetRowType(0,"insert");			
		}
		
		ds_Multi_Re_chg.UpdateControl = false;
		ds_Multi_Re_chg.SetRowType(0,"update");
		
		ds_Multi_Re_chg.SetColumn(0,"DANGER_JOB_STS_FLAG","03");
		ds_Multi_Re_chg.SetColumn(0,"CHG_APPRO_REQ_FLAG","Y"); // 변경 체크 할경우 Y 값 강제 넣기
		ds_Multi_Re_chg.SetColumn(0,"UPD_IP",gfn_GetGlobalValue("G_IPADDRESS")); 
		ds_Multi_Re_chg.SetColumn(0,"UPD_EMPID",gfn_GetGlobalValue("G_USER_NO"));
		ds_Multi_Re_chg.SetColumn(0,"DANGER_NUM",ds_Multi_Re.GetColumn(0,"DANGER_NUM"));
		ds_Multi_Re_chg.SetColumn(0,"DANGER_JOB_FLAG","10");
	
		ds_Multi_Re_chg.SetColumn(0,"CHG_APPRO_REQ_YMD",cal_ChgApproReqYmd.Value); 
		ds_Multi_Re_chg.SetColumn(0,"CHG_APPRO_WHY",edt_ChgApproWhy.text); 

		ds_Multi_Ok_chg.SetColumn(0,"CRT_IP",gfn_GetGlobalValue("G_IPADDRESS")); 
		ds_Multi_Ok_chg.SetColumn(0,"CRT_EMPID",gfn_GetGlobalValue("G_USER_NO"));		
		ds_Multi_Ok_chg.SetColumn(0,"UPD_IP",gfn_GetGlobalValue("G_IPADDRESS")); 
		ds_Multi_Ok_chg.SetColumn(0,"UPD_EMPID",gfn_GetGlobalValue("G_USER_NO"));
		ds_Multi_Ok_chg.SetColumn(0,"DANGER_NUM",ds_Multi_Re.GetColumn(0,"DANGER_NUM"));
		ds_Multi_Ok_chg.SetColumn(0,"DANGER_JOB_FLAG","10");
	
		var strSvcID = "chg_danger";
		var strPart = "D10";
		var strURL = "handle.do?ServiceName=D1_DangerWorkRequirEMgr-service";
		var strInDatasets  = "ds_Multi_Re_chg=ds_Multi_Re_chg:u ds_Multi_Ok_chg=ds_Multi_Ok_chg:u";
		var strOutDatasets  = "";
		var strArgument  = "chg_danger=1";	
		
		gfn_SyncCall(
						 strSvcID
					   , strPart
					   , strURL
					   , strInDatasets
					   , strOutDatasets
					   , strArgument
					  );
					  
		if(ErrorCode < 0)
		{    
			alert(ErrorMsg);
			return;
		}				  
	}
	else
	{
		alert("변경 요청 작업단계가 아닙니다..");
		return;
	}	
	
	cal_ChgApproReqYmd.Enable = false;
	edt_ChgApproWhy.Enable = false;
	btn_Change.Enable = false;
	ds_Multi.SetColumn(ds_Multi.row,"DANGER_JOB_STS_FLAG" , '03');
	
	//전자결재 처리필요...	
	var dangernum        = ds_Multi.GetColumn(ds_Multi.row,"DANGER_NUM");
	var dangerjobflag    = ds_Multi.GetColumn(ds_Multi.row,"DANGER_JOB_FLAG");
	var dangerjobstsflag = ds_Multi.GetColumn(ds_Multi.row,"DANGER_JOB_STS_FLAG");
	gfv_callGWInfo("fmD37","d10","",2,dangernum+','+dangerjobflag);
}

//------------------------------------------------------------------------------------------------------------------------------------------
// 함수명   : fn_update2()
// 설명     : 접수,검토시 update (반려시 02)
// return 내용 : 
//-------------------------------------------------------------------------------
function fn_return()
{   
	ds_Multi_Re2.UpdateControl = false;
	ds_Multi_Re2.SetRowType(0,"update");

	ds_Multi_Re2.SetColumn(0,"UPD_IP",gfn_GetGlobalValue("G_IPADDRESS"));
	ds_Multi_Re2.SetColumn(0,"UPD_EMPID",gfn_GetGlobalValue("G_USER_NO"));     
	ds_Multi_Re2.SetColumn(0,"DANGER_NUM",ds_Multi_Re.GetColumn(0,"DANGER_NUM"));
	ds_Multi_Re2.SetColumn(0,"DANGER_JOB_FLAG","10");
	
	ds_Multi_Re2.SetColumn(0,"REVIEW_EMPID",gfn_GetGlobalValue("G_USER_NO"));
	ds_Multi_Re2.SetColumn(0,"REVIEW_EMPID_NM",gfn_GetGlobalValue("G_NAME"));
	ds_Multi_Re2.SetColumn(0,"REVIEW_YMD",today());
	//반려 상태 적용 2010.12.6
	//'02': 반려
	ds_Multi_Re2.SetColumn(0,"DANGER_JOB_STS_FLAG","02");
	ds_Multi_Re2.SetColumn(0,"REVIEW_FLAG","N");
	
	fv_FindRow_ShowRow = 1;
	fv_FindRow_ShowKey = ds_Multi_Re.GetColumn(0,"DANGER_NUM"); //위험작업번호		
		
	var strDANGER_JOB_FLAG = ds_Multi_Re.GetColumn(0,"DANGER_JOB_FLAG");
	var strJOB_REQ_YMD1    = ds_Multi_Re.GetColumn(0,"JOB_REQ_YMD1");
	
	var strSvcID = "return";
	var strPart = "D10";
	var strURL = "handle.do?ServiceName=D1_DangerWorkRequirEMgr-service";
	var strInDatasets  = "ds_Multi_Re2=ds_Multi_Re2:u";
	var strOutDatasets  = "";
	var strArgument  = "return=1";
	
	gfn_SyncCall(
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
				  );
				  
	if(ErrorCode < 0)
	{    
		alert(ErrorMsg);
		return;
	}else{
		alert("시공업체로 반려처리 되었습니다.."); 		
		fn_CommonSearch();	
	}				  
}

