function cob_BranchCd_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex) 
{
    fn_SetComboBox_Comcode("1", cob_BranchCd.value, fn_setDsObject_Comcode("1", "cob_ZipNo1"));
    cob_ZipNo1.Value = "";
    
	// 2013.06.26. 이건민 - 권기철 과장님 요청사항으로 주석처리
    //gfn_D1_SetComboBox_Teamcd_all("ds_Team", cob_BranchCd);
    
    // 2013.06.26. 이건민 - 권기철 과장님 요청사항(지사 선택 시 팀정보에 공사관리팀, 수요개발팀 빼기)
    gfn_D1_SetComboBox_Teamcd("ds_Team", cob_BranchCd);
    
    cob_TeamCdSearch.Value = "";

	ds_Multi.ClearData();
}
//-------------------------------------------------------------------------------
// 설명     :   시간대별 작업계획 중복 체크
//-------------------------------------------------------------------------------
function ds_Grid1_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if(strColumnID == "DANGER_JOB_PLAN_CD")
	{
		var DangerJobPlanCd = ds_Grid1.GetColumn(ds_Grid1.row,"DANGER_JOB_PLAN_CD");
		
		for(var i=0; i < ds_Grid1.GetRowCount(); i++)
		{
			if( DangerJobPlanCd == ds_Grid1.GetColumn(i, "DANGER_JOB_PLAN_CD") && i != ds_Grid1.row )
			{
				alert("이미 작성된 작업내용 입니다. 재입력 해주세요");
				ds_Grid1.SetColumn(ds_Grid1.row,"DANGER_JOB_PLAN_CD","");
				return;
			}
		}		
	}
	
	if(strColumnID == 'JOB_STR_TIME')
	{
	
		if(ds_Grid1.GetColumn(nRow, "JOB_STR_TIME") == '' || ds_Grid1.GetColumn(nRow, "JOB_STR_TIME") == null){
			return;
		}
		
		if(ds_Grid1.GetColumn(nRow, "JOB_STR_TIME") > "2400"){
			alert("24:00 이상 입력할 수 없습니다. " );
			ds_Grid1.SetColumn(nRow, "JOB_STR_TIME", '');
			return;
		}
		
		/*
		if( length( ds_Grid1.GetColumn(nRow, "JOB_END_TIME") ) > 0 ) {
			if(ds_Grid1.GetColumn(nRow, "JOB_STR_TIME") > ds_Grid1.GetColumn(nRow, "JOB_END_TIME")){
				alert("작업계획 종료시간보다 클 수 없습니다. ");
				ds_Grid1.SetColumn(nRow, "JOB_STR_TIME", '');
				return;
			}
		}*/
	}
	
	if(strColumnID == 'JOB_END_TIME'){
				
		if(ds_Grid1.GetColumn(nRow, "JOB_END_TIME") == '' || ds_Grid1.GetColumn(nRow, "JOB_END_TIME") == null){
			return;
		}
		
		if(ds_Grid1.GetColumn(nRow, "JOB_END_TIME") > "2400"){
			alert("24:00 이상 입력할 수 없습니다.  ");
			ds_Grid1.SetColumn(nRow, "JOB_END_TIME", '');
			return;
		}
		/*
		if( length( ds_Grid1.GetColumn(nRow, "JOB_STR_TIME") ) > 0){		
			if(ds_Grid1.GetColumn(nRow, "JOB_STR_TIME") > ds_Grid1.GetColumn(nRow, "JOB_END_TIME")){
				alert("작업계획 시작시간보다 작을 수 없습니다. ");
				ds_Grid1.SetColumn(nRow, "JOB_END_TIME", '');
				return;
			}
		}
		*/
	}	
}

		
