	{
		ds_Grid3.AddRow();
	}
	else
	{
		alert("검토 처리되어 방출량 등록이 불가능합니다...");
		return;
	}		
}

function btn_RowDel_OnClick(obj)
{
	// var diam = ds_Grid3.GetColumn(ds_Grid3.RowCount()-1,"DIAM");
	// var leng = ds_Grid3.GetColumn(ds_Grid3.RowCount()-1,"LENG");
	// var pipepress = ds_Grid3.GetColumn(ds_Grid3.RowCount()-1,"PIPE_PRESS");
	// var gasreleaseqty = ds_Grid3.GetColumn(ds_Grid3.RowCount()-1,"GAS_RELEASE_QTY");
	
	// if(ds_Grid3.GetRowType(ds_Grid3.RowCount()-1) == "insert")
	// {
		// if((diam==""||diam==null) && (leng==""||leng==null) && (pipepress==""||pipepress==null) && (gasreleaseqty==""||gasreleaseqty==null))
		// {
			// ds_Grid3.DeleteRow(ds_Grid3.RowCount()-1);
		// }else if(confirm("작성된 내용이 있습니다. 그래도 삭제하시겠습니까?"))
		// {
			// ds_Grid3.DeleteRow(ds_Grid3.RowCount()-1);
		// }
	// }
	//검토자 등록여부 체크 후 검토사항없는 경우 삭제 가능보완	
	
	// else 2010.3.12 권기철 제외함
	// {
		// if(ds_Grid3.RowCount() > 0)
		// {
			// alert("기존 저장된 값은 삭제가 불가능합니다.");
		// }
	// }
	
	if (ds_Grid3.GetRowCount() == 0)
	{
		return;
	}
		
	if(ds_Grid3.GetRowType(ds_Grid3.row) == "insert")
	{
		ds_Grid3.DeleteRow(ds_Grid3.row);
		return;
	}

	if (trim(edt_ReviewEmpidNm.text) == '' || length(trim(edt_ReviewEmpidNm.text)) == '0') 
	{
		if(confirm("삭제하시겠습니까?"))
		{
			ds_Grid3.DeleteRow(ds_Grid3.row);
	
			var strSvcID         = "del_gas";
			var strPart          = "D10";	
			var strURL           = "handle.do?ServiceName=D1_DangerWorkRequirEMgr-service";					
			var strInDatasets    = "ds_Grid3=ds_Grid3:u";
			var strOutDatasets   = "";		
			var strArgument      = "del_gas=1";
		
			gfn_SyncCall(
							 strSvcID
						   , strPart  	 
						   , strURL
						   , strInDatasets
						   , strOutDatasets
						   , strArgument
					   );
		
			if(ErrorCode < 0)
			{
				return;
			}
			else
			{
				alert(gfn_GetMessage("M000009","%AAA%","방출량 내역이 정상적으로 삭제를"));
			}		
		}
		else
		{
			return;
		}	
	}
	else	
	{	
		alert("검토 처리되어 삭제가 불가합니다...");
		return;
	}	
	
}

// 시간대별 작업계획 추가
function btn_RowAdd01_OnClick(obj)
{

	if( ds_Multi_Re.GetRowCount() <= 0 )
	{
		alert("추가버튼을 누르시고 다시 입력바랍니다.");
		return;
	}
	
	ds_Grid1.AddRow();
	if(ds_Grid1.GetRowCount() == 1){
		return;
	}else{
		ds_Grid1.SetColumn(ds_Grid1.row,"JOB_STR_TIME",ds_Grid1.GetColumn(ds_Grid1.row-1,"JOB_END_TIME"));
	}	
}


// 시간대별 작업계획 삭제
function btn_RowDel01_OnClick(obj)
{

	if( ds_Multi_Re.GetRowCount() <= 0 )
	{
		alert("삭제할 대상이 없습니다.");
		return;
	}

	var strtime = ds_Grid1.GetColumn(ds_Grid1.RowCount()-1,"JOB_STR_TIME");
	var endtime = ds_Grid1.GetColumn(ds_Grid1.RowCount()-1,"JOB_END_TIME");
	var dangerJPC = ds_Grid1.GetColumn(ds_Grid1.RowCount()-1,"DANGER_JOB_PLAN_CD");
	var rmk = ds_Grid1.GetColumn(ds_Grid1.RowCount()-1,"RMK");
	
	if(ds_Grid1.GetRowType(ds_Grid1.RowCount()-1) == "insert")
	{
		if((strtime==""||strtime==null) && (endtime==""||endtime==null) && (dangerJPC==""||dangerJPC==null) && (rmk==""||rmk==null))
		{
			ds_Grid1.DeleteRow(ds_Grid1.row);
		}else if(confirm("작성된 내용이 있습니다. 그래도 삭제하시겠습니까?"))
		{
			ds_Grid1.DeleteRow(ds_Grid1.row);
		}
	}else if(confirm("등록된 작업계획을 삭제하시겠습니까?"))
	{
		fn_D1_WorkJobDelete(ds_Grid1.row);
	}
}
//----------------------------------------------------------------------------------------------
// 함수명: fn_D1_WorkJobDelete()
// 설명:  작업계획 삭제 
// arguments
// return 내용: 없음
//----------------------------------------------------------------------------------------------
function fn_D1_WorkJobDelete(row)
{    
	ds_Grid1.DeleteRow(row);
	
	var strSvcID = "fn_D1_WorkJobDelete";
	var strPart = "D10";
	var strURL = "handle.do?ServiceName=D1_DangerWorkRequirEMgr-service";  
	var strInDatasets  = "ds_Grid1=ds_Grid1:u";
	var strOutDatasets  = "";
	var strArgument  = "deletePlan=1";	
	
	gfn_SyncCall(
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
				  );
				  
	if(ErrorCode < 0)
	{    
		return;
	}else{
		gfn_SetStatusMsg(gfn_GetMessage("M000009","%AAA%","정상적으로 삭제를"),"black",true); 
		alert(gfn_GetMessage("M000009","%AAA%","정상적으로 삭제를")); 
		
	}					  
}
