// 기      능   :  가스방출량 계산한 값 가져오기  
//-------------------------------------------------------------------------------
function Grid4_OnExpandEdit(obj,nRow,nCell,strVal,nPivotIndex)
{
	// var args = "DIAM=" + ds_Grid3.GetColumn(nRow,"DIAM")
			 // + " LENG=" + ds_Grid3.GetColumn(nRow,"LENG")
			 // + " PIPE_PRESS=" + ds_Grid3.GetColumn(nRow,"PIPE_PRESS");

	var result = gfn_Dialog("D10","D1502010011U","",450,400,true,-1,-1); 

	if( length( result ) > 0 )
	{
		ds_Grid3.SetColumn( nRow, "DIAM",           result[0]);
		ds_Grid3.SetColumn( nRow, "LENG",           result[1]);
		ds_Grid3.SetColumn( nRow, "PIPE_PRESS",     result[2]);
		ds_Grid3.SetColumn( nRow, "GAS_RELEASE_QTY",result[3]);
	}
	
	alert("방출량 정보는 상단의 저장을 클릭하셔야 저장됩니다...");
}	
	
//-------------------------------------------------------------------------------
// 기      능   :  안전수칙 보기
//-------------------------------------------------------------------------------
function btn_Safety_OnClick(obj)
{
	if( fv_COOP_YN )
 	{
		alert("시공사는 등록 불가합니다..");
		return;
	}
		
	if(ds_Multi_Re.SetColumn(0,"DANGER_JOB_STS_FLAG") != '07')
	{
		alert("안전수칙은 결재완료처리되어야 등록이 가능합니다..."); 
		return false;
	}
	else if(ds_Multi_Re.SetColumn(0,"DANGER_JOB_STS_FLAG") == '10') 
	{
		alert("공사완료 되었습니다.."); 
		return false;
	}
	else if(ds_Multi_Re.SetColumn(0,"DANGER_JOB_STS_FLAG") == '90') 
	{
		alert("공사취소건입니다...."); 
		return false;
	}
	
	var DangerNum     = ds_Multi_Re.GetColumn(ds_Multi_Re.row,"DANGER_NUM")+"%$";
	var DangerJobFlag = ds_Multi_Re.GetColumn(ds_Multi_Re.row,"DANGER_JOB_FLAG");
	
	if(gfn_IsNull(DangerNum) || gfn_IsNull(DangerJobFlag)){
		alert("위험작업번호와 작업구분 정보가 없습니다. ");
		return false;
	}
	
	args = "WORK_FIRM_CD="     + quote(edt_WorkFirmCdNm1.Text)
	     + " SITE_CHIEF_NM="   + quote(edt_SiteChiefNm.Text  )
	     + " DANGER_JOB_FLAG=" + quote(DangerJobFlag         )
	     + " DANGER_NUM="      + quote(DangerNum             )
	     ;
	
	gfn_Dialog("D10","D1503010011U",args,660,536,true,-1,-1);
	
}
//-------------------------------------------------------------------------------
// 기      능   :  파일첨부
//-------------------------------------------------------------------------------
function btn_FileFolder_OnClick(obj)
{
	if(gfn_IsNull(edt_DangerNum.Text))
	{
		alert("등록된 위험작업이 없습니다. 데이터를 저장후 파일업로드를 진행바랍니다.");
		return;
	}
	else
	{
		
		var strEnable = "";
		
		// 투자외부 - 위험작업요청전(00) 이전까지 수정된다.
		/*
		if( ! (danger_job_sts_flag == "00" || danger_job_sts_flag == "02") )
		{
			strEnable = "Y";	
		}
		else
		{
			// 시공사가 아닐때 막는다
			if( ! fv_COOP_YN )
			{
				strEnable = "Y";			
			}
		}
		*/
		// 2010-03-02 무조건 푼다. - 권기철과장
		strEnable = "";
		
		var arg = "fa_Url=handle.do?ServiceName=D1_DangerWorkRequirEMgr-service&DANGER_NUM="+ds_Multi_Re.GetColumn(0,"DANGER_NUM")+"&DANGER_JOB_FLAG=10&WORK_FLAG=10 fa_Part=D10 fa_Enable="+strEnable;
		var arrList = gfn_Dialog("D10","D1101010018U",arg,450,280,true,-1,-1);
		edtFileList.text = "";
		for(var i=0; i<arrList.length; i++)
		{
			edtFileList.text += arrList[i];
			if(arrList.length-1 != i) edtFileList.text += "/";
		}		
	}
}

