//-------------------------------------------------------------------------------
function cal_DigAdmitFrom_OnChanged(obj,strOldText,strNewText)
{
	/*
	if(cal_DigAdmitFrom.Value < today() && !gfn_IsNull(cal_DigAdmitFrom.Value)){
		alert("굴착허가기간 시작일자는 오늘날짜보다 작을 수 없습니다. 수정해주세요.");
		cal_DigAdmitFrom.Value = "";
		return;	
	}
	*/
	
	if(!gfn_IsNull(cal_DigAdmitFrom.Value) && !gfn_IsNull(cal_DigAdmitTo.Value) && cal_DigAdmitFrom.Value > cal_DigAdmitTo.Value){	
		alert("TO 날짜는 FROM 날짜보다 같거나 큰값을 입력하세요.");
		cal_DigAdmitFrom.SetFocus();
		cal_DigAdmitFrom.value = "";
		cal_DigAdmitTo.value = "";
		return;
	}		
}

