//-------------------------------------------------------------------------------
//	  재질에 따른 관경정보 셋팅 
//-------------------------------------------------------------------------------
function cob_BefoMaterFlag_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	var MaterFlag = obj.Value ;
	var PipeFlag  = '';
	
	if(obj == eval("cob_BefoMaterFlag1")) {
		PipeFlag = "cob_BefoPipeDiamCd1";
		cob_BefoPipeDiamCd1.Enable = true;
	}
	if(obj == eval("cob_BefoMaterFlag2")) {
		PipeFlag = "cob_BefoPipeDiamCd2";
		cob_BefoPipeDiamCd2.Enable = true;
	}
	
	switch(MaterFlag){
		//10 PLP : D10300
		case "10" : gfn_SetComboBox_Comcode("D10300",gfn_setDsObject_Comcode(PipeFlag));	
		break;
		//20 PE : D10301
		case "20" : gfn_SetComboBox_Comcode("D10301",gfn_setDsObject_Comcode(PipeFlag));	
		break;
		//30 ZS : D10302
		case "30" : gfn_SetComboBox_Comcode("D10302",gfn_setDsObject_Comcode(PipeFlag));	
		break;	
		//40 AS : D10303
		case "40" : gfn_SetComboBox_Comcode("D10303",gfn_setDsObject_Comcode(PipeFlag));	
		break;
		//50 PT : D10304
		case "50" : gfn_SetComboBox_Comcode("D10304",gfn_setDsObject_Comcode(PipeFlag));	
		break;
	}
}

function cal_JobReqWmd1_OnChanged(obj,strOldText,strNewText){
	if(cal_JobReqWmd1.Value < today() && !gfn_IsNull(cal_JobReqWmd1.Value)){
		alert("작업요청일자가 오늘날짜보다 작을 수 없습니다. 수정해주세요.");
		cal_JobReqWmd1.Value = "";
		return;	
	}	
}

// 굴착허가기간 체크
function cal_DigAdmitYmd_OnChanged(obj,strOldText,strNewText){
	
	if(gfn_IsNull(cal_DigAdmitYmd.Value)) return;
	
	if( !gfn_IsNull(cal_DigAdmitYmd.Value) ) 
	{
		if( cal_DigAdmitYmd.Value > cal_DigAdmitFrom.Value)
		{
			alert("굴착허가일자는 [허가기간FROM] 보다 이전이거나 같은날짜를 선택해주세요.");
			cal_DigAdmitYmd.Value = strOldText;
			return;
		}
	}
	/*
	if(cal_DigAdmitYmd.Value < today() && ){
		alert("굴착허가일자가 오늘날짜보다 작을 수 없습니다. 수정해주세요.");
		cal_DigAdmitYmd.Value = "";
		return;	
	}
	
	*/
}
function cal_JobResultYmd_OnChanged(obj,strOldText,strNewText){
//권기철 잠시 보완
	// if(cal_JobResultYmd.Value < today() && !gfn_IsNull(cal_JobResultYmd.Value)){
		// alert("작업결과일자 오늘날짜보다 작을 수 없습니다. 수정해주세요.");
		// cal_JobResultYmd.Value = "";
		// return;	
	// }
}
function cal_ReviewYmd_OnChanged(obj,strOldText,strNewText){
	if(gfn_IsNull(cal_ReviewYmd.Value)){
		return;
	}else{
		fn_DateCheckTodayOver(obj,"검토일자");
	}
}
function cal_ChgApproReqYmd_OnChanged(obj,strOldText,strNewText)
{
	if(gfn_IsNull(cal_ChgApproReqYmd.Value))
	{
		return;
	}else{
		if(Calendar1.Value >=  cal_ChgApproReqYmd.Value)
		{
			alert("변경일자는 최초작업일자 이후여야 합니다..");
			cal_ChgApproReqYmd.Value = '';
			return;
		}
	}
}

function ds_Multi_Re_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if(strColumnID == 'JOB_REQ_STR_TIME'){
		
		if(edt_JobReqStartTime.Value == '' || edt_JobReqStartTime.Value == null){ return;}				
		if(substr(edt_JobReqStartTime.Value,0,2) > "24"){
			alert("25시 이상 입력할 수 없습니다. ");
			edt_JobReqStartTime.Value = '';
			edt_JobReqStartTime.SetFocus();
			return;
		}
		if(substr(edt_JobReqStartTime.Value,2,2) > "59"){
			alert("60분 이상 입력할 수 없습니다. ");
			edt_JobReqStartTime.Value = '';
			edt_JobReqStartTime.SetFocus();
			return;
		}	
		/*	
		if(edt_JobReqEndTime.value != ''){
			if(edt_JobReqStartTime.Value > edt_JobReqEndTime.Value){
				alert("작업요청 종료시간보다 클 수 없습니다. ");
				edt_JobReqStartTime.Value = '';
				edt_JobReqStartTime.SetFocus();
				return;
			}
		}
		*/
	}
	
	if(strColumnID == 'JOB_REQ_END_TIME'){
				
		if(edt_JobReqEndTime.Value == '' || edt_JobReqEndTime.Value == null){
			return;
		}
		
		if(substr(edt_JobReqEndTime.Value,0,2) > "24"){
			alert("25시 이상 입력할 수 없습니다. ");
			edt_JobReqEndTime.Value = '';
			edt_JobReqEndTime.SetFocus();
			return;
		}
		if(substr(edt_JobReqEndTime.Value,2,2) > "59"){
			alert("60분 이상 입력할 수 없습니다. ");
			edt_JobReqEndTime.Value = '';
			edt_JobReqEndTime.SetFocus();
			return;
		}
		
		/*
		if(edt_JobReqStartTime.value != ''){
			if(edt_JobReqStartTime.Value > edt_JobReqEndTime.Value){
				alert("작업요청 시작시간보다 작을 수 없습니다. ");
				edt_JobReqEndTime.Value = '';
				edt_JobReqEndTime.SetFocus();
				return;
			}
		}
		*/
	}
}

function ds_Multi_Re2_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if(strColumnID == 'SUPPLY_STOP_TIME'){
		
		if(edt_SupplyStopTime.Value == '' || edt_SupplyStopTime.Value == null){ 
			return;
		}				
		
		if(substr(edt_SupplyStopTime.Value,0,2) > "24"){
			alert("25시 이상 입력할 수 없습니다. ");
			edt_SupplyStopTime.Value = '';
			edt_SupplyStopTime.SetFocus();
			return;
		}
		if(substr(edt_SupplyStopTime.Value,2,2) > "59"){
			alert("60분 이상 입력할 수 없습니다. ");
			edt_SupplyStopTime.Value = '';
			edt_SupplyStopTime.SetFocus();
			return;
		}		
		if( length ( trim( edt_SupplyStopEndTime.value)  ) > 0 ){
			if(edt_SupplyStopTime.Value > edt_SupplyStopEndTime.Value){
				alert("중단 종료시간보다 클 수 없습니다. ");
				edt_SupplyStopTime.Value = '';
				edt_SupplyStopTime.SetFocus();
				return;
			}
		}
	}
	
	if(strColumnID == 'SUPPLY_STOP_END_TIME')
	{			
		if(edt_SupplyStopEndTime.Value == '' || edt_SupplyStopEndTime.Value == null){
			return;
		}
		
		if(substr(edt_SupplyStopEndTime.Value,0,2) > "24"){
			alert("25시 이상 입력할 수 없습니다. ");
			edt_SupplyStopEndTime.Value = '';
			edt_SupplyStopEndTime.SetFocus();
			return;
		}
		if(substr(edt_SupplyStopEndTime.Value,2,2) > "59"){
			alert("60분 이상 입력할 수 없습니다. ");
			edt_SupplyStopEndTime.Value = '';
			edt_SupplyStopEndTime.SetFocus();
			return;
		}		
		// if( length ( trim(edt_SupplyStopTime.value) ) > 0){
			// if(edt_SupplyStopTime.Value > edt_SupplyStopEndTime.Value){
				// alert("중단 시작시간보다 작을 수 없습니다. ");
				// edt_SupplyStopEndTime.Value = '';
				// edt_SupplyStopEndTime.SetFocus();
				// return;
			// }
		// }
	}
}

