// 작업수행 및 비상연락망, 공급중단내역, 검토 등등
function fn_D10_Common2()
{
	ds_Multi_Re2.ClearData();
	ds_Multi_Re2.AddRow();
	
	ds_Multi_Re2.SetColumn(0,"TASKMAST_EMPID",ds_Multi_Re.GetColumn(0,"TASKMAST_EMPID"));
	ds_Multi_Re2.SetColumn(0,"TASKMAST_NMM",ds_Multi_Re.GetColumn(0,"TASKMAST_NMM"));
