	ds_Multi_Re2.SetColumn(0,"TASKMAST_TEL_EXN",ds_Multi_Re.GetColumn(0,"TASKMAST_TEL_EXN"));
	ds_Multi_Re2.SetColumn(0,"TASKMAST_TEL_NUM",ds_Multi_Re.GetColumn(0,"TASKMAST_TEL_NUM"));
	ds_Multi_Re2.SetColumn(0,"GAS_FIRM_PIPE_OPER_EMPID",ds_Multi_Re.GetColumn(0,"GAS_FIRM_PIPE_OPER_EMPID"));
	ds_Multi_Re2.SetColumn(0,"GAS_FIRM_PIPE_OPER_NMM",ds_Multi_Re.GetColumn(0,"GAS_FIRM_PIPE_OPER_NMM"));
	ds_Multi_Re2.SetColumn(0,"GAS_FIRM_PIPE_OPER_PERS",ds_Multi_Re.GetColumn(0,"GAS_FIRM_PIPE_OPER_PERS"));
	ds_Multi_Re2.SetColumn(0,"GFIRM_PIPE_OPER_TEL_DDD",ds_Multi_Re.GetColumn(0,"GFIRM_PIPE_OPER_TEL_DDD"));
	ds_Multi_Re2.SetColumn(0,"GFIRM_PIPE_OPER_TEL_EXN",ds_Multi_Re.GetColumn(0,"GFIRM_PIPE_OPER_TEL_EXN"));
	ds_Multi_Re2.SetColumn(0,"GFIRM_PIPE_OPER_TEL_NUM",ds_Multi_Re.GetColumn(0,"GFIRM_PIPE_OPER_TEL_NUM"));
	ds_Multi_Re2.SetColumn(0,"SUPPLY_STOP_HOUSH_CNT",ds_Multi_Re.GetColumn(0,"SUPPLY_STOP_HOUSH_CNT"));
	ds_Multi_Re2.SetColumn(0,"SUPPLY_STOP_SALES_CNT",ds_Multi_Re.GetColumn(0,"SUPPLY_STOP_SALES_CNT"));
	ds_Multi_Re2.SetColumn(0,"SUPPLY_STOP_INDUS_CNT",ds_Multi_Re.GetColumn(0,"SUPPLY_STOP_INDUS_CNT"));
	ds_Multi_Re2.SetColumn(0,"SUPPLY_STOP_AREA",ds_Multi_Re.GetColumn(0,"SUPPLY_STOP_AREA"));		
	ds_Multi_Re2.SetColumn(0,"SUPPLY_STOP_TIME",ds_Multi_Re.GetColumn(0,"SUPPLY_STOP_TIME"));
	ds_Multi_Re2.SetColumn(0,"SUPPLY_STOP_END_TIME",ds_Multi_Re.GetColumn(0,"SUPPLY_STOP_END_TIME"));
	ds_Multi_Re2.SetColumn(0,"STOP_ADV_METHOD",ds_Multi_Re.GetColumn(0,"STOP_ADV_METHOD"));	
	ds_Multi_Re2.SetColumn(0,"REVIEW_EMPID",ds_Multi_Re.GetColumn(0,"REVIEW_EMPID"));
	ds_Multi_Re2.SetColumn(0,"REVIEW_EMPID_NM",ds_Multi_Re.GetColumn(0,"REVIEW_EMPID_NM"));
	ds_Multi_Re2.SetColumn(0,"REVIEW_YMD",ds_Multi_Re.GetColumn(0,"REVIEW_YMD"));
	ds_Multi_Re2.SetColumn(0,"REVIEW_OPINI",ds_Multi_Re.GetColumn(0,"REVIEW_OPINI"));
	ds_Multi_Re2.SetColumn(0,"REVIEW_FLAG",ds_Multi_Re.GetColumn(0,"REVIEW_FLAG"));
	
	// 추가
	ds_Multi_Re2.SetColumn(0,"TEAM_CD",ds_Multi_Re.GetColumn(0,"TEAM_CD"));         // 팀
	ds_Multi_Re2.SetColumn(0,"TAKER_EMPID",ds_Multi_Re.GetColumn(0,"TAKER_EMPID")); // 담당자
		
	// 반려
	if(ds_Multi_Re.GetColumn(0,"REVIEW_FLAG") == 'N' )
	{
		Check_02.Value = 1;			
	}
	else
	{
		Check_02.Value = 0;			
	}
	
	
/*  결재상태 (A10411)
0 - 승인
1 - 작성
2 - 결재중
4 - 요청취소
5 - 반려 */	
}	

