	ds_DangerEqupScg.SetColumn(4,"EA1","0");
	ds_DangerEqupScg.SetColumn(0,"EA2","0"); 
	ds_DangerEqupScg.SetColumn(1,"EA2","0");
	ds_DangerEqupScg.SetColumn(2,"EA2","0");
	ds_DangerEqupScg.SetColumn(3,"EA2","0");
}

