	for(var i=0; i<ds_Grid1.RowCount(); i++)
	{		
		if( gfn_IsNull( ds_Grid1.GetColumn(i,"JOB_STR_TIME") ) )
		{
			alert(gfn_GetMessage("M000015","%AAA%","작업계획 시작시간은 "));
