				   , strArgument
			   );
}

//-------------------------------------------------------------------------------
// 기      능   :  파일첨부 조회
//-------------------------------------------------------------------------------
function fn_File_List()
{	

	ds_FileList.ClearData();
	
	var strWORK_FLAG = "10";	
	
	var strSvcID = "findFile";
	var strPart = "D10";	
	var strURL = "handle.do?ServiceName=D1_DangerWorkRequiRegisHmppMgr-service";  
	var strInDatasets  = "";
	var strOutDatasets = "ds_FileList=ds_FileList";
	var strArgument    = "findFile=1";
	    strArgument   += " DANGER_NUM=" + quote(danger_num);
		strArgument	  += " DANGER_JOB_FLAG=" +quote(danger_job_flag);
		strArgument	  += " WORK_FLAG=" + quote(strWORK_FLAG);
			
	gfn_SyncCall( 
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
			   );
	
	edtFileList.text = "";
	for(var i=0; i<ds_FileList.RowCount(); i++)
	{
		edtFileList.text += ds_FileList.GetColumn(i,"ORG_FILE_NAME");
		if(ds_FileList.RowCount()-1 != i) edtFileList.text += "/";
	}
}

//-------------------------------------------------------------------------------
// 기      능   :  위험작업 요청(버튼 클릭시 상태값 강제 01) 변경
//-------------------------------------------------------------------------------
function Btn_Danger_Req_OnClick(obj)
{   
	danger_job_str_flag = ds_Multi_Re.GetColumn(0,"DANGER_JOB_STS_FLAG");

	if(danger_job_str_flag == "00" || danger_job_str_flag == "02" )
	{	
		if(Confirm("위험작업 요청을 하시겠습니까? ") == true)
		{
			ds_Multi_Re.UpdateControl = false;
			ds_Multi_Re.SetRowType(0,"update");
			
			ds_Multi_Re.SetColumn(0,"DANGER_JOB_STS_FLAG","01");
			ds_Multi_Re.SetColumn(0,"UPD_IP",gfn_GetGlobalValue("G_IPADDRESS"));   
			ds_Multi_Re.SetColumn(0,"UPD_EMPID",gfn_GetGlobalValue("G_USER_NO"));   
			ds_Multi_Re.SetColumn(0,"DANGER_NUM",danger_num);  
			ds_Multi_Re.SetColumn(0,"DANGER_JOB_FLAG","20");
				
			fv_FindRow_ShowRow = 1;
			fv_FindRow_ShowKey = ds_Multi_Re.GetColumn(0,"DANGER_NUM"); //위험작업번호
			
			var strSvcID = "Btn_Danger_Req_OnClick";
			var strPart = "D10";
			var strURL = "handle.do?ServiceName=D1_DangerWorkRequiRegisHmppMgr-service";
			var strInDatasets  = "ds_Multi_Re=ds_Multi_Re:u";
			var strOutDatasets  = "";
			var strArgument  = "updateflag=1";
			
			gfn_SyncCall(
							 strSvcID
						   , strPart
						   , strURL
						   , strInDatasets
						   , strOutDatasets
						   , strArgument
						  );
						  
			fn_D10_BpmSearchStr();						//BPM 신청
			
			if(ErrorCode < 0)
			{    
				alert(ErrorMsg);
				return;
			}else{
				gfn_SetStatusMsg(gfn_GetMessage("M000009","%AAA%","정상적으로 저장을"),"black",true); 
				fn_CommonSearch();	
			}		
		}
	}else{
		alert("이미 위험작업 요청을 하였습니다.");
		return;
	}
}

//--------------------------------------------------------------------------------------------
//   신청(등록)후 BPM 값 보내기 
//--------------------------------------------------------------------------------------------
function fn_D10_BpmSearchStr()
{
	ds_sKeyUp.ClearData();
	ds_sKeyUp.AddRow();
	//ds_sKeyUp.SetColumn(0,"userId","920090001");										//사용자아이디
	ds_sKeyUp.SetColumn(0,"userId",gfn_GetGlobalValue("G_USER_NO"));					//사용자아이디
	ds_sKeyUp.SetColumn(0,"processDefName","SS_DW");									//SS_DW
	ds_sKeyUp.SetColumn(0,"dangerNumPK",ds_Multi_Re.GetColumn(0,"DANGER_NUM"));			//위험작업번호
	ds_sKeyUp.SetColumn(0,"dangerJobFlag",ds_Multi_Re.GetColumn(0,"DANGER_JOB_FLAG"));  //공사구분
	ds_sKeyUp.SetColumn(0,"branchCode",ds_Multi_Re.GetColumn(0,"BRANCH_CD"));			//지사코드
	
	ds_sKeyUp.SetColumn(0,"branchNm",ds_Multi_Re.GetColumn(0,"BRANCH_CD_NM"));			//지사코드명   			(03.16.월)추가
	
	//ds_sKeyUp.SetColumn(0,"teamCode",ds_Multi_Re.GetColumn(0,"TEAM_CD"));				//팀코드   				(03.16.월)추가
	ds_sKeyUp.SetColumn(0,"teamCode",gfn_GetGlobalValue("G_DEPT_CODE"));				//팀코드
	
	//ds_sKeyUp.SetColumn(0,"teamNm",ds_Multi_Re.GetColumn(0,"TEAM_CD_NM"));			//팀코드명   			(03.16.월)추가
	ds_sKeyUp.SetColumn(0,"teamNm",gfn_GetGlobalValue("G_DEPT_NAME"));					//팀코드명   			(03.16.월)추가
	
	ds_sKeyUp.SetColumn(0,"workFirmUserId","920090001");								//접수담당자(시공사)ID
	ds_sKeyUp.SetColumn(0,"scUserId","199643547");										//영업공사담당자ID
	ds_sKeyUp.SetColumn(0,"csUserId","198540269");										//고객지원담당자ID
	ds_sKeyUp.SetColumn(0,"zipNo2",ds_Multi_Re.GetColumn(0,"ZIP_NO2"));					//지역명(동)
	ds_sKeyUp.SetColumn(0,"jobNm",ds_Multi_Re.GetColumn(0,"JOB_NM1"));					//공사명
	ds_sKeyUp.SetColumn(0,"dealNm",ds_Multi_Re.GetColumn(0,"WORK_FIRM_NM"));			//시공사명
	ds_sKeyUp.SetColumn(0,"workNumPK",ds_Multi_Re.GetColumn(0,"WORK_NUM"));				//공사번호
	ds_sKeyUp.SetColumn(0,"interfacetype","Type3");
	
	// 검색 문자열을 생성한다 
	var strSvcID = "fn_D10_BpmSearchStr";
	var strPart = "D10";
	var strURL = "handle.do?ServiceName=D1_DangerWorkRequiRegisHmppMgr-service&findBpmUp=1";
	var strInDatasets  = "ds_sKeyUp=ds_sKeyUp";
	var strOutDatasets  = "";
		strOutDatasets  = "";
	var strArgument  = "";

	gfn_SyncCall( 
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
			   );
}


