function fn_update2()
{   
	danger_num = ds_Multi_Re.GetColumn(0,"DANGER_NUM");	
	
	ds_Review.UpdateControl = false;
	ds_Review.SetRowType(0,"update");
	
	if(chk_Change2.Value == 1 && chk_Change3.Value == 0){	
		ds_Review.SetColumn(0,"DANGER_JOB_STS_FLAG","04");
		ds_Review.SetColumn(0,"REVIEW_FLAG","Y");
	}else if(chk_Change2.Value == 0 && chk_Change3.Value == 1){
		ds_Review.SetColumn(0,"DANGER_JOB_STS_FLAG","02");
		ds_Review.SetColumn(0,"REVIEW_FLAG","N");
	}
	ds_Review.SetColumn(0,"UPD_IP",gfn_GetGlobalValue("G_IPADDRESS"));   
	ds_Review.SetColumn(0,"UPD_EMPID",gfn_GetGlobalValue("G_USER_NO"));  
	ds_Review.SetColumn(0,"DANGER_NUM",danger_num); 
	ds_Review.SetColumn(0,"DANGER_JOB_FLAG","20");
		
	fv_FindRow_ShowRow = 1;
	fv_FindRow_ShowKey = danger_num; //위험작업번호	
	
	var strSvcID = "update2";
	var strPart = "D10";
	var strURL = "handle.do?ServiceName=D1_DangerWorkRequiRegisHmppMgr-service";
	var strInDatasets  = "ds_Review=ds_Review:u";
	var strOutDatasets  = "";
	var strArgument  = "update2=1";
	
	gfn_SyncCall(
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
				  );
				  
	if(ErrorCode < 0)
	{    
		alert(ErrorMsg);
		return;
	}else{
		gfn_SetStatusMsg(gfn_GetMessage("M000009","%AAA%","정상적으로 저장을"),"black",true); 
		fn_CommonSearch();	
	}				  
}

/********************************************************************************
* 기      능   :  작업계획(위험작업내용(SCG) INSERT & UPDATE)
********************************************************************************/
function fn_D10_Save4()
{
	for(var i=0; i<ds_DangerJob.RowCount(); i++)
	{
		if(ds_DangerJob.GetRowType(i) == "insert"){
			ds_DangerJob.SetColumn(i,"DANGER_NUM",danger_num);
		}		
		ds_DangerJob.SetColumn(i,"DANGER_JOB_FLAG","20");
		ds_DangerJob.SetColumn(i,"CRT_IP",gfn_GetGlobalValue("G_IPADDRESS"));	//gfn_GetGlobalValue("G_IPADDRESS")); 
		ds_DangerJob.SetColumn(i,"CRT_EMPID",gfn_GetGlobalValue("G_USER_NO"));		//gfn_GetGlobalValue("G_USER_NO"));
		ds_DangerJob.SetColumn(i,"UPD_IP",gfn_GetGlobalValue("G_IPADDRESS"));	//gfn_GetGlobalValue("G_IPADDRESS")); 
		ds_DangerJob.SetColumn(i,"UPD_EMPID",gfn_GetGlobalValue("G_USER_NO"));		//gfn_GetGlobalValue("G_USER_NO"));0	
	}
	
	var strSvcID = "fn_D10_Save4";
	var strPart = "D10";
	var strURL = "handle.do?ServiceName=D1_DangerWorkRequiRegisHmppMgr-service";
	var strInDatasets  = "ds_DangerJob=ds_DangerJob:u";
	var strOutDatasets  = "";
	var strArgument  = "save2=1";	
	
	gfn_SyncCall(
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
				  );
	if(ErrorCode < 0)
	{    
		return;
	}else{
		gfn_SetStatusMsg(gfn_GetMessage("M000009","%AAA%","정상적으로 저장을"),"black",true); 
	}				  
}

